css:
/* Background image */
@document url-prefix( https://docs.google.com/document)
{
#kix-appview
{
    background-color: #333 !important;
	background: url(http://img.wallpaperstock.net:81/compaq-light-blue-wallpapers_1210_1920x1200.jpg) !important;
    background-size: cover !important;
    border: 0 !important;
    outline: 0 none !important;
}
div#docs-header.docs-og-minibar {
  display: none !important;
}

div#docs-menubars {
  padding-top: 3px !important; 
  height: 27px !important
}

div#docs-notice.docs-title-save-label.docs-title-save-label-clickable { 
  display: none !important
}

div#textLtrButton.goog-toolbar-toggle-button { 
  display: none !important 
}

div#textRtlButton.goog-toolbar-toggle-button { 
  display: none !important 
}

div#inputToolsSeparator.goog-toolbar-separator.goog-inline-block { 
  display: none !important 
}

div#inputToolsToggleButton { 
  display: none !important 
}

div#inputToolsMenuButton { 
  display: none !important 
}

div.docs-presence-widget.docs-presence-widget-normal {
  top: 5px !important;
}

div#docs-chat-pane.docs-chat-pane { 
  width:220px !important; 
  font-size: 11px !important
}

div#docs-chat-messages.docs-chat-messages { 
  font-size: 9px !important 
}

div#docs-chat-edit-container.docs-chat-edit-container { 
  width:220px !important
}

div.docs-chat-pane-container {
  top:30px !important; 
  width:220px !important; 
  font-size: 11px !important 
}

div#docs-chat-edit-box {
  width:185px !important;
  resize: none !important;
  outline: none !important
}

textarea#docs-chat-edit-box.docs-chat-edit-box.docs-chat-edit-box-focus {
  width: 185px !important;
  resize: none !important;
}

/* Sheet texture */
.kix-page.kix-page-compact-first
{
    /*background-color: transparent !important;*/
}
.kix-paginateddocumentplugin.kix-paginateddocumentplugin-compact-mode
{
    border: none !important;
}
.kix-page.kix-page-paginated,
.kix-page-content-wrapper 
{

    background-size: 818px Auto !important;
    background-repeat: repeat !important;
}

/* Comment section */
.kix-commentoverlayrenderer-normal
{
    opacity: .1 !important;
}
.kix-commentoverlayrenderer-highlighted
{
    background-color: #000000 !important;
    opacity: .4 !important;
}
.docos-anchoreddocoview
{
    opacity: .1 !important;
}
.docos-anchoreddocoview:hover,
.docos-docoview-active.docos-anchoreddocoview:hover,
.docos-docoview-active.docos-anchoreddocoview
{
    opacity: .9 !important;
}

div.docs-ui-unprintable:last-child
{
    background-color: transparent !important;
}
#docs-chrome
{
    width: 100% !important;
    z-index: 100; 
    position: fixed !important;
    margin-top: 0px !important;
    -moz-transition: all 0.8s;     
    -webkit-transition: all 0.8s;
    opacity: 0.0 !important;
}

#docs-chrome:hover
{
    margin-top: 0 !important;
    opacity: 1 !important;
    -moz-transition: all 0.4s;     
    -webkit-transition: all 0.4s;
}

::-webkit-scrollbar-thumb
{
     -webkit-transition: background-color 0.3s;
}

iframe
{
     border-top: none !important;
}
}
