@-moz-document regexp("http(s)*://.*\.?animespirit.(ru|online|cc)/.*") {
div.accordion p embed {
      width: 1000px;
      height: 615px;
      position: relative;
      z-index: 900;
    }

    div.accordion p object {
      width: 1000px;
      height: 615px;
      position: relative;
      z-index: 900;
    }

    div.accordion p iframe {
      width: 1000px !important;
      height: 615px !important;
    }

    div.content-block {
      background: #444;
      border-radius: 5px;
    }

    div.content-main-block {
      background: #333;
    }

    body {
      background: #333;
    }

    div.block {
      background: #444;
      border-radius: 5px;
    }

    .block-title {
      background-image: none;
      border-radius: 5px 5px 0 0;
    }

    .block-title h3 {
      background-image: none;
    }

    .block-title h2 {
      background-image: none;
    }

    .search-block {
      background-image: none;
      border-radius: 5px;
      background-color: #89d2ea;
      box-shadow: 0 0px 10px #89d2ea;
    }

    .search-block-in {
      background-image: none;
    }

    .content-block-title {
      background-image: none;
      background-color: #89d2ea;
      border-radius: 5px 5px 0 0;
      box-shadow: 0 0px 10px #89d2ea;
      margin-bottom: 5px;
    }

    .content-block-title h2 {
      background-image: none;
    }

    a {
      color: #6da8ba;
    }
  
    .accordion p {
      background: #222;
      border-left: solid 1px #555;
      border-right: solid 1px #555;
    }

    .accordion h3 {
      background: #222;
      border-color: #555;
    }

    .accordion h3:hover {
      background-color: #89d2ea;
      box-shadow: 0 0px 10px #89d2ea;
    }

