css:
@-moz-document url-prefix("http://www.last.fm/") {
body
{
	background-color:#1F1F1F;
}
.content
{
	background:#E6E6E6;
}
.user-dropdown
{
	background-image:none;
  	background-color:#3B3B3B;
  	border-color:#3B3B3B;
  	box-shadow:inset #3B3B3B 0 1px 1px, #3B3B3B 0 3px 10px;
}
.user-dropdown-divider-top
{
	border-color:#3B3B3B;
}
.user-dropdown-divider-bottom
{
	border-color:#3B3B3B;
}
h2,.h2
{
	color:#1F1F1F;
}
#page h2.heading
{
	color:#1F1F1F;
}
.h2Wrapper,.h2Wrapper a
{
	color:#1F1F1F;
}
.masthead
{
	background-image:none;
  	background-color:black;
  	border:none;
}
.btn--header
{
	background-image:none;
  	background-color:#1F1F1F;
}
.btn.btn--header
{
	box-shadow:#787878 0 1px 1px, inset rgba(0,0,0,0.25) 0 1px 1px
  
}
.user-badge
{
	border-right:1px solid #3B3B3B;
}
.user-badge:hover
{
	border-right:1px solid #3B3B3B;
}
.btn--header:hover
{
	background-image:none;
  	background-color:#3B3B3B;
}

.lfmRed .site-search .search-box
{
	box-shadow:inset #3B3B3B 0 -5px 5px, inset #131313 0 1px 5px, #3B3B3B 0 1px 0;
}
.lfmRed .site-search .search-box
{
	border-color: #3B3B3B;
}

}
