css:
/*======================================
  thauder tepco ja jp compact 2014/07
  ======================================*/
@-moz-document domain("thunder.tepco.co.jp") {

/*--- unwanteds ---*/
dummy#dummy.dummy {display: none !important;}

/*--- generic containers ---*/
body, div, h1, h2, h3, h4, h5, h6
 {box-sizing: border-box; -moz-box-sizing: border-box;}
body, div, p, span, h1, h2, h3, h4, h5, h6 {
 font-family: inherit !important;
 font-size: medium !important; font-weight: normal !important;
 line-height: 1.4em !important;
 }
/*-- table breaker --*/
table, tbody, tr, th, td {display: block; width: auto; height: auto;}

/*--- header ---*/
#header {display: none !important;}

/*--- notifier ---*/
#header+center {text-align: left !important;}
#header+center td {text-indent: 1em;}
#header+center br {display: none !important;}

/*--- footer ---*/
#footer {display: none !important;}

/*--- main contents ---*/
.contents_wrap {width: auto !important; margin: 0 !important;}
.right_area {display: none !important;}
.left_area {width: auto !important;}
#tabs {margin: 0 !important;}
.tab_wrap {width: auto !important;}

.tab_contents_wrap {min-height: auto !important;}

.data_main {min-height: auto !important;}

.data_top .right {height: auto !important;}
.area_select .wrap select {width: auto !important;}

}
