css:
@-moz-document domain("psim.us"), domain("pokemonshowdown.com") {
/*

This is a Stylish skin for Pokémon Showdown. It uses a few assets from the PokéBattle website, and also uses the battle interface CSS from this style: https://userstyles.org/styles/101626/kalos-theme-x-y-style-pokemon-showdown. Credit where credit is due. Thanks to Ridaz, Scotteh, Lilligant, the Pokémon Showdown team, the PokéBattle team, and the creators of the aforementioned Kalos Theme X/Y-Style Pokémon Showdown skin.

*/

html {font-size:100%;height:100%;}
body, .chat-log, .battle-log, .chatbox, .chatbox textarea, ul.userlist * {font-family:'proxima-nova','Helvetica Neue','Free Sans',Calibri,'Droid Sans',Helvetica,Arial,sans-serif;}
html, body, .mainmenuwrapper, .userlist, .chat-log-add, .battle-log-add, .chat-log .inner, .ps-room, body, .ps-room.ps-room-light, .ps-room.ps-room-opaque, .ps-room .battle-log, .chat-log, .battle, .innerbattle, .pfx-panel, .pm-log-add {background: url('http://s3.pokebattle.com/img/bg.png')!important;}

/* Header */
#header, #wrapper .header, .pfx-topbar, .pfx-topbar.header, .ps-header{background:#151515;border:0} /*0d0d0d*/
#header, #wrapper .header {max-height:40px}
.pfx-topbar, .pfx-topbar.header {height:60px}
.logo{display:none;}
#header:after{content:url("http://i.imgur.com/1nGotRD.png");padding-left:50px;z-index:1;position:relative;}
.userbar {top: 10px;}
.maintabbarbottom {background:#478BD9;border:0;bottom:-10px;height:11px;}
.tabbar a.button {height:40px;cursor:pointer;text-align:center;color:#38342A;background:#FFF;}
.tabbar a.button:hover {background:#9ABEE8;color:#FFF;}
.tabbar a.button:active{background:#478BD9;border-radius:0;height: 40px;}
.tabbar a.button.cur, .tabbar a.button.cur:hover{color:#FFF;background:#478BD9;}
.siderooms li{z-index:1;margin-right:1px;}
.tabbar li:first-child a.button, .tabbar li:last-child a.button{border-radius:0;}
.tabbar li .button.notifying {background:#B6CBE3;margin-top:0px;}
.tabbar li .button.notifying:hover {background:#A4B8CF;margin-top:0px;}
.closebutton, .minimizebutton{color:#38342A;}
.closebutton:hover, .minimizebutton:hover, .closebutton:active, .minimizebutton:active{color:#DDD;}

/* Body */
.ps-room {margin-top:-5px;color:black;}

.menugroup {background:#D9D9D9;}
.menugroup .button{text-shadow:0;border:0;}
.menugroup .button, .menugroup .button.big{background:#333;color:#FFF;}
.menugroup .button:hover{background:#478BD9;color:#FFF;}
.label{color:#000;font-size:9pt;font-weight:bold;display:block;text-shadow:none;}
.select{border:1px solid #888;background:#EEE;color:black;border-radius:none;box-shadow:none;}
.select:hover{border-color:#555;background:#FFF;box-shadow:none;}
.select:active{border-color:#777;background:#DDD;box-shadow:none;}

.ps-popup {font-size:12pt;position:absolute;top:auto;right:auto;left:auto;bottom:auto;text-align:left;background:#FFF;color:black;padding:2px 10px;box-shadow:none;z-index:20;}
.popupmenu h3{font-size:12pt;color:#38342B;}

.credits h2{font-size:11pt;font-style:italic;margin-bottom:0;color:#38342B;}
.credits li small{font-size:10pt;display:block;color:#555;}
.credits ul{padding-left:1.5em;margin-top:0;margin-bottom:0;color:#478BD9;}

.ladder table,.ladder td,.ladder th{border-collapse:collapse;border:1px solid #BBB;}
.ladder th{text-align:left;font-size:9pt;background:#D9D9D9;}

.infobox{border:1px solid #d9d9d9;padding:2px 4px;}

small{font-size:8pt;font-weight:normal;}
.chat-log .notice,.chat-log .chat,.pm-log .chat,.battle-log .notice, .battle-log .chat{line-height:10pt;font-size:10pt;}
.pm-log{background:#FFF;}
.highlighted {background: #BFDDFF;}
.news-embed{border-bottom:1px solid #AAA;}

.chatbox label{font-size:11pt;}
.userlist li em.group{font-style:normal;font-size:11pt;color:#AAA;display:block;float:left;text-align:center;padding:1px 0 0 1px;width:14px;}
ul.userlist *{font-family:'proxima-nova','Helvetica Neue','Free Sans',Calibri,'Droid Sans',Helvetica,Arial,sans-serif;font-size:11pt;}
.userlist li {border-bottom:0;}
.userlist li button:hover {background: #FFF;}

.ps-popup > p:nth-child(4) {display:none;}

.mainmenufooter{bottom:15px;left:20px;color:#38342A;text-shadow:none;}
.mainmenufooter small{font-size:9pt;}
.mainmenufooter a{text-decoration:underline;color:#38342A;}
.mainmenufooter a:hover{text-decoration:underline;color:#478BD9;}

/* Battle */

.battle-log h2{color:#38342b;background:transparent;font-size:20pt;border:0;padding:0;margin-left:0;margin-right:0;}

/* Trainers */
.teamicons {opacity:1!important;}
.trainersprite {z-index:1;opacity:1!important;}
.trainer strong{display:block;font-size:10pt!important;overflow:hidden;padding-top:15px!important;padding-left:110px;color:#000;text-shadow: -1px -1px 0 #FFF, 1px -1px 0 #FFF, -1px 1px 0 #FFF, 1px 1px 0 #FFF;}

.rightbar{position:absolute; right:15px; height:100px; width:320px; background-color:rgba(255,255,255,0); border:0px solid rgba(215,215,215,0.5); color:#777777; background-image:url(http://i.imgur.com/2Z8AxcI.png); background-position:0px -21px}
.rightbar .trainer{margin-top:0px;}
.rightbar .trainersprite{position:absolute;top:0px;left:40px;opacity:1!important}
.rightbar .trainer strong{display:block; font-size:12pt;overflow:hidden;padding-top:10px;text-align:left;position:relative;left:19px;}
.rightbar .trainer .teamicons{float:left;position:relative;top:0px;left:108px}

.leftbar{position:absolute; left:15px; height:100px; width:320px; background-color:rgba(255,255,255,0); border:0px solid rgba(215,215,215,0.5); color:#777777; background-image:url(http://i.imgur.com/Ryx05Zd.png); background-position:0px -21px}
.leftbar .trainer{margin-top:0px;}
.leftbar .trainersprite{position:absolute;top:0px;right:40px;opacity:1!important}
.leftbar .trainer strong{display:block; font-size:12pt;overflow:hidden;padding-top:10px;text-align:right;position:relative;left:-130px;}
.leftbar .trainer .teamicons{float:right;position:relative;top:0px;right:108px}

/* Battle Screen */
.ps-room .battle{top:0px;border-width:0px;}
.ps-room .battle-controls{top:430px;}

.innerbattle {height:420px;overflow:hidden;}
.backdrop, .background, .innerbattle div div img, .innerbattle div img, .turn, .statbar, .lstatbar, .rstatbar {margin-top: 60px;}
.battle{border:0px solid #AAA;width:640px;height:420px;overflow:hidden;font-size:10pt;}
.backdrop:before{display:block;content:"";background-size:cover;width:640px;height:360px}
.backdrop{width:640px;height:360px;position:absolute;top:0px;left:0px;overflow:hidden;background:-50px -90px;opacity:1!important}
.innerbattle:after{display:block;content:"";background:url(http://i.imgur.com/ALNRPPT.png);position:absolute;width:640px;height:360px;top:60px;left:0;}

.battle-controls p{margin:2px 0 8px 0;padding:0 8px;}
.battle-controls .whatdo{margin-top:-2px;padding:0 8px;font-size:9pt;color:#555555;}
.battle-controls .whatdo small{float:right;padding:1px 2px;border:1px solid #999999;border-radius:4px;}
.battle-controls .whatdo small.weak{color:#AAAA22;border-color:#AAAA22;}
.battle-controls .whatdo small.critical{color:#EE4433;border-color:#EE4433;}
.whatdo{position:relative;font-size:11.5pt!important;opacity:1!important;bottom:59px;font-family: "Lucida Grande", "Lucida Sans Unicode", "Lucida Sans", Geneva, Verdana, sans-serif;right:-20px;z-index:2;color:#FFF!important;text-shadow: -1px -1px 0 #000, 1px -1px 0 #000, -1px 1px 0 #000, 1px 1px 0 #000;} 

#tooltipwrapper * {z-index:99;}
.foehint{position:relative;top:45px}
.turn{position:absolute;top:0px;left:270px !important;width:101px;height:17px;font-size:8pt;font-weight:bold;margin:0px;padding:0px;padding-top:35px;border:0;border-radius:0;opacity:1!important;background:url(http://i.imgur.com/6SgSXmb.png);text-align:center;color:#000;}

/* Battle statuses */
.lstatbar strong{z-index:1;}
div[class|="turnstatus"], div[class|="sidecondition"]{margin-top:60px;}  
.healthy{float:right;color:#b4b4b4;position:relative;top:1px;opacity:0;right:-350px}  
.weak{float:right;position:relative;top:1px;opacity:0;right:-350px}
.critical{float:right;position:relative;top:1px;opacity:0;right:-350px}

.weather:after{display:block;background:url(http://i.imgur.com/ALNRPPT.png);position:absolute;top:0;left:0;width:640px;height:360px;}   
.weather em {position:absolute;top:-30px; left:-100px;}

/* HP bars */
.hptextborder{display:none!important;}

.statbar .hpbar .hp{height:3px; border-top:1px solid #007734; background:#00bb51; border-bottom:2px solid #007734; border-radius:0px;}
.statbar .hpbar .hp-yellow{background-color:#f5d538; border-color:#be9f0a}
.statbar .hpbar .hp-red{background-color:#ee4928; border-color:#a3260d}
.statbar .hpbar .prevhp{background:url(http://i.imgur.com/ByYA9T2.png);  height:6px; border-radius:0px}
.statbar .hpbar .prevhp-yellow{background:url(http://i.imgur.com/ByYA9T2.png); }
.statbar .hpbar .prevhp-red{background-color:#facec5}.hptext{margin-top:2px}  
.statbar .hpbar .hptext{position:absolute; color:#FFF; text-shadow:1px 0 black,-1px 0 black,0 1px black,0 -1px black; font-size:9px; width:32px; height:12px; text-align:center}
.rstatbar .hpbar .hptext{width:42px; height:16px; background:url(http://i.imgur.com/LPDWEbx.png); right:-47px; top:-5px; font-size:11px; color:#49c39f; border-radius:0 4px 4px 0}
.lstatbar .hpbar .hptext{width:40px; height:16px; background:url(http://i.imgur.com/LPDWEbx.png); left:155px;  top:-5px;  font-size:11px;  color:#49c39f; border-radius:4px 0 0 4px; padding-left:2px}

/* Stat bars */
.statbar .status{ margin-top:15px; min-height:10px; font-size:7pt}  
.statbar span.frz{position:absolute; left:-107px; top:-5.5px; font-size:0px;background:url(http://i.imgur.com/L7UTW2y.png); padding:10px 36px; border:0; border-radius:3px}
.statbar span.frz:hover{transition:All 1.6116s ease;transform: rotate(0deg) scale(0.90) skew(0deg) translate(0px);}   
.statbar span.slp{position:absolute; left:-107px; top:-5.5px; font-size:0px;background:url(http://i.imgur.com/D3VqIVw.png); padding:10px 36px; border:0; border-radius:3px}
.statbar span.slp:hover{transition:All 1.6116s ease;transform: rotate(0deg) scale(0.90) skew(0deg) translate(0px);}
.statbar span.par{position:absolute; left:-107px; top:-5.5px; font-size:0px;background:url(http://i.imgur.com/4XrzuuB.png); padding:10px 36px; border:0; border-radius:3px}
.statbar span.par:hover{transition:All 1.6116s ease;  transform: rotate(0deg) scale(0.90) skew(0deg) translate(0px);}
.statbar span.brn{position:absolute; left:-107px; top:-5.5px; font-size:0px;background:url(http://i.imgur.com/4F0lebL.png); padding:10px 36px; border:0; border-radius:3px}
.statbar span.brn:hover{transition:All 1.6116s ease;transform: rotate(0deg) scale(0.90) skew(0deg) translate(0px);}
.statbar span.psn{position:absolute; left:-107px; top:-5.5px; font-size:0px;background:url(http://i.imgur.com/4wrWKR0.png); padding:10px 36px; border:0; border-radius:3px}
.statbar span.psn:hover{transition:All 1.6116s ease;transform: rotate(0deg) scale(0.90) skew(0deg) translate(0px);}
.statbar span.bad{background:#e74c3c;color:#ecf0f1;border-color:#c0392b}
.statbar span.good{background:#27ae60;color:#ecf0f1;border-color:#067A37}
.statbar span.neutral{background:#7f8c8d;color:#ecf0f1;border:solid #2c3e50}
.statbar{position:absolute; display:none; width:200px;height:23px;background:url(http://i.imgur.com/x76Yv1K.png);min-height:0px; padding:3px 0px 0px 32px;}
.statbar strong{position:absolute; left:1px; top:-22px; font-family: "Trebuchet MS", "Lucida Grande", "Lucida Sans Unicode", "Lucida Sans", Tahoma, sans-serif;font-size:17px; font-weight:normal; display:block; text-align:Center; color:#FFF; text-shadow:#000 1px 1px 0,#000 1px -1px 0,#000 -1px 1px 0,#000 -1px -1px 0;}
.statbar strong small{font-size:17px; font-family: "Trebuchet MS", "Lucida Grande", "Lucida Sans Unicode", "Lucida Sans", Tahoma, sans-serif;color:#acc5d4;font-weight:normal;}
.statbar .hpbar{position:relative; border:1px solid #131313; background:url(http://i.imgur.com/ByYA9T2.png); height:6px; margin:0 0 0 0; width:151px; border-radius:0px; padding:0; top:3px;}

/* Moves */
.movemenu button{transition:All 1.6116s ease;width:150px; height:50px; border-radius:0; border-width:0; box-shadow:none; color:#000; font-size:10pt;}
.movemenu button small{text-align:center; width:62px;}
.movemenu button small.type, .movemenu button small.pp{padding-left:2px;color:whitesmoke;text-shadow:1px 1px black;}
.movemenu button.type-Normal{background:url(http://i.imgur.com/1ctemFU.png)}
.movemenu button.type-Normal:hover{transform: rotate(0deg) scale(1.10) skew(0deg) translate(0px); background:url(http://i.imgur.com/vTfBrkj.png)}
.movemenu button.type-Fairy{background:url(http://i.imgur.com/rdPeaVu.png)}
.movemenu button.type-Fairy:hover{transform: rotate(0deg) scale(1.10) skew(0deg) translate(0px); background:url(http://i.imgur.com/bxU4lIL.png)}
.movemenu button.type-Fire{background:url(http://i.imgur.com/aP39wjQ.png)}
.movemenu button.type-Fire:hover{transform: rotate(0deg) scale(1.10) skew(0deg) translate(0px); background:url(http://i.imgur.com/Wbbty9g.png)}
.movemenu button.type-Water{background:url(http://i.imgur.com/cMJaVjV.png)}
.movemenu button.type-Water:hover{transform: rotate(0deg) scale(1.10) skew(0deg) translate(0px); background:url(http://i.imgur.com/Ufx3H8C.png)}
.movemenu button.type-Electric{background:url(http://i.imgur.com/UF3tbwp.png)}
.movemenu button.type-Electric:hover{transform: rotate(0deg) scale(1.10) skew(0deg) translate(0px); background:url(http://i.imgur.com/MQHXttp.png)}
.movemenu button.type-Grass{background:url(http://i.imgur.com/bNq1PyT.png)}
.movemenu button.type-Grass:hover{transform: rotate(0deg) scale(1.10) skew(0deg) translate(0px); background:url(http://i.imgur.com/k2PpmJa.png)}
.movemenu button.type-Ice{background:url(http://i.imgur.com/Kwcx3yV.png)}
.movemenu button.type-Ice:hover{transform: rotate(0deg) scale(1.10) skew(0deg) translate(0px);   background:url(http://i.imgur.com/uufLOLk.png)}
.movemenu button.type-Fighting{background:url(http://i.imgur.com/hkBjSQ2.png)}
.movemenu button.type-Fighting:hover{transform: rotate(0deg) scale(1.10) skew(0deg) translate(0px); background:url(http://i.imgur.com/IvimpSI.png)}
.movemenu button.type-Poison{background:url(http://i.imgur.com/F4JSeNV.png)}
.movemenu button.type-Poison:hover{transform: rotate(0deg) scale(1.10) skew(0deg) translate(0px); background:url(http://i.imgur.com/cKmY6Kn.png)}
.movemenu button.type-Ground{background:url(http://i.imgur.com/HfD3UW8.png)}
.movemenu button.type-Ground:hover{transform: rotate(0deg) scale(1.10) skew(0deg) translate(0px); background:url(http://i.imgur.com/Szrf2un.png)}
.movemenu button.type-Flying{background:url(http://i.imgur.com/Y6XAWm6.png)}
.movemenu button.type-Flying:hover{transform: rotate(0deg) scale(1.10) skew(0deg) translate(0px); background:url(http://i.imgur.com/qiEnZhM.png)}
.movemenu button.type-Psychic{background:url(http://i.imgur.com/ta9EV9f.png)}
.movemenu button.type-Psychic:hover{transform: rotate(0deg) scale(1.10) skew(0deg) translate(0px); background:url(http://i.imgur.com/wyCTbE4.png)}
.movemenu button.type-Bug{background:url(http://i.imgur.com/mvJWmou.png)}
.movemenu button.type-Bug:hover{transform: rotate(0deg) scale(1.10) skew(0deg) translate(0px); background:url(http://i.imgur.com/lqdXcxD.png)}
.movemenu button.type-Rock{background:url(http://i.imgur.com/523IIPS.png)}
.movemenu button.type-Rock:hover{transform: rotate(0deg) scale(1.10) skew(0deg) translate(0px); background:url(http://i.imgur.com/kPd2jmf.png)}
.movemenu button.type-Ghost{background:url(http://i.imgur.com/bGT4IB7.png)}
.movemenu button.type-Ghost:hover{transform: rotate(0deg) scale(1.10) skew(0deg) translate(0px); background:url(http://i.imgur.com/gnZ14wY.png)}
.movemenu button.type-Dragon{background:url(http://i.imgur.com/dJUGqK6.png)}
.movemenu button.type-Dragon:hover{transform: rotate(0deg) scale(1.10) skew(0deg) translate(0px); background:url(http://i.imgur.com/kIGBRyq.png)}
.movemenu button.type-Dark{background:url(http://i.imgur.com/PtLZlWc.png)}
.movemenu button.type-Dark:hover{transform: rotate(0deg) scale(1.10) skew(0deg) translate(0px); background:url(http://i.imgur.com/cWSnAJs.png)}
.movemenu button.type-Steel{background:url(http://i.imgur.com/2A1Rzvs.png)}
.movemenu button.type-Steel:hover{transform: rotate(0deg) scale(1.10) skew(0deg) translate(0px); background:url(http://i.imgur.com/vGQVEtf.png)}
.movemenu button.type-undefined, .movemenu button.type-undefined:hover{background:url(http://i.imgur.com/1ctemFU.png)}
.movemenu button:disabled{background:url(http://i.imgur.com/5V27fmf.png) !important;opacity:.20} 
.movemenu button:disabled:hover{transform: rotate(0deg) scale(0.90) skew(0deg) translate(0px); background:url(http://i.imgur.com/vGQVEtf.png)}

.moveselect {display:none;} 
.movemenu button:disabled:hover{transform: rotate(0deg) scale(0.90) skew(0deg) translate(0px);background:url(http://i.imgur.com/vGQVEtf.png)}

.movemenu label{text-transform:uppercase;font-size: 0px;width:110px;height:110px;color:#b4b4b4;font-weight: bold;margin:0px 0px 0px 45px;padding:61px;background:url(http://i.imgur.com/wHPOyb9.png);top:-450px;left:-30px;position:relative;} 
.movemenu label:hover{background:url(http://i.imgur.com/3ffb8Os.png);}
.movemenu input{width:15px;color:#b4b4b4;height:40px;position:relative;top:60px;left:-102px;margin:-15px -114px 70px 94px}

.movecontrols:after {content:"";background:url('http://i.imgur.com/V4WGvVA.png');opacity:1;position:absolute;top:-95px;left:14px;width:612px;height:72px;z-index:1;}

.whatdo{position:relative;font-size:11.5pt!important;opacity:1!important;bottom:55px;font-family: "Lucida Grande", "Lucida Sans Unicode", "Lucida Sans", Geneva, Verdana, sans-serif;right:-20px;z-index:2;color:#FFF!important;text-shadow: -1px -1px 0 #000, 1px -1px 0 #000, -1px 1px 0 #000, 1px 1px 0 #000;} 

.controls {width:640px;}
.movecontrols{width:320px!important;height:220px!important;}
.switchcontrols{width:290px!important;height:250px!important;margin-left:320px;margin-top:-215px;}

/* Switch */
.switchmenu button, .switchmenu button:disabled{width:137px;height:48px; background:url(http://i.imgur.com/WMic5PP.png) !important; border:none; box-shadow:none; border-radius:0; font-size:10pt;}
.switchmenu button {color:black;text-align: left;text-shadow:1px 1px grey}
.switchmenu button .pokemonicon{margin-bottom:5px;margin-left: 4px;}
.switchmenu button .hpbar{height:2px;width: 92px;border-color: #0e0e0e;margin-bottom:8px;margin-left:27px;}
.switchmenu button:disabled{opacity:.5}
.switchmenu button:hover{transition:All 1.6116s ease;transform: translate(03px);background:url(http://i.imgur.com/rJmTxtU.png) !important; }
.switchselect{display:none;}
.weather{margin-top:60px;}

/* Teambuilder */
div[class|="setchart"], ul.utilichart li h3, .teambuilder-results h3, h1{background:transparent!important;border:0px!important;box-shadow:0px 0px 0px 0px!important;border-radius:0px!important}

/* Other site parts [apart from forum] */
.pfx-body a{color:#000;}
.pfx-body a:hover{color:#AAA;}
.pfx-body a.button {color:#FFF;}
.pfx-body a.button:hover {color:#000;}

.pfx-body a.button.cur {color:#000;}

.button, .ps-nav li a.button, .linklist a:hover, .linklist a.cur, .linklist a.cur:hover,.pfx-loading {border:0!important;background:#478BD9;color:#FFF;text-shadow:none!important;box-shadow:none!important;}
.button:hover, .ps-nav li a.button:hover, .linklist a{background:white;color:#000;box-shadow:0;border: 0;text-shadow:none;}
.button:active, .linklist a:active{box-shadow:0;border: 0;text-shadow:none;}
.linklist li:hover small, .linklist a.cur small, .linklist a.cur:hover small {color:#FFF;}
.linklist li small {color:#000;}
.nav-first img{content: url("http://i.imgur.com/vFUcim0.png");height: 60px; padding-left: 50px;z-index:1;position:relative;}
.main.credits {background:#FFF;padding:1px 15px!important;}
.credits h2:after {border-bottom:1px solid #AAA;}
.credits a {color:#000; border-bottom:1px solid #AAA;text-decoration:none;}
.credits a:hover {color:#478BD9; border-bottom:1px solid #AAA;text-decoration:none;}

/* Textboxes */
.textbox, .textbox:focus:hover, .textbox:focus,.textbox:hover{padding:3px;box-shadow:none!important;background:#FFF;}
.chooser {background:#FFF;}
.colorchooser{display:none;}

/* Replays */
.pfx-panel .replay-wrapper .battle{border:1px solid #AAA;}
.pfx-panel .replay-wrapper .battle-log{position:absolute;top:9px;left:640px;right:0;height:420px;font-size:10pt;color:black;word-wrap:break-word;overflow:auto;overflow-x:hidden;-webkit-overflow-scrolling:touch;overflow-scrolling:touch;font-family:'proxima-nova','Helvetica Neue','Free Sans',Calibri,'Droid Sans',Helvetica,Arial,sans-serif;background: url('http://s3.pokebattle.com/img/bg.png')!important;}
.replay-controls,.replay-controls-2,.urlbox{margin-top:60px;}
