css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("gw2wvw.net") {
body {
background-color: #111;
}
#page, #header, #page-wrapper, #content {
background-color: #111;
background-image: none;
border: none;
}
 
ul.menu li {
border: none;
background-color: #222;
padding: 0;
margin-bottom: 5px;
}
 
ul.menu li.menuparent a {
padding-right: 25px !important;
}
 
ul.menu a:hover {
text-decoration: none;
}
 
ul.menu li:first-child {
margin-left: 0;
}
 
a > .sf-sub-indicator {
top: 14px;
}
 
#footer-wrapper {
background: #111;
}
 
#logo {
padding-top: 0 !important;
}
 
#menubar, #submenubar, #navigation {
border: none;
}
ul.menu li a {
background-color: #222;
border: none !important;
height: 30px !important;
padding: 10px 10px 0px !important;
color: #ddd !important;
}
 
ul.menu li a:hover {
background-color: #D33B3B !important;
}
 
.poll .bar .foreground {
background-color: #D33B3B;
}
 
ul.menu li.menuparent li a {
height: auto !important;
padding-bottom: 0.8em !important;
}
 
.menuparent ul, .menuparent li {
margin: 0 !important;
border-radius: 0 !important;
padding: 0 !important;
}
 
.sf-megamenu {
border: none !important;
}
 
#breadcrumb a {
font-style: normal;
font-size: 0.9em;
}
 
#block-superfish-1 {
float: left;
}
 
#block-superfish-2 {
float: right;
}
 
#page #content h2, #page #content h1{
display: block;
padding: 0.5em 0.3em;
background: none;
font-size: 1.8em;
font-weight: 100;
color: inherit !important;
}
 
.panel-pane {
background: #333;
}
 
.pane-content {
background-color: #262626;
padding: 0.5em 0.3em;
}
 
#forum .pane-content {
padding: 0;
background-color: #111;
}
 
.pane-content h2 {
font-size: 1.3em !important;
}
 
table.views-view-grid tr {
background-color: transparent;
}
 
.node {
background-color: transparent;
border: none;
}
 
#content a {
color: #D33B3B;
}
 
.view-header a, .tabs li a, .af-button-large, ul.links a,
ul.pager a
{
display: inline-block !important;
background-color: #050505 !important;
padding: 0.4em 0.5em !important;
margin-right: 0.5em !important;
border: none !important;
}
 
.view-header a:hover, .tabs li a:hover, .af-button-large:hover, ul.links a:hover,
ul.pager a:hover
{
background: #D33B3B !important;
color: #000 !important;
text-decoration: none !important;
}
 
.tabs {
float: right;
}
 
table.cols-2 td {
width: 50%;
}
 
.views-field-term-node-tid {
font-size: 0.8em;
}
 
.views-field-field-youtube-video {
width: 100%;
}
 
.panels-flexible-region-2-center {
float: left;
width: 100%;
}
 
.forum-number-topics, .forum-number-posts, td.views-field-comment-count, td.views-field-totalcount {
font-size: 22px;
font-weight: 100;
padding-top: 10px !important;
}
 
th.views-field-comment-count, th.views-field-totalcount {
text-align: center;
}
 
.forum-number-new-topics, .forum-number-posts a, .views-field-comment-count a {
font-size: 12px
}
 
#forum th {
font-weight: 100;
background-color: transparent;
border: none;
}
 
.forum-table-wrap {
margin-bottom: 1em;
background-color: #262626;
padding: 1em;
}
 
.forum-last-reply {
font-size: 0.8em;
line-height: 15px;
}
 
#forum .views-field-title {
font-size: 1em;
color: #666
}
 
.topic-pager {
color: #666;
font-style: normal;
font-size: 10px
}
 
.marker {
font-variant: small-caps;
color: white;
}
 
tr.odd, tr.odd td {
background-color: #222;
}
 
tr.even, tr.even td {
background-color: #151515;
}
.forum-table-superheader {
font-size: 1.2em
}
.forum-table-description {
font-size: 14px;
}
.views-field-topic-icon {
padding: 0.3em 1em !important;
}
.sticky-header tr {
background-color: #111;
border: none;
}
 
table.sticky-header {
background-color: #111;
}
 
.forum-post-panel-main {
min-height: 0;
}
.author-pane-line {
font-size: 11px;
color: #666;
}
.forum-post {
background: #222;
margin-bottom: 20px !important;
}
.forum-post-panel-main {
border-left: 1px solid #333;
}
 
.author-signature {
border-top: 1px solid #333;
padding: 0.5em 0 ;
margin-right: 1em;
color: #666;
}
.forum-post-footer {
border-top: 1px solid #333;
}
.forum-post-footer a, .forum-post-footer div, .forum-post-footer li, .forum-post-footer ul {
margin: 0 !important;
}
 
.forum-post-footer ul.links a, .forum-post-footer .statistics_counter {
height: 20px;
padding: 0.5em 0.5em !important;
display: block;
line-height: 17px;
background-color: transparent !important;
}
 
.forum-post-info {
color: #666;
padding: 0.3em;
}
 
 
.forum-post-footer .forum-jump-links {
margin: 0.4em 0 0 1em !important;
font-size: 12px;
}
 
form fieldset {
border: 1px solid #333;
}
 
 
.quote-msg, .quote-snip {
border: 1px solid #333;
padding: 1em;
margin-right: 1em;
}
 
.quote-author {
display: block;
font-weight: bold;
margin: 0 0 0.5em;
}
 
#page-title {
color: #eee;
padding-left: 0 !important;
}
 
blockquote {
border-color: #444;
}
 
.views-row {
margin: 0.5em 0.5em 1em;
padding: 1em;
background: #161616;
}
 
.node .submitted {
color: #666;
}
.views-view-grid td {
background: #161616;
border: 10px solid #262626
}
.views-field-title {
font-size: 1.2em;
margin-bottom: 1em;
}
 
.views-view-grid .views-field-field-youtube-video img {
width: 100%;
}
 
.field-type-taxonomy-term-reference {
float: right;
clear: right;
}
.field-name-field-youtube-video {
float: right;
clear: right;
}
 
.sf-menu.sf-horizontal.sf-shadow ul, .sf-menu.sf-vertical.sf-shadow ul, .sf-menu.sf-navbar.sf-shadow ul ul {
background: #222 !important;
padding: 0 !important;
border: none !important;
border-radius: none !important;
box-shadow: 0px 5px 5px #000;
margin: 0 !important;
}
 
body.page-node .node {
background-color: #262626;
padding: 1em;
border-radius: 0;
margin-top: 1em;
}
 
body.page-node .node .content .field {
float: none;
}
}
