css:
@-moz-document url("http://space.galaxybuster.net/go.php") {
body
{
	background-color:#1a477b;
  	background-image: url("http://i.imgur.com/LdaOzcW.png");
}

label, a
{
	color: #ffffff;
}

#dash
{
	background-color: #245b9d;
	border-bottom: 1px solid #ffffff;
    background-image: url("http://i.imgur.com/TQHj2ii.png");
	color: #ffffff;
}

#right-sidebar
{
	background-color: #245b9d;
	color: #ffffff;
	border-left: 1px solid #ffffff;
  
  	background-image:url('http://i.imgur.com/mG64Q1t.png');
}

#container
{
    background-image: url("http://i.imgur.com/TQHj2ii.png");
	background-color: #245b9d;
	border: 2px solid #ffffff;
    font-weight:bold;
}

#titlebar
{
	background-color: #ffffff;
	border-bottom: 3px double #245b9d;
}

#comSender, #comSubject, #comBody
{
    background-image: url("http://i.imgur.com/TQHj2ii.png");
	border: 1px solid #ffffff;
	background-color: #245b9d;
    font-weight:bold;
	color: #ffffff;
}

#t-container
{
    background-image: url("http://i.imgur.com/TQHj2ii.png");
	background-color:#245b9d;
}

#inbox
{
    background-image: url("http://i.imgur.com/TQHj2ii.png");
	border: 1px solid #ffffff;
	color: #ffffff;
}

.row-message:hover, .admin, .row-header
{
	background-color: #ffffff;
	color: #245b9d;
    font-weight:bold;
}

.msg-red { color: #fe6854; }
.msg-red:hover { background-color: #fe6854;color: #245b9d;cursor: pointer; }
.msg-lime { color: #6ce235; }
.msg-lime:hover { background-color: #6ce235;color: #245b9d;cursor: pointer; }
.msg-cyan { color: #16e2b9; }
.msg-cyan:hover { background-color: #16e2b9;color: #245b9d;cursor: pointer; }
.msg-blue { color: #6da4ff; }
.msg-blue:hover { background-color: #6da4ff;color: #245b9d;cursor: pointer; }
.msg-white { color: #000000; }
.msg-white:hover { background-color: #000000;color: #245b9d;cursor: pointer; }
.msg-pink { color: #ff6da7; }
.msg-pink:hover { background-color: #ff6da7;color: #245b9d;cursor: pointer; }


#options-container, #preview, #options-view, #compose-options-view, #pages, #notif, #view-container
{
	color: #ffffff;
    font-weight:bold;
}

#starred-container
{
    background-image: url("http://i.imgur.com/TQHj2ii.png");
	background-color:#245b9d;
  	font-weight:bold;
}

#starred-inbox
{
    background-image: url("http://i.imgur.com/TQHj2ii.png");
	border: 1px solid #ffffff;
	font-weight:bold;
	color: #ffffff;
}

.bb-hr
{
	border-top: 1px solid #ffffff;
	color: #ffffff;
}

#bannerbody
{
	background-color: #ffffff;
}

#banner a
{
	color: #245b9d;
}

#banner-close:hover
{
	color: #ffffff;
}

#b-payme
{
	background-color: #245b9d;
	color: #ffffff;
	border: 3px solid #245b9d;
}

#b-payme:hover
{
	background-color: #ffffff;
	border: 3px solid #245b9d;
	color: #245b9d;
}

#sideb-payme
{
	background-color: #ffffff;
	color: #245b9d;
	border: 3px solid #ffffff;
}
#sideb-payme:hover
{
	background-color: #245b9d;
	border: 3px solid #ffffff;
	color: #ffffff;
}

#pwyw
{

	background-color: #ffffff;
	border-bottom: 4px solid #245b9d;
}

.button-container
{
	width: 110%;
	margin: 0 auto;
    margin-left: -10;
}

.btn
{
	display: block;
	border: 1px solid #245b9d;
	background-color: #ffffff;
    font-weight:bold;
	color: #245b9d;
  	width: 23%;
	margin-right: 2%;
}

.btn:hover, .on
{
    background-image: url("http://i.imgur.com/TQHj2ii.png");
	background-color: #245b9d;
    font-weight:bold;
    border: 0px;
	color: #ffffff;
}

.btn:last-of-type
{
	margin-right: 5;
	float:right;
}

.select-container select
{
	background-color: #ffffff;
	border: 1px solid #ffffff;
	color: #245b9d;
}

#notif-spin > div {
	background-color: #ffffff;
}

.spinner > div {
	background-color: #ffffff;
}

#motd
{
	color: #2b6ab6;
	position: relative;
	left: 3px;
    font-weight:bold;
}

#motd::before
{ 
content:url('http://i.imgur.com/Nq0ToSA.png');
}

input[type="checkbox"] + label span
{
	background:url('http://i.imgur.com/6PNiVLA.png') left top no-repeat;
}
input[type="checkbox"]:checked + label span
{
	background:url('http://i.imgur.com/6PNiVLA.png') -14px top no-repeat;
}

#notif
{
	float: right;
	color: #2b6ab6;
}
#flag-notif
{
	float: right;
	margin-left: 32px;
}
}
