css:
@-moz-document url-prefix("http://www.tumblr.com/"), url-prefix("https://www.tumblr.com/") {
/* If you are having trouble with your theme please feel free to contact me at tiranasgoldfish.tumblr.com*/

#dashboard_index, #dashboard_edit_post, #dashboard_submissions, #dashboard_drafts,#dashboard_post_queue, #dashboard_followers, #dashboard_members, #dashboard_useraction_followers, #activity_actions_index, #ui_dialog_lock, #dashboard_posts_likes,  #tagged_actions_display /*#search_actions_search*/, #dashboard_posts_editing, #settings_actions_tumblelog, #dashboard_messages_inbox, #main_help, #settings_actions_account, #settings_actions_dashboard, #settings_actions_notifications, #settings_actions_apps

{background: url(http://38.media.tumblr.com/42c267a9516bf52b39af16af3b50a112/tumblr_n8vi0jaFJK1txq8ozo1_r2_1280.png) no-repeat bottom left fixed !important; background-color: #cfe5ff !important;}


#dashboard_index div.l-content, #dashboard_submissions div.l-content, #dashboard_drafts div.l-content,#dashboard_post_queue div.l-content, #dashboard_members #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {background: rgba(0,0,0,0) !important}

/**/
.controls_section .well {
color: rgba(255,255,255,1);
}
    
.right_column .small_text {
color: rgba(255,255,255,1);
}

.help_search {
position: relative;
border: 2px solid #FFF;
background: rgba(255,255,255,.6)
}

.help_search:after{
box-shadow: none;
    }

.help_search input {
background: #F7F7F7 url('https://www.tumblr.com/images/corp_pages/icon_search.png?4bf24e0a63e6e3c5122536e222d860f7') 0 -60px no-repeat;
border: 1px solid rgba(113, 196, 252, 1);
box-shadow: none;
}

#main_help .chrome.clear{
background: rgba(255,255,255,.6);
border: 1px solid rgba(113, 196, 252, 1);
color: rgba(113, 196, 252, 1);
}

/*post tab fade*/
.plexi.show.color {
background: rgba(82, 128, 182, 0.6);
}


/*Search Bar----------------*/
.search_form_row {
position: relative;
background-color: #FFFFFF/*#71C4FC*/;
  opacity:.7
}

.popover.search_popover .search_subheading {
background: rgba(113, 196, 252, 1);
color: #FFFFFF;
}

.tag_controls .track_tag_wrapper a{
  color:rgba(113, 196, 252, 1);
}

.popover_menu_item a {
  color:rgba(113, 196, 252, 1);
}

/*placeholder text*/
.search_form_field
::-webkit-input-placeholder {
   color: rgba(113, 196, 252, 1);
  opacity:.6;
}

.search_form_field .search_query {
  color: #71C4FC;
}

/*END search bar----------------------*/

/*search page - do i carae enough to finish it (no)
.search_results_flex .l-container:before {
background-color: rgba(58, 88, 117, 0);
}

.search_controls {
color: rgba(113, 196, 252, 1);
}

.search_controls .search_control_button {
color: rgba(113, 196, 252,, 1);
}
/*no more search page------------------*/

/*links*/
a {
color: #2374B4;
}

/*quote color*/
.post .post_content blockquote {
border-left: solid 4px rgba(5, 125, 255, 0.15);
}
    
/*Activity page divider*/
.notifications_page .section.divider {
background: transparent;
}

      
/*whichever right_column section is the current one*/      
.controls_section li:not(.no_push):not(.section_header):not(.popover_blogs).selected a{
background-color: rgba(113, 196, 252, .25);
}
          
/*hover over selected*/
.controls_section li:not(.no_push):not(.section_header):not(.popover_blogs).selected:hover a {
background-color: rgba(113, 196, 252, 0.15);
}      

/*hover over non-selected*/
.controls_section li:not(.popover_button_blogs):not(.popover_blogs):hover a {
background-color: rgba(113, 196, 252, 0.15);
}      

/*hover over blogs
.controls_section li:(.popover_button_blogs):hover a {
background-color: rgba(113, 196, 252, 0.15);
}             
*/              
          
#left_column #posts .notification .avatar {opacity:0.7 !important;}
#left_column #posts .notification .avatar:hover{opacity:1 !important;}

/*#right_column{background-color: #FFFFFF opacity:1 !important;}



/*arrows*/        
.sub_control {
color: rgba(255, 255, 255, 1) !important;
}

/*# for posts, followers, and drafts*/    
.count {
color: rgba(255, 255, 255, 1) !important;
}                

/*Right_column text*/    
.controls_section li a {
color: rgba(113, 196, 252, 1) !important;
}

.small_links a {
color: rgba(113, 196, 252, 1) !important;
}

/*line*/
.popover_button_blogs {
border-bottom: 2px solid rgba(255, 255, 255, 1) !important;
}

/*line + account*/
li.section_header {
border-bottom: 2px solid rgba(255, 255, 255, 1) !important;
color: rgba(113, 196, 252, 1) !important;
font-weight: bold;
font-size: 1.5;
text-transform: lowercase;
}

/*lines*/
.controls_section li {
border-top: 1px solid rgba(255, 255, 255, 0.7) !important;
}

/*Tumblr Radar fadey bit*/
.right_column:after {
  background: transparent !important;
}


/*Activity Sparkline (brightness to 150% if you want a good match*/
#right_column canvas {
-webkit-filter: brightness(600%) hue-rotate(20deg) saturate(500%) !important;
}

/*Nipple up top*/
.selection_nipple {
border-bottom: 12px solid rgba(113, 196, 252, 0.4) !important;
}


/*So you don't get the weird corner thing when you click a post from your dash*/
.post_permalink:before {
border-right: 16px solid rgba(200, 229, 255, 1) !important;
}


#new_post_notice_container{color:#fff!important}


#posts .notification {
	opacity:0.8 !important;
	background:  #71C4FC !important;
	border: 1px #ffffff solid !important;
	margin: -1px;
	}

#posts .notification .nipple.border, #posts .notification.alt .nipple.border {
	border-right: 7px solid #000000 !important;
	}

#posts .notification .nipple, #posts .notification.alt .nipple {
	border-right: 6px solid #163056 !important;
	}

#posts .notification {
  color: rgba(255,255,255,0.8) !important;
}
    
.post_full {
background: rgba(255,255,255,0.85);
}

.post .post_content {
background: transparent;
clear: both;
}

a.username {
color: rgba(255,255,255,1) !important;
}

.notification_sentence a {
font-weight: bold;
color: rgba(255,255,255,1) !important;
}

.right_column {background-color: rgba(255,255,255, .2);}


.l-content.clearfix{
background-color:  rgba(113, 196, 252, 0.05);
}
}
