css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain('himado.in') {

    #player_ad_window,#player_ad_tag,#sh_fc2footer_fix,.thumbblock_3colum_ama,.movie_beforeafter_comment,.amaimage_skycraper,.amabanner_body,.amabanner_creator {
    display:none!important;
    }

    #movie_beforeafter table,#body_movie #content,#topmovie_left_box,#topmovie_right_box,#undermovie,#movie_left_box,#movie_right_box,#header > table:nth-child(1),.tb_noborder,.rowdata {
    width:100%!important;
    }

    #leftbox,#fc2count {
    padding:10px!important;
    }

    #content {
    padding:0 10px!important;
    }

    #header {
    margin-bottom:10px!important;
    }

    .skycraper {
    width:300px!important;
    }

    .searchbox {
    width:calc(100% - 100px)!important;
    border:1px solid #999!important;
    }

    #content,#topmovie_left_box,#topmovie_right_box,.head3 {
    box-sizing:border-box!important;
    border:none!important;
    }

    #author_comment {
    border:none!important;
    background:#eee!important;
    margin:10px 0!important;
    }

    #player {
    position:relative!important;
    width:100%!important;
    height:0!important;
    padding-top:25px!important;
    padding-bottom:56.5%!important;
    }

    #playerswf {
    position:absolute!important;
    top:0!important;
    left:0!important;
    width:100%!important;
    height:100%!important;
    max-height:950px!important;
    z-index:0!important;
    }

    #movie_beforeafter {
    overflow:auto!important;
    height:auto!important;
    }

    #movie_beforeafter table tbody tr {
    display:inline-block!important;
    overflow:hidden!important;
    width:250px!important;
    height:150px!important;
    margin-bottom:5px!important;
    border:1px solid #999!important;
    background:#eee!important;
    }

}
