css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain('crunchyroll.ca'), domain('crunchyroll.com') {

    * {
    line-height:1.5em!important;
    box-sizing:border-box!important;
    box-shadow:none!important;
    margin:0;
    padding:0;
    }

    .message-container.cf,.showmedia-submenu,div.showmedia-related,.orange-button,#slidebox,.guestbook,.menubar-item-beta > span,.showmedia-btns {
    display:none;
    }

    .new_layout,#message_box,#template_body,#template_container,#header_container,#main_content {
    width:100%!important;
    margin:auto!important;
    }

    #template_scroller {
    padding:2px!important;
    }

    #template_container,.cf,.template-container,.no-adskin {
    padding:0!important;
    }

    .showmedia_video_box {
    position:relative;
    height:0;
    padding-top:25px;
    padding-bottom:56.25%;
    }

    #showmedia_video_player {
    width:100%;
    height:800px;
    }

    h1.ellipsis {
    text-align:center;
    margin:5px!important;
    }

    #header_beta,#header_menubar_beta li a,#header_userpanel_beta .login a,#header_userpanel_beta .signup a,#header_searchpanel_beta,.search,#header_search_beta,#main_searchbox,#header_search_input,#header_searchpanel_beta #header_search_input,#header_userpanel_beta a {
    top:0!important;
    height:28px!important;
    }

    #logo_beta {
    position:absolute;
    top:-10px!important;
    margin:auto;
    }

    #sidebar {
    float:none;
    width:100%;
    background:#FFF;
    margin:auto!important;
    padding:10px;
    }

    .collection-carousel {
    float:none!important;
    margin:auto!important;
    }

    #main_content_white,.white-wrapper,.large-margin-bottom,#footer {
    margin:0!important;
    padding:0!important;
    }

}
