css:
@-moz-document url-prefix("http://dota2lounge.com/"), url-prefix("http://csgolounge.com/"), url-prefix("https://csgolounge.com/"), url-prefix("https://csgolounge.com/"){

body {
background: #333;
}

p {
color: #aaa; 
}

div, .standard {
color: #999; 
}

#modalPreview {
  box-shadow: #1e1e1e 1px 1px 3px 1px;
  background-color: #222;
}

.value {
  color: #999;
  text-shadow: -1px 1px #111;
  background: rgba(0,0,0,0.35);
}

.title, .title a {
color: #666;
background: none !important;
padding-left: 8px !important;
}
 
.box {
background-color: #333;  
}

#skin {
display: none;
}

#submenu {
border-left: 1px solid #272727;
background: none;
padding-top: 1px;
}

#submenu div {
margin: 0; 
}

#submenu span, #submenu #follow, footer {
display: none; 
}

header {
border-bottom: 1px solid #222;
box-shadow: none;
background: #272727;
}

.matchheader .buttonright {
 float: right;
}

.tradeheader, .bpheader, .matchheader {
border-top: 1px solid #272727;
background: #333;
background: -webkit-radial-gradient(top, ellipse cover, #373737 0%,#333 70%);
background: -o-radial-gradient(top, ellipse cover, #373737 0%,#333 70%);
background: -ms-radial-gradient(top, ellipse cover, #373737 0%,#333 70%);
background: radial-gradient(ellipse at top, #3c3c3c 0%,#333 70%);
}
  
/*
.message {
box-shadow: -1px -1px 1px #222;
background: #CCC;
background: -webkit-radial-gradient(0 0, ellipse cover, #3c3c3c 0%,#333 50%);
background: -o-radial-gradient(0 0, ellipse cover, #3c3c3c 0%,#333 50%);
background: -ms-radial-gradient(0 0, ellipse cover, #3c3c3c 0%,#333 50%);
background: radial-gradient(ellipse at 0 0, #3c3c3c 0%,#333 50%);
}
*/

.message {
  background: #2c2c2c !important;
  box-shadow: 1px 1px 2px #151515;
}
  
#tradelist a, .whenm, .user {
color: #999 !important;
text-shadow: none !important;
}
  
textarea {
color: #999; 
}

div.match {
background: #2c2c2c !important; 
box-shadow: 1px 1px 2px #151515;
}

a.match {
box-shadow: 1px 1px 2px #151515;
}

.right, .left, .winnings, .box-shiny, .box-shiny-alt, #satrade, .left {
 background: #2c2c2c !important; 
box-shadow: 1px 1px 2px #151515;
border: none;
}
  
.winsorloses {
 background: #2c2c2c !important;
 padding-top: 5px;
}

.box-shiny-alt a {
color: #ccc;
text-shadow: 1px 1px 1px #222;
}
 
.gradient {
background: #222 !important;
box-shadow: 1px 1px 2px #1f1f1f;
border-bottom: 1px solid #111;
}

#replist li {
 padding: 5px !important; 
}

td {
 padding-top: 5px !important;
 padding-right: 15px !important;
}

tr {
 border-bottom: 0 !important;
 border-top: 0 !important;
}

.donor, .donor2, .donor3, .donor4, .donorUq1, .donorUq2, .donorUq3, .donorUq4, .donorUq5, .donorUq6,.staff, .s {
text-shadow: none !important;
color: #999 !important;
}

#replist li:nth-child(even), tbody {
  background: #2c2c2c !important;
}

hr {
  border-top: 1px solid #222;
  border-bottom: none; 
}
  
.matchleft {
width: 100%;
}
  
.teamtext {
background: none;
color: #ccc;
}

.match a {
color: #ccc;
}

object {
height: 450px;
}

.tab {
color: #777;
font-weight: normal;
background-color: #1c1c1c; 
border-bottom: none;
box-shadow: 0 1px #1f1f1f;
}
  
.tab:hover {
background-color:#1c1c1c;        
}

.button, .buttonright {
color: #aaa !important;
float: left;
margin: 0.4em;
font-size: 13px;
padding: 5px;
text-shadow: 1px 1px 0 #222 !important;
box-shadow: 1px 2px 2px #222;
-webkit-border-radius: 3px;
border-radius: 3px;
background: #b4b4b4;
background: -webkit-linear-gradient(bottom, #333 0%,#a2a2a2 100%);
background: -o-linear-gradient(bottom, #c3c3c3 0%,#a2a2a2 100%);
background: -ms-linear-gradient(bottom, #c3c3c3 0%,#a2a2a2 100%);
background: linear-gradient(to bottom, #3c3c3c 0%,#333 100%);
}

.button:hover, .buttonright:hover {
box-shadow: 0px 0px 3px #444;
background: #BBB;
}

.selectbox {
background: none repeat scroll 0 0 #272727;
position: relative;
border: none;
color: #999;
margin: 3px;
font-size: 13px;
padding: 3px;
height: 30px;
border-radius: 3px;
box-shadow: 1px 1px 1px #1f1f1f;
}

predict {
background: red !important; 
}
.item {
background: #1f1f1f; 
}
  
.Consumer, .Base {
box-shadow: none;
}

.item .rarity {
  font-weight: bold;
  background: rgba(0,0,0,0.75);
  color: #999;
}

.whenm, .eventm {
  font-weight: bold;
  text-shadow: none;
}

.name, .name .oitm:hover {
  border: 1px solid #1e1e1e;
  color: #e2e2e2 !important;
  background: #414141;
}

.msgtxt a {
  text-shadow: none;
  color: #ff8a00;
}

.name a {
  text-shadow: none !important;
  color: #aaa !important;
}

.button:hover, .buttonright:hover {
  box-shadow: none;
  background: #4b4b4b;
}

#chat_embed, .share-buttons {
  display: none;
}

.full td {
  font-size: 14px;
  margin-top: 5px;
  padding-bottom: 8px;
}
  
.full td:first-child {        
  padding-left: 5px;
}
  
.full td:last-child {
  font-size: 12px;
  padding-bottom: 0;
  color: #666;
  font-weight: bold;
}

.full tr:nth-child(odd) {
  background: #222;
}

table a {
  color: #999 !important;
}

.full .lost {
  text-shadow: none;
  color: #D12121;
}
  
.full .won {
  text-shadow: none;
  color: #72A326;
}

.matchheader .whenm span {
  text-shadow: none !important;
}

div.trade-description {
  display: none;
}

.appID730 .marketPriced .rarity {
  text-shadow: none;
  color: #999;
  background: rgba(0, 0, 0, 0.75) !important;
}

.inventoryStatisticsBox {
  background: #272727;
  box-shadow: 1px 1px 1px #1f1f1f;
}

#preview.destroyer {
  background: #333;   
}

#submenu>nav {
  margin-top: -1px;   
}

.ld-dark { 
  background: #2c2c2c;
}

.ld-dark section {
    box-shadow: 1px 1px 2px #151515;
}

.ld-dark #submenu nav a {
  background-color: #272727;
  border-top: 1px solid #303030;
  border-bottom: 1px solid #222;
}

.ld-trade-filters, .ld-match-filters, div.trade-description, .ld-steam-extra {
  background-color: #272727;
  box-shadow: 1px 1px 2px #151515;
}

.ld-steam-extra .ld-steam-info {
  border-right: 1px solid #202020;
}


.ld-steam-extra::after {
  background-image: none;    
}

.ld-trade-filters a, .ld-match-filters a {
  color: #dedede;  
}

div.trade-description p {
  color: #999;
}

#pleft {
  background-color: #333;
}

#pright {
  background-color: #1f1f1f;
}

#submenu a{
    display: none;
}

}
