css:
@-moz-document url-prefix("http://zlogames.ru/") {
*::selection {
  color: #fff;
  background: #A2AA8F;
}

div.ipsSideBlock, tr._recordRow, .ipsMemberList, #board_stats, div.post.entry-content {
  text-shadow: -1px 0 1px black, 0 1px 1px black, 1px 0 1px black, 0 -1px 1px black !important;
}

#footer_utilities {
  padding: 5px;
  border-radius: 10px;
  background: rgba(0, 0, 0, 0.3) !important;
}

#copyright {
  display:none;
}

#backtotop {
  margin-top: -50px;
}

iframe {
  max-width: 100%;
  display: block;
  margin: 0 auto;
}

div#branding {
  margin-top: 10px;
}

div#logo {
  margin-top: 5px;
}

div.wrapper {
  max-width: 1280px;
}

div#wrapper {
  background: rgba(66, 83, 40, 0.2);
  min-width: 990px;
}

div.ipsLayout_right.clearfix > div.ipsLayout_right {
  width: 240px;
  margin-right: -250px;
}

div.ipsLayout_withright.clearfix {
  padding-right: 250px;
}

div#board_index {
  padding-right: 280px;
}

div.ipsBox.clear, div.ipsBox.table_wrap {
  background: rgba(0, 0, 0, 0.2);
  border-radius: 10px;
}

div.ipsBox_container.ipsPad, div.ipsBox_container {
  background: rgba(0, 0, 0, 0.5);
  border-radius: 8px 8px 0 0;
}

.ipsBox_container {
  background: rgba(0, 0, 0, 0.5);
}

.general_box h3 {
  border-radius: 0 0 8px 8px;
}

.general_box h3 > ul {
  position:absolute;
  margin-top:-18px
}

div.post.entry-content {
  background: rgba(0, 0, 0, 0.2);
  padding: 15px 10px 0 10px !important;
  border-radius: 10px;
}

div.post_wrap > div.author_info {
  clear: both;
}

.ipsBadge_orange {
  background: #755A40 !important;
}

.reputation.negative {
  background: #682210 !important;
}

.ipsBadge_green, .reputation.positive {
  background: #687C35 !important;
}

li#nav_backgrounds > a {
  background: #5A663C;
  box-shadow: 0 0 5px rgba(0,0,0,0.5);
}

table#forum_table, table.ipb_table {
  border-collapse: separate !important;
  border-spacing: 0 10px !important;
}

tr._recordRow,
div.category_block.block_wrap table tr,
table#forum_table tr,
ul#recentTopics_table tr {
  background: rgba(0, 0, 0, 0.3) !important;
}

div.content_border {
  border:none;
}

ul#recentTopics_table > div > li tr,
table#announcements tr {
  background: rgba(90, 100, 65, 0.6) !important;
}

ul#recentTopics_table > div > li tr span.desc.lighter.blend_links,
ul#recentTopics_table > div > li tr span a,
ul#recentTopics_table > div > li tr li a,
ul#recentTopics_table > div > li tr li {
  color: #AFA69D;
}

div#recentTopicsWrapper div,
div.category_block.block_wrap > div > div{
  background: transparent;
  border: none;
}

tr td:first-child {border-top-left-radius: 10px}
tr td:last-child {border-top-right-radius: 10px}
tr td:first-child {border-bottom-left-radius: 10px}
tr td:last-child {border-bottom-right-radius: 10px}

td.col_f_icon.short.altrow {
  background: rgba(0,0,0,0.3);
}

div.ipsSideBlock > ul > li {
  border-radius: 10px;
  margin-bottom: 5px;
  background: rgba(0,0,0,0.5);
  background-image: -webkit-gradient(linear, left top, left bottom, from(rgba(255,255,255,0.15)), to(transparent));
  background-image: -moz-linear-gradient(top, rgba(255,255,255,0.15) 0%, transparent 100%);
}

td.col_f_post {
  width: 185px !important;
}

td.col_f_post a[href*="getlastpost"]:after {
  content:" â†’";
}

a[hovercard-ref="member"] > span {
  color: #A2AA8F;
  font-size: 10px;
}

a[hovercard-ref="member"] i, .ipsSideBlock i, div#board_statistics span {
  font-style: normal;
}

div.ipsSideBlock > span.desc, div.ipsSideBlock div > p {
  color: #a8a8a8;
}

h3 a[hovercard-ref="member"] > span {
  color: #fff;
}

img[src*="downloads_traffic_images/browser"] {
  display:none;
}

a[href*="search_tags"] > span {
  color:#978D7E;
}

a[hovercard-id="3028"] > span span {
  color:#E2B835 !important;
}

a.ipsBadge.ipsBadge_green > img {
  content:url("http://deadland.su/forum/public/style_images/warrior/bullet_star.png");
}
}
