css:
@-moz-document domain("n0l.ch") {
html, body, footer, form table tr th, li, div.banner, div#blotter.blotter, div.pages {
  	font-family: "Courier New", Consolas, monospace !important;
  	background: white !important;
  	font-size: 10pt!important;
  	color: #333 !important;
}
* {
	font-family: "Courier New", "Consolas", monospace !important;
  	text-shadow: none !important;
  	font-size: 1em !important;
}
body {
  	background: white !important;
  	margin:0 !important;
  	margin-bottom:auto !important;
}
body form {
	margin-bottom: 14px !important;
}
div.body-borders {
	display: none !important;
}
a {
  	color:#666666 !important;
  	text-decoration:underline !important;
}
a.post_no {
  	font-size: 1em !important;
  	font-weight: bold !important;
}
blockquote blockquote {
  	margin-left:0em !important;
}
form {
  	margin-bottom:0px !important;
}
h1, div.subtitle, h2 {
 	width:auto !important;
    font-weight: normal !important;
    font-size: inherit !important;
    color: inherit !important;
  	letter-spacing: 0px !important;
}
h1:after {
  	content: ".txt" !important;
}
code {
  	border:#666666 dashed 1px !important;
}
pre {
  	font-family:Consolas, "Lucida Console", monospace  !important;
  	margin:0em !important;
}
a.post_no {
  	color:#333333 !important;
  	text-decoration:none !important;
}
a.post_no:hover	{
  	color:#800000 !important;
}
a.post_no:visited {
  	color:#333333 !important;
}
span.quote, span.omitted, span.subject, span.toolong, summary, div.post.op label:after, div.post.reply label:after {
  	color: inherit !important;
}
div.post.reply {
  	border: 1px dashed #333 !important;
  	border-width: 1px 0 !important;
   	border-radius: 0 !important;
  	-webkit-border-radius: 0 !important;
   	background: white !important;
   	padding: 1px !important;
}
div.post.reply.highlighted, div.post.reply.post-hover {
  	border:	1px dashed #333 !important;
}
span.mentioned.unimportant a {
  	font-size:11px !important;
}
span.spoiler:hover, span.spoiler:hover a {
  	color: white !important;
}
span.spoiler, span.spoiler a {
  	color: black !important; background: black !important;
}
span.spoiler a, span.spoiler a:visited, span.spoiler a:hover {
  	color:inherit !important;
}
div.menu li a {
	display: inline !important;
}
hr {
	border: 1px dashed #333 !important;
  	height: 0px !important;
}
input, select, textarea, span#toggle-frame, span#toggle-gallery {
	padding: 1px !important;
	background-color: white !important;
	border: black 1px solid !important;
  	color: black !important;
}
span#toggle-frame, span#toggle-gallery {
	opacity: 1 !important;
  	height: 16px !important;
}
input[type="file"], form#quick-reply table {
	border: none !important;
}
div.pages {
  	border: none !important;
  	opacity: 1 !important;
  	top: -36px !important;
}
div.pages.top {
  	border: 1px black solid !important;
  	border-right: 0px !important;
  	border-top: 0px !important;
  	top: 0 !important;
}
ul#linklist li {
	font-weight: bold !important;
}
ul#linklist li ul li {
	font-weight: normal !important;
}
ul#linklist li a:hover {
	background-color: #1e90ff !important;
	color: #ffffff !important;
}
a.close-btn, a.quick-reply-btn, a.hide-thread-link, a.unhide-thread-link {
	text-decoration: none !important;
}
}
