css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("slashdot.org") {
    .ts{
        width: 95% !important;
    }
    
    .container{
        width: 95% !important;
    }
    
    .split-right{
        background-image: none !important;
    }
    
    .river-left{
        margin-right: 250px !important;
        border-right: 10px solid #DFDFDF !important;
    }
    
    .rail-right{
        margin-left: -250px !important;
    }
    
    .col-rail{
        width: 250px !important;
    }
    
    .col-rail aside{
        padding: 12px;
    }
    
    .col-rail aside:nth-child(2), 
    .col-rail aside:nth-child(3), 
    .col-rail aside:nth-child(4), 
    .col-rail aside:nth-child(5),
    .col-rail aside:nth-child(6),
    .col-rail aside:nth-child(7),
    .col-rail aside:nth-child(9){
        display: none;
    }
    
    .rail-module .row .span7, .rail-module .row .span5{
        width: 100%;
    }
    
    .nls .btn{
        left: 0;
    }
    
    .featured-wrap{
        height: 105px !important;
    }
    
    .feature-top, .feature-left, .feature-right{
        width: 100% !important;
        height: 35px !important;
    }

    .feature-top .story-header, 
    .feature-left .story-header, 
    .feature-right .story-header{
        width: 100% !important;
        padding-left: 0 !important;
    }
    
    .feature-top .story-header h1, 
    .feature-left .story-header h1, 
    .feature-right .story-header h1{
        height: 35px;
        padding-top: 4px;
    }
    
    .feature-top .story-header h1 a, 
    .feature-left .story-header h1 a, 
    .feature-right .story-header h1 a{
        background: none;
    }
        
    .feature-top a img, .feature-left a img, .feature-right a img{
        display: none;
    }
    
    h1, h1 a{
        border-radius: 8px 0 0 0;
        padding:  0 0 0 8px;
        background-color: #005050;
        color: white !important;
        font-size: 15px;
        font-weight: bold;
        line-height: 100% !important;
        vertical-align: 3px;
    }
    
    header h4 {
        padding-left: 16px;
        background-color: #E0E0E0;
        color: black !important;
    }
    
    .layout-article a noscript img{
        display: none;
    }
    
    .elips , .elips i, .elips p{
        font-family: tahoma !important;
        font-size: 13px !important;
        line-height: 1.1rem;
    }
    
    .elips i{
        margin-bottom: 10px !important;
    }
  
    .elips a, .elips i a{
        color: #006666;
        text-decoration: underline;
    }
    
    .story-with-module{
        margin-right: 250px;
    }

    header h3 {
        margin-left: 0 !important;
        border-radius: 8px 0 0 0;
        padding: 4px 15px;
        width: 100%;
        background-color: #005050;
        color: white !important;
        font-size: 15px !important;
        font-weight: bold;
    }

    .comment-body{
        font-family: tahoma !important;
        font-size: 13px !important;
    }
    
    .msg-loading{
        display: none !important;
    }
    
    .com-toggle{
        display: none;
    }
}
