css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix('http://www.tumblr.com/'), url-prefix('https://www.tumblr.com/')   


{


body {
/*background image for all pages*/
  background: url("http://www.m-i-u.de/images-i85165beokwc.jpg")no-repeat fixed !important;
  background-color: rgba(255,255,255,0.00) !important;
  background-position: 0% 0% !important;
  background-size: 100% 100%  !important;

}

 /* Header */
.l-header-container--refresh {background-color: rgba(255,255,255,0.30)  !important; }	
 .identity-refresh .inbox_notice_container, .identity-refresh .new_post_notice_container { border: none !important; }

.identity-refresh .selected .tab_notice, .identity-refresh .inbox_notice_container, .identity-refresh .new_post_notice_container { background: #D95E40 !important; 


  /* Right Column */

color:rgba(255,255,255,1.00)!important;  
}
    .controls_section a { color: rgba(255,255,255,0.6) !important; }
    .controls_section a .count canvas { color: #fff !important; }
    .right_column:after { visibility: hidden !important; }
    #popover_blogs a { color: rgba(0,0,0,0.7) !important; }
    .radar .radar_footer .radar_avatar:before { background: none !important; }
    .identity-refresh .controls_section li:hover { background: rgba(255,255,255,0.80) !important; 
}


#right_column {

background:none!important;

}

#left_column {

background:none!important;

}

/****LOGIN SCREEN****/

LABEL {
  color: rgba(0,0,0, 1.0) !important; 
}


#right_column .controls_section li a {
  color: rgba(0,0,0,1.00) !important; 
}



#right_column:after { 
display: none !important; 
}

#right_column a {
color:rgba(0,0,0,1)!important;  
}

#right_column .controls_section li a {
  color: rgba(0,0,0,1) !important; /*(0,0,0, 0.75) (255,255,255,0.75)*/
}

#right_column .controls_section li a .count {

  color: #000 !important; 
}


.login.sign_up,
A[href="/forgot_password"],
#login_form SPAN,
.footer_links {
  display: none !important; /*Do not display 'Sign up' link, 'Forgot Password?' link, 'Log in' button text or footer links*/
}

/***MAIN DASHBOARD SCREEN***/

.post_question_nipple, .arrow{
    background:transparent!important;}



/*tumblr logo*/
#logo {
  width: 196px !important;
  height: 44px !important;
  padding-left: 0px !important; /*replace with your tumblr logo image width*/
  padding-top: 0px !important; /*replace with your tumblr logo image height*/

}

/*new post icons bar*/
img[src*="http://assets.tumblr.com/images/new_post_icons_no_labels.png"]{
opacity:0.00 !important;  
}

/*new post icons bar background color*/
.post.is_mine.with_avatar.new_post {
  background: rgba(255,255,255.1.0) !important; /*the first three numbers are for the color - the last number (0.50) is for how transparent, from 0.00 to 1.00)*/
}

/*text color for icons bar icons*/
.new_post_label{
  color: rgba(0,0,0, 1.00) !important; /*the first three numbers are for the color (black) - the last number (1.0) is for how transparent, from (0.00 to 1.00)*/
}
/*main background color*/
.l-content {
  background: rgba(255,255,255,0.35) !important; /*the first three numbers are for the color - the last number (0.50) is for how transparent, from (0.00 to 1.00)*/
}
/*.selection.nipple - dashboard, user, inbox - "white pages"*/
/*.selection_nipple.white - create new blog, help, preferences*/
.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) {
background: none !important;
width: 0px !important;
height: 0px !important;
margin-top: 0px !important;
border-bottom: solid 11px rgba(255,255,255,1.00) !important;/*share the same color/opacity as the "white pages"*/
border-right: 11px solid transparent !important;
border-left: 11px solid transparent !important;
}
.selection_nipple:not([class="selection_nipple white"]) {
border-bottom: solid 11px rgba(255,255,255,0.90)  !important;/*share the same color/opacity as the content pane*/
}

/***ADD A TEXT POST SCREEN***/

/*text post message body background*/
.mceContentBody {
  background: url("http://s10.postimg.org/5hd4tnzmh/chrisflowers3.jpg") !important; /*replace with your image url*/
  background-size: 100% !important; /*change to less than 100% to repeat image pattern - if the number is too small, your background will disappear*/
  color: rgba(0,0,0, 1.00) !important; /*message font color - the first three numbers are for the color - the last number (1.0) is for how transparent, from (0.00 to 1.00)*/
}

/*top content bar - rounded bar with design similar to buttons*/
.l-content_top {
  width: 0px !important;
  height: 0px !important;
/*uncomment the lines below if you wish to replace the top content image with your own*/
/*  background: url(http://assets.tumblr.com/images/content_top_edit_form.png) !important;  /*replace with your top content bar image url*/
/*  width: 900px !important; /*replace with your top content bar image width*/
/*  height: 25px !important; /*replace with your top content bar image height*/
}

/*bottom content bar - rounded bar with design similar to buttons*/
.l-content_bottom {
  width: 0px !important;
  height: 0px !important;
/*uncomment the lines below if you wish to replace the bottom content image with your own*/
/*  background: url(http://assets.tumblr.com/images/content_top_edit_form.png) !important;  /*replace with your bottom bar content image url*/
/*  width: 900px !important; /*replace with your bottom content bar image width*/
/*  height: 25px !important; /*replace with your bottom content bar image height*/
}

/*large rectangular area containing text message, controls and form fields = content container frame*/
.l-content {
  padding-top: 25px !important; /*pads the content container frame to make up for the missing top content bar */
  padding-bottom: 25px !important; /*pads the content container frame to make up for the missing bottom content bar*/
   -moz-border-radius: 20px !important; /*rounds the corners of the content container frame for appearance*/
}

/*some text in the right column that was too hard to read*/
LABEL {
  color: rgba(0,0,0,1.0) !important; /*text font color - the first three numbers are for the color (black) - the last number (1.0) is for how transparent, from (0.00 to 1.00)*/
}



/****OPTIONAL****/
/*If you want to turn these back on, delete the appropriate section*/
.radar {
  display: none !important; /*removes the radar from the main dashboard*/
}
#footer {
  display: none !important; /*removes the footer links from all pages*/
}
.selected .following,
.selected .followers,
.selected .settings,
.selected .posts,
.selected .messages,
.selected .drafts,
.selected .queue,
.selected .likes  {
  color: rgba(0,0,0,1.00) !important; /*selected font color - the first three numbers are for the color - the last number (0.50) is for how transparent, from (0.00 to 1.00)*/
  background: rgba(255,255,255, 0.70) !important; /*changes the Following, Settings, Posts, Drafts Queue bars (when selected) to match the theme colors*/
}


#new_post .arrow {
  opacity: 1.00 !important;
  margin-left: -1px !important; /*matches the arrow on the left of the icons bars with background*/
}


.no_posts_found, h1.dashboard_header {
















color: #333333;














}

 }
