css:
@-moz-document domain("sachalayatan.com"), domain("prothom-alo.com"), domain("kalerkantho.com"), domain("jugantor.com"), domain("samakal.net"), domain("priyo.com") {
body {
  font-family: "Noto Sans Bengali";
}

textarea {
  display: block;
  border: 0 solid #ccc;
  line-height: 130%;
  font-family: "Noto Sans Bengali";
  font-size: 15px;
  padding: 8px;
}

p,input,ul,li,select,a {
  font-family: "Noto Sans Bengali";
  font-size: 16px;
}
/* sachalayatan */
#node-preview {
  width: 95%;
  float: left;
  margin: 0 10px 0 10px;
  overflow: visible;
}

.language-bn {
  font-family: "Noto Sans Bengali", BanglaSangamMN,BNG,SolaimanLipi,Mukti,Lucida Grande,Tahoma,Arial,Helvetica,sans-serif;
  font-size: 16px;
}

#container {
  width: 1025px;
}

#header {
  width: 100%;
}

#content {
  width: 78%;
  float: left;
  margin: 0;
  overflow: visible;
}

#sidebar-left {
  margin-left: 0;
  float: left;
  width: 1px;
  visibility: hidden;
}

#sidebar-right {
  float: right;
  width: 17%;
  margin: 8px;
  font-size: 12px;
}

.block ul {
  font-size: 10px;
}

.plugin, .pluginSkinLight, ._li {
  visibility: hidden;
  height: 0px;
  width: 0px;
}

#block-fb_social-facebook_recommendations {
  visibility: hidden;
  height: 0px;
  width: 0px;
}

/* prothom-alo */

.p_d, .p_d_d {
  width: 100%;
}

.content img, .content table, .content span.jw_media_holder {
  max-width: 100% !important;
  display: block;
}

.common_google_ads {
  visibility: hidden;
  height: 0px;
  width: 0px;
}

.sub_menu ul li a {
  font-size: 14px;
}

.main_menu a {
  font-size: 14px;
}

div.gads-300x250 {
  width: 0px;
  height: 0px;
}
}
