@-moz-document url(chrome://browser/content/browser.xul) {

#main-window {
    background-image: none !important;
    background-color: transparent !important;
    border-style: none !important;
}

#tab-view-deck {
    background-color: -moz-dialog !important;
    margin-top: -1px !important;
    background-repeat: repeat-x;
    background-position: 0px -1px;
    background-image: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAIAAAAYCAQAAAB68uRSAAAAAXNSR0IArs4c6QAAAAJiS0dEAP+Hj8y/AAAACXBIWXMAAAsTAAALEwEAmpwYAAAAB3RJTUUH3AgdBTsUG4hL2QAAACZJREFUCNdjYIABxv/5DAwsDL9xEb+wcP9gYeE34A8DA+N/FgYGAPJYFl6Nmu7cAAAAAElFTkSuQmCC");
}

#main-window[sizemode='normal']>#tab-view-deck {
    border-top-left-radius: 6px !important;
    border-top-right-radius: 6px !important;
    border-style: solid;
    border-width: 1px;
    border-color: rgb(150,150,150);
}

#tab-view-deck:-moz-window-inactive {
    box-shadow: none !important;
    background-image: none !important;
}

#close-button {
    list-style-image: url("data:image/svg+xml;base64,") !important;
    -moz-appearance: none !important;
    padding: 3px 4px 3px 3px !important;
}

#close-button:-moz-window-inactive {
    list-style-image: url("data:image/svg+xml;base64,") !important;
}

#close-button:hover {
    list-style-image: url("data:image/svg+xml;base64,") !important;
}

#close-button:active {
    list-style-image: url("data:image/svg+xml;base64,") !important;
}

#restore-button {
    list-style-image: url("data:image/svg+xml;base64,") !important;
    -moz-appearance: none !important;
    padding: 3px 4px 3px 3px !important;
}

#restore-button:-moz-window-inactive {
    list-style-image: url("data:image/svg+xml;base64,") !important;
}

#restore-button:hover {
    list-style-image: url("data:image/svg+xml;base64,") !important;
}

#restore-button:active {
    list-style-image: url("data:image/svg+xml;base64,") !important;
}

#minimize-button {
    list-style-image: url("data:image/svg+xml;base64,") !important;
    -moz-appearance: none !important;
    padding: 3px 4px 3px 3px !important;
}

#minimize-button:-moz-window-inactive {
    list-style-image: url("data:image/svg+xml;base64,") !important;
}

#minimize-button:hover {
    list-style-image: url("data:image/svg+xml;base64,") !important;
}

#minimize-button:active {
    list-style-image: url("data:image/svg+xml;base64,") !important;
}

#hctp-controls-top-fixed {
    padding-right: 6px;
}

}
