css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("christianchat.com") {

/*Site Background*/

#vbulletin_html {
    background:url('http://i1294.photobucket.com/albums/b611/forthangel777/BeigeWatercolor_zpsbf60386f.jpg') !important;
    background-attachment:fixed!important;
    background-size:100%!important;  
}
    
html {
    background: url('http://i1294.photobucket.com/albums/b611/forthangel777/BeigeWatercolor_zpsbf60386f.jpg')!important;
    background-attachment:fixed!important;
    background-size:100%!important;  
}
    
html.CSS1Compat {
    background: #fff!important;
    background-attachment:fixed;
    background-size:100%;  
}

/*Header*/

#header {
    background:url('http://i1294.photobucket.com/albums/b611/forthangel777/BeigeMarble_zpsf5c6a8da.jpg');
}
    
.toolsmenu {
    background: rgba(0,0,0,0)!important;
    width: 100%;
    display: block;
    border: 0px solid #CECECE!important;
    float: left;
    clear: both;
    margin: 0.5em 0px;
}

.navbar {
    background: url('http://i1294.photobucket.com/albums/b611/forthangel777/BrownStoneNav_zps405b3e42.jpg')!important;
}
    
.navtabs li a.navtab {
    border-top: 0px solid #CEDFEB!important;
    border-left: 0px solid #CEDFEB!important;
    display: block;
    background: url('http://christianchat.com/images/styles/christianchat/gradients/gradient20120723.png') repeat-x scroll 0% 0% #369;
    min-width: 60px;
    width: auto !important;
    text-align: center;
    color: #FFF;
    font: bold 12px/26px Arial,Calibri,Verdana,Geneva,sans-serif;
    text-decoration: none;
}

.navlinks {
    background: rgba(0,0,0,0)!important;
    border-top: 0px solid #E7E7E7!important;
}
    
.navtabs ul {
    border-top: 0px solid #5A7F97;
}

.navtabs li.selected a.navtab {
    color: #000;
    background: url('http://christianchat.com/images/gradients/selected-tab-gradient-with-top-alpha.png') repeat-x scroll 0% 0%!important;
    position: relative;
    top: -4px;
    padding-top: 4px;
    z-index: 10;
}
    
.globalsearch .textboxcontainer {
    height: 26px;
    border-left: 0px solid #5A7F97!important;
    display: inline-block;
    float: left;
    text-align: center;
}
    
.globalsearch .textboxcontainer span {
    border-top: 0px solid #CEDFEB!important;
    border-left: 0px solid #CEDFEB !important;
    display: inline-block;
    height: 22px;
    padding-left: 2px;
    padding-right: 2px;
    padding-top: 3px;
    margin-bottom: 1px;
    width: 147px;
}
    
.globalsearch .searchbutton {
    border-top: 0px none;
    padding: 0px;
    width: 22px;
    margin-top: -3px;
    margin-left: -2px;
    background:url('http://christianchat.com/images/styles/christianchat/gradients/gradient20120723.png');
}
    
.globalsearch .buttoncontainer {
    height: 26px;
    border-left: 0px;
    display: inline-block;
    border-right: 0px;
    width: 25px;
    overflow: hidden;
    float: right;
}

/*Main Body & Tables*/

.body_wrapper {
    background: rgba(0,0,0,0)!important;
}

body a {
    color: #52483F; /*forum text color*/
    text-decoration: none;
}

.forumhead.foruminfo {
    box-shadow: -2px 2px 2px #423A32;
}
    
.forumhead {
    background:url('http://i1294.photobucket.com/albums/b611/forthangel777/BrownMarble_zps1cbd7523.jpg')!important;
    color: #FFF;
}

.forumrow.table {
    background:url('http://i1294.photobucket.com/albums/b611/forthangel777/BeigeMarble_zpsf5c6a8da.jpg');
    box-shadow: -2px 2px 2px #423A32!important;
}
    
.forum_info .blockhead {
    font-size: 12px;
    font-weight: bold;
    color: #FFF;
    background:url('http://i1294.photobucket.com/albums/b611/forthangel777/BrownMarble_zps1cbd7523.jpg'); 
    border: 1px solid #606060;
    margin-top: 5px;
    box-shadow: -2px 2px 2px #423A32;
}
    
.forum_info .blockbody {
    background: url('http://i1294.photobucket.com/albums/b611/forthangel777/BeigeMarble_zpsf5c6a8da.jpg');
    box-shadow: -2px 2px 2px #423A32;
}
    
#forum_icon_legend h5 {
    font: bold 12px Tahoma,Calibri,Verdana,Geneva,sans-serif;
    border: 0px solid #5A7F97!important;
    background: transparent!important;
    color: #FFF;
    padding: 5px 10px;
}
    
.wgo_block .blockhead {
    font-size: 12px;
    font-weight: bold;
    color: #FFF;
    background: url('http://i1294.photobucket.com/albums/b611/forthangel777/BrownMarble_zps1cbd7523.jpg') repeat-x scroll 0% 0% #707070;
    border: 1px solid #606060;
}

/*Sidebars*/

#sidebar_container > ul {
    background: url('http://i1294.photobucket.com/albums/b611/forthangel777/PinkishMarble_zps44fdd3c0.jpg') repeat scroll left top #F1F1F1!important;
    box-shadow: -2px 2px 2px #423A32!important;
}

#sidebar_container .blockrow {
    background: url('http://i1294.photobucket.com/albums/b611/forthangel777/BeigeMarble_zpsf5c6a8da.jpg') repeat scroll 0% 0% #FFF !important;
}
    
#sidebar_container .block {
margin-bottom: 4px;
padding: 6px;
background: transparent!important;
-webkit-border-top-left-radius: 5px;
-webkit-border-top-right-radius: 5px;
border-top-left-radius: 5px;
border-top-right-radius: 5px;
}

#sidebar_container .blocksubhead {
    color: #52483F;
}
    
#blog_user_sidebar, #blog_sidebar_generic {
    background: url('http://i1294.photobucket.com/albums/b611/forthangel777/PinkishMarble_zps44fdd3c0.jpg') !important;
    border: 1px solid #C1C1C1 !important;;
    border-radius: 5px !important;;
    box-shadow: 0px 0px 0px #C8C8C8 !important;
    margin-left:4px !important;
}

/*Threadbits*/    
    
.forumbit_nopost .forumbit_nopost .forumrow, .forumbit_post .forumrow {
    color: #4E4E4E;
    background: url('http://i1294.photobucket.com/albums/b611/forthangel777/BeigeMarble_zpsf5c6a8da.jpg') repeat-x scroll left bottom #FFF !important;
    box-shadow: -2px 2px 2px #423A32;
}
    
.threadbit .sticky, .threadbit .discussionrow {
background: url('http://i1294.photobucket.com/albums/b611/forthangel777/WhiteMarble_zps15674a7e.jpg') repeat-x scroll left bottom #FFF !important;
}
    
.threadbit .nonsticky, .threadbit .discussionrow {
background: url('http://i1294.photobucket.com/albums/b611/forthangel777/WhiteMarble_zps15674a7e.jpg') repeat-x scroll left bottom #FFF !important;
}
    
.forumbits .forumhead h2 {
    box-shadow: -2px 2px 2px #423A32;
}

.threadlisthead {
    border: 1px solid #5A7F97;
    background: url('http://i1294.photobucket.com/albums/b611/forthangel777/BrownMarble_zps1cbd7523.jpg') repeat-x scroll 0% 0% #369!important;
    box-shadow: -2px 2px 2px #423A32;
}

.threadbit {
    box-shadow: -2px 2px 2px #423A32;
}

.threadbit .threadinfo {
    background: url('http://i1294.photobucket.com/albums/b611/forthangel777/WhiteMarble_zps15674a7e.jpg');
    width: 62%;
    min-width: 350px;
}

.threadbit .alt {
    background: url('http://i1294.photobucket.com/albums/b611/forthangel777/WhiteMarble_zps15674a7e.jpg')!important;
}

.threadbit .threadlastpost {
    background: url('http://i1294.photobucket.com/albums/b611/forthangel777/WhiteMarble_zps15674a7e.jpg')!important;
}

/*Footer*/

.footer {
    box-shadow: -2px 2px 2px #423A32!important;
    background: url('http://i1294.photobucket.com/albums/b611/forthangel777/BrownMarble_zps1cbd7523.jpg') repeat-x scroll 0% 0% #369!important;
    border: 1px solid #425769;
}

/*Threads*/

.postbitlegacy .postbody, .eventbit .eventdetails .eventbody {
    background: url('http://i1294.photobucket.com/albums/b611/forthangel777/BeigeMarble_zpsf5c6a8da.jpg')!important;
}

.postbitlegacy .postdetails, .eventbit .eventdetails {
    background: url('http://i1294.photobucket.com/albums/b611/forthangel777/PinkishMarble_zps44fdd3c0.jpg')!important;
}

.postbitlegacy .userinfo {
    background: transparent!important;
}

.postbit .posthead, .postbitlegacy .posthead, .eventbit .eventhead {
    background: url('http://i1294.photobucket.com/albums/b611/forthangel777/BrownMarble_zps1cbd7523.jpg') repeat-x scroll 0% 0% #369;
    border: 1px solid #5A7F97;
}
    
.postbitlegacy .postfoot {
    background:url('http://i1294.photobucket.com/albums/b611/forthangel777/BrownStoneNav_zps405b3e42.jpg')!important;
}
    
/*Blocks*/
    
#usercp_nav .blockbody {
background: url('http://i1294.photobucket.com/albums/b611/forthangel777/BeigeMarble_zpsf5c6a8da.jpg')!important;
}

#usercp_nav .blockrow {
background: url('http://i1294.photobucket.com/albums/b611/forthangel777/BeigeMarble_zpsf5c6a8da.jpg')!important;
padding: 1px 10px;
}
    
#usercp_nav .blockrow .active {
background-color: #F5E6A2!important;
}
    
#usercp_nav .blockrow * a:hover {
background-color: #A3843B!important;
color: #fff!important;
text-decoration: none;
}
    
.floatcontainer, .formcontrols .blockrow, dl.stats {
display: block;
border: 0px!important;
}
    
.pmlist .blockrow {
    border-top: 1px solid #CEDFEB;
    background: transparent !important;
    clear: both;
    position: relative;
}

#content .blockhead {
    box-shadow: -2px 2px 2px #423A32;
    font-weight: bold;
}
    
#content .blockrow > li, #content .blockrow > ol, #content .blockrow > div {
    box-shadow: -2px 2px 2px #423A32;
}
    
.blockhead {
    background: url('http://i1294.photobucket.com/albums/b611/forthangel777/BrownMarble_zps1cbd7523.jpg') repeat-x scroll 0% 0% #369!important;
}

.blockbody {
    background:url('http://i1294.photobucket.com/albums/b611/forthangel777/BeigeMarble_zpsf5c6a8da.jpg')!important;
    padding-left: 0px;
    padding-right: 0px;
}
    
.blocksubhead {
    background: url('http://i1294.photobucket.com/albums/b611/forthangel777/PinkishMarble_zps44fdd3c0.jpg')!important;
}
    
.blockfoot {
    border: 1px solid #5A7F97!important;
    background: url('http://i1294.photobucket.com/albums/b611/forthangel777/BrownMarble_zps1cbd7523.jpg')!important;
    border-bottom-right-radius: 5px;
    border-bottom-left-radius: 5px;
}
    
.wgo_block .section {
    background: url('http://i1294.photobucket.com/albums/b611/forthangel777/WhiteMarble_zps15674a7e.jpg');
    box-shadow: -2px 2px 2px #423A32;
}
    
/*Activity Stream*/
    
#activitylist {
    background: url('http://i1294.photobucket.com/albums/b611/forthangel777/BrownMarble_zps1cbd7523.jpg');
}
    
li.activitybit {
    background:url('http://i1294.photobucket.com/albums/b611/forthangel777/BeigeMarble_zpsf5c6a8da.jpg')!important;
}
    
#availsubs .blockfoot .note {
    font-size: 11px;
    text-align: center;
    color: white;
}

/*Today's Posts*/

h2.searchlisthead {
    background: url('http://i1294.photobucket.com/albums/b611/forthangel777/BrownMarble_zps1cbd7523.jpg') repeat-x scroll 0% 0% #369;
    box-shadow: -2px 2px 2px #423A32;
}

.threadbit .nonsticky, .threadbit .discussionrow {
    background: url('http://i1294.photobucket.com/albums/b611/forthangel777/WhiteMarble_zps15674a7e.jpg') repeat-x scroll left bottom #FFF;
}

/*Site Leaders*/
.floatcontainer, .formcontrols .blockrow, dl.stats {
    display: block;
    background-color: transparent !important;
}

/*Groups*/

#groupsearch {
    float: right;
    border: 2px solid #C1C1C1;
    border-radius: 5px;
    padding: 0px 10px;
    background-color: #fff;
}

/*Albums*/

#usercss .blockhead {
    background-color: #fff;
    background-image: url('http://i1294.photobucket.com/albums/b611/forthangel777/BrownMarble_zps1cbd7523.jpg');
    color: #fff;
}
    
#usercss .block, #usercss .popupmenu {
    border-color: #000;
    background-image: none;
    background-color: rgba(0,0,0,0);
}

#usercss {
    background:url('http://i1294.photobucket.com/albums/b611/forthangel777/PinkishMarble_zps44fdd3c0.jpg');
    border-color: #000;
    border-style: hidden;
    background-color: #000;
}

/*Blogs*/
    
#usercss.blog {
    background:url('http://i1294.photobucket.com/albums/b611/forthangel777/BeigeMarble_zpsf5c6a8da.jpg');
    border-color: #000;
    border-radius: 25px !important;
}
    
.blogcontent {
    color:#52483F;
}
    
.blog #content_container {
    background:url('http://i1294.photobucket.com/albums/b611/forthangel777/BeigeMarble_zpsf5c6a8da.jpg');
}
    
#blogentries {
    margin-bottom: 1em;
    background:url('http://i1294.photobucket.com/albums/b611/forthangel777/BeigeMarble_zpsf5c6a8da.jpg')!important;
}

/*Tables*/

table {
    font-size: inherit;
    background: url('http://i1294.photobucket.com/albums/b611/forthangel777/BeigeMarble_zpsf5c6a8da.jpg');
    border-left:0px;
}

dl.tabslight {
    border-bottom: 0px solid #6B91AB;
    padding-left: 20px;
    margin-bottom: 1em;
    margin-top: 15px;
}

ul.userlist_showavatars li {
    overflow: hidden;
    position: relative;
    width: 215px;
    height: 80px;
    padding: 4px;
    border: 2px solid #808080;
}}
