css:
@-moz-document url-prefix("https://www.toggl.com/") {
body {
  background: #333 !important;
}

/* Timer */
.description-container input, .timer .tags-container, .timer .billable-container, .timer .project-container,
.timer .js-project, .date-time-container .input-duration {
  background: #444 !important;
}

.timer input, .timer a {
  color: #ddd !important;
}

.button.green {
  background: green !important;
}

/* Project */
.timer-sticky .row {
  background: #444 !important;      
}

/* Time Entries */
.time-entries-list>li, .entry-time, .time-entries-list>li .project-container .btn-add-input,
.time-entries-list>li.edit-mode .popdown-input {
  color: white !important;
}
          
.time-entries-list>li.edit-mode .popdown-input {
  background: #555 !important;              
}
      
.time-entries-list>li:nth-child(even), .time-entries-list>li:nth-child(even) .tags-list {
  background: #333 !important;
}

.time-entries-list>li:nth-child(odd), .time-entries-list>li:nth-child(odd) .tags-list {
  background: #555 !important;       
}

.timer-entries-group-header .date-container span {
  color: white !important;
}

.duration {
  color: white !important;  
}

.duration strong {
  color: #a6e22e !important;   
}
  
.tag span {
  background: #79200b !important;
  color: white !important;
}

.time-entries-list>li i {
  color: white !important;  
}

/* Time Entry Dropdown */
.button.dropdown .show-dropdown {
  background: #555 !important;  
}

.button.dropdown>ul li a {
  color: white !important;
}

.button.dropdown>ul li:hover {
  color: white !important;
  background: #333 !important;
}

/* Time Entry Date Picker */
.popdown-datetime .content {
  background: #333 !important;
}

.popdown-datetime, .popdown-datetime input {
  color: white !important;
}

.popdown-datetime input {
  background: #555 !important;
}

/* Tag Dropdown */
.popdown .popdown-btm {
  background: #555 !important;
}

.popdown-container input, .popdown-list {
  background: #333 !important;
}

.popdown-list a, .popdown-input, .popdown-container .popdown-subheader{
  color: white !important;
}

.popdown .popdown-list ul li>a:hover,.popdown .popdown-list ul li>a>label:hover,.popdown .popdown-list ul li>label:hover
{
  background: #555 !important;
}

/* Bottom Div */
.timer-sticky .wrapper{
  color: white !important;
  background: #555 !important;
  -webkit-box-shadow:0 0 12px rgba(255,255,255,.4) !important;
  -moz-box-shadow:0 0 12px rgba(255,255,255,.4) !important;
  box-shadow:0 0 12px rgba(255,255,255,.4) !important;
}

.timer-sticky .wrapper div {
  color: white !important;
}

/* Settings */
.button.dropdown>ul li, .button.dropdown>ul li button, .button.dropdown>ul li.active button:before, .icon-cog:before {
  color: white !important;
}

.button.dropdown:hover {
    background: black !important;
}

/* Toggl Ad */
#badge-notifications .badge {
  display: none !important; 
}

 /* Bulk Edit */
.bulkEditTEsModal{
  color: white !important;
  background: #333;
}

.bulkEditTEsModal .js-input-description, .bulkEditTEsModal .btn-add {
  background: #444 !important;
  color: white !important;
}

.bulkEditTEsModal .submit-container span, .bulkEditTEsModal .submit-container i, .bulkEditTEsModal .popdown-host a {
  color: white !important;
}

.bulkEditTEsModal .popdown-input {
  background: #555 !important;
}

/* Footer */
.footer {
  background: #333 !important;
}

.footer .inline-list a {
  color: white !important;
}
}
