css:
@-moz-document url("http://astarsearcher.bitbucket.org/") {
body {
  margin: 10px auto !important;
  width: 700px !important;
  overflow-y: scroll !important; /* prevents centering jumps */
    
  font-family: "Courier New", monospace !important;
  letter-spacing: -1px !important; /* space is precious */
  
  /* prevent accidental highlighting */
  -webkit-user-select: none !important; /* Chrome/Safari */        
  -moz-user-select: none !important; /* Firefox */
  -ms-user-select: none !important; /* IE10+ */
  cursor: default !important; /* don't even tempt me to highlight */
}

body, #main-name, #details, #research, #upgrades, #stats, #options, #poptree, #no-upgrades, #no-research {
  background-color: #000000 !important;
  color: #ffffff !important;
}

/* Frame */
#tabholder {
  width: 400px;
  background: black !important;
  border: 1px solid #ffffff !important;
  display: inline-block !important;
  float: left !important;
}
#poptree {
  position: relative !important;
  width: 150px !important;
  margin-top: 20px !important;
  display: inline-block !important;
}
#details {
  height: 100% !important;
  margin-bottom: 16px !important;
}

/* Header */
#main-name {
  border: none !important;
  text-align: center !important;
  margin: 0 auto !important;
}
#main-goals {
  margin-left: 118px !important;
}
#main-goals, #main-mods {
  padding-left: 0.8em !important;
  padding-right: 0.8em !important;
  margin-top: 8px !important;
  margin-bottom: 10px !important;
}
#main-info, #main-complete {
  text-align: center !important;
  margin-top: 0px !important;
  margin-bottom: 12px !important;
}
#main-buildings div span {
  border: none !important;
}
#main-complete {
  margin-bottom: 0 !important;
}

/* Buildings */
#main-currency {
  width: 80px !important;
}
#main-income {
  width: 60px !important;
}
#main-buildings div {
  border: 1px solid white !important;
  margin: -1px inherit !important;
}
span.building-name {
  width: 140px !important;
  text-align: center !important;
  display: inline-block !important;
}
div.buildingaffordable span.building-name:hover {
  background-color: #00e000 !important;
}
div.buildingunaffordable span.building-name:hover {
  background-color: #ff1a1a !important;
  cursor: default !important;
}
span.building-count {
  width: 40px !important;
  text-align: right !important; 
  display: inline-block !important;
}
span.building-cost{
  width: 130px !important;
  text-align: right !important;
  display: inline-block !important;
}
span.building-income {
  width: 65px !important;
  text-align: right !important;
  display: inline-block !important;
}
/* building / research / upgrade cell colors */
div.researchaffordable, div.upgradeaffordable, div.researchunaffordable, div.upgradeunaffordable {
  padding: 2px 4px !important;
}
div.buildingaffordable, div.researchaffordable, div.upgradeaffordable {
  background-color: #1a1aff !important;
}
div.buildingaffordable:hover, div.researchaffordable:hover, div.upgradeaffordable:hover {
  background-color: #00e000 !important;
  color: #202000 !important;
  cursor: pointer !important;
}
div.buildingunaffordable, div.researchunaffordable, div.upgradeunaffordable {
  background-color: #ff1a1a !important;
}
div.upgrade-cont, div.research-cont {
  border: 1px solid white !important;
  margin: -1px inherit !important;
}
/*div.buildingunaffordable:hover, div.researchunaffordable:hover {
  background-color: #cc3300 !important;
}*/
.upgrade-desc {
  width: 285px !important; /* account for padding */
}

/* Tabs menu */
#menu {
  background: #000000 !important;
  border: none !important;
  border-radius: 0 !important;
  z-index: 100 !important; /* make tabs look flush with contents */
}
#menu li {
  width: 92px !important;
  background: #000000 !important;
  color: #ffffff !important;
  border-bottom: none !important;
  -webkit-border-top-left-radius: 2px !important;
  -webkit-border-top-right-radius: 2px !important;
  -moz-border-radius-topleft: 2px !important;
  -moz-border-radius-topright: 2px !important;
  border-top-left-radius: 2px !important;
  border-top-right-radius: 2px !important;
}
.ui-tabs .ui-tabs-nav *:hover {
  background: #252522 !important;
}
#menu li.ui-state-active, #menu li.ui-state-active a, #no-upgrades {
  background: #333333 !important;
}
#menu, #menu * {
  outline: none !important;
}
.ui-tabs .ui-tabs-nav li a {
  background: #000000 !important;
  color: #ffffff !important;
  text-align: center !important;
  width: 89%;
}
#upgrades, #research, #stats, #options {
  margin-top: -1px !important;
  background: #333333 !important;
  border: 1px solid #ffffff !important;
  border-radius: 2px !important;
  z-index: 50 !important; /* hide border below tab */
}

/* Location Tree */
.jstree-default:hover {
  transition: none !important;
}
.jstree-anchor {
  background: #333333;
  border-radius: 2px !important;
  margin-bottom: -1px;
  border: 1px solid #4f4840;
  
}
.jstree-anchor.jstree-clicked {
  background: #ff5500 !important;
  color: #220022 !important;
  text-shadow: 1px 0px 0px #aa6633 !important;
  transition: none !important;
}
.jstree-hovered {
  transition: none !important;
  margin-bottom: 1px !important;
  background-color: #E500B3 !important;
  border: none !important;
}
.jstree-icon {
  margin-left: 2px !important;
}

/* Miscellaneous */
.jGrowl-notification.ui-state-highlight.ui-corner-all {
  color: #ffff00 !important;
  background-color: #000000 !important;
  font-size: 32px !important;
  font-family: "Courier New", monospace !important;
  letter-spacing: -1px !important;
  text-align: center !important;
  vertical-align: center !important;
  border: 2px solid #ffff00 !important;
  border-radius: 4px !important;
}
#chitikaAdBlock-0 {
  display: none !important;
}
.button {
  border: 2px solid #103710 !important;
  background-color: #197615 !important;
  color: #ffffff !important;
  border-radius: 12px !important;
  font-weight: normal !important;
}
.button:hover {
  -webkit-filter: brightness(1.2) drop-shadow(1px 1px 5px #333333);
}
.cmpbtn {
  margin: 8px auto 4px auto !important;
  width: 178px !important;
}
#reset-hard, #opt-export, #opt-import, #opt-patch {
  padding: 5px 0 !important;
  line-height: 1.2em !important;
  height: 200% !important;
}
div[role="tooltip"] {
  float: left !important;
  padding: 2px 10px !important;;
  background-color: #111111 !important;
  color: #ffff00 !important;
  border-radius: 2px !important;
  border: 2px solid #ffff00 !important;
}
img, i.jstree-icon {
  -webkit-filter: invert(1.0) !important;
}
}
