css:
@-moz-document domain("n0l.ch") {
body, html, footer, div.pages.top {
	background: #EEEEEE !important;
	color: #333333 !important;
  	opacity: 1 !important;
}
* {
	text-shadow: none !important;
}
a:link, a:visited, div.banner a {
	color: #FF6600 !important;
}
div.post.reply div.body a {
	color: #FF6600;
}
a:hover, a.post_no:hover, div.post.reply div.body a:hover, div.banner a:hover {
	color: #0066FF !important;
}
a.post_no {
	color: black !important;
}
ul#linklist a:hover {
	background: #DDDDDD !important;
}
span.spoiler, span.mark span.spoiler, span.spoiler span.mark, span.spoiler a {
	background: #BBBBBB !important;
	color: #BBBBBB !important;
}
span.spoiler:hover {
	color: #333333 !important;
}
span.mark span.spoiler:hover, span.spoiler:hover span.mark {
	color: white !important;
  	background: brown !important;
  	transition: all 100ms linear 0s !important;
}
span.quote, span.quote span.spoiler:hover, div.post.reply label:after {
	color: #789922 !important;
}
table.postform tr th {
	background: #DDD !important;
	border: 1px solid #CCC !important;
	padding-left: 6px !important;
	padding-right: 6px !important;
}
h1, h2 {
	color: #FF6600 !important;
	letter-spacing: 0px !important;
}
div.subtitle {
	color: #FF6600 !important;
}
.omitted {
	color: #666666 !important;
}
div.banner, div#blotter.blotter {
	color: #FF6600 !important;
	background: #DDDDDD !important;
	border: 1px solid #CCCCCC !important;
	margin: 0.5em 0px !important;
  	width: auto !important;
}
div.post.reply {
	background: #DDDDDD !important;
	border: none !important;
}
div.post.reply.highlighted {
	background: #EEDACB !important;
	border-color: #CCCCCC;
}
p.intro span.subject {
	color: #002244 !important;
}
form table tr th {
	background: #DDDDDD !important;
	border: 1px solid #CCCCCC !important;
	background: transparent !important;
}
div.pages {
	background: transparent !important;
	border-width: 0 !important;
}
div.pages a.selected {
	color: #333333 !important;
	font-weight: inherit !important;
}
hr {
  	border: none;
	border-top: 1px solid #BBBBBB !important;
	height: 0;
	clear: left;
}
span#toggle-frame, span#toggle-gallery {
	align-items: flex-start !important;
   	outline: none !important;
  	background: -webkit-gradient(linear, 0 0, 0 100%, from(white), to(buttonface)) !important;
	text-align: center !important;
	cursor: default !important;
	color: buttontext !important;
	border: 1px solid grey !important;
	border-image-source: initial !important;
	border-image-slice: initial !important;
	border-image-width: initial !important;
	border-image-outset: initial !important;
	border-image-repeat: initial !important;
  	opacity: 1 !important;
  	margin-left: 0.2em !important;
	margin-right: 0.2em !important;
	padding: 0 1em !important;
	height: 15px !important;
	white-space: pre !important;
}
span#toggle-frame:hover, span#toggle-gallery:hover {
  	border: 1px solid black !important;
}
span#toggle-frame:active, span#toggle-gallery:active {
  	background: -webkit-gradient(linear, 0 0, 0 100%, from(buttonface), to(white)) !important;
	border: 1px solid gray !important;
  	color: black !important;
}
span#toggle-frame, span#toggle-gallery, select {
  	font-family: Arial, Verdana, 'Dejavu Sans', sans-serif !important;
  	font-size: 90% !important;
}
#quick-reply input[type="url"], #quick-reply select, #quick-reply textarea {
	-moz-box-sizing: border-box !important;
	-webkit-box-sizing: border-box !important;
	box-sizing: border-box !important;
}
}
