css:
@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document domain(zip.4chan.org),
domain(orz.4chan.org),
domain(cgi.4chan.org) {



* {
font-family:"Mona", sans-serif !important;
font-size:18px !important;
-moz-appearance: none !important
}

body {

}

.reply a.linkmail[href="mailto:sage"] { background-color: #000000 !important; }
.replyhl a.linkmail[href="mailto:sage"] { background-color: #000000 !important; }

.delform {
color:#FFFFFF!important;
}

.pages {
background-color:transparent !important;
}

span.plus {
color:#FFFFFF !important;
}

.hr {
color:#FFFFFF!important;
}

.omittedposts{
color:#FF0000 !important;
}

blockquote {
color:#FFFFFF  !important;
}

table {
color:#FF0000  !important;
}

.filetitle {
color:#FFFFFF !important;
}

.commentpostername a.linkmail[href="mailto:sage"]:after
  { content: " (SAGE)"; }

textarea{
height:8em !important;
width:22em !important;
}

img.thumb,img[src*="/thumb/"] {
background:transparent !important;
border:solid 7px #FFFFFF !important;
-moz-border-radius:10px !important;
opacity:.85 !important;
z-index:3 !important;
}

body[bgcolor]{
font-size:16px !important;
background-color:#000000 !important;
background-image:url(http://img244.imageshack.us/img244/6843/122011894472532235rq1.png) !important;
background-position: right bottom !important;
background-repeat:no-repeat !important;
background-attachment:fixed !important
}
.reply, .replyhl {
background-position: right bottom !important;
background-repeat: no-repeat !important;
background-attachment: fixed !important;
opacity: .60 !important;
-moz-border-radius-bottomright:12px !important;
-moz-border-radius-topleft:12px !important;
}

DIV[class="4chan_ext_thread"] > BLOCKQUOTE {
max-width:445px !important
}
.replymode {
background-color:transparent !important;
color:#FFFFFF !important;
}


th {
background-color:#CC0000 !important;
opacity:0 !important;
-moz-border-radius:10px !important;
}

tr {
background-color:transparent !important;
color:#FFFFFF !important;
}

.unkfunc{
font-weight:bold !important;color:#000000 !important;
}




#bod1 {
background-color:transparent !important;
}


td imput, td textarea{
-moz-border-radius:10px !important;
}

[class*="postername"] {
background-color:transparent !important; 
color:#990000 !important
}

[class*="postertrip"] {
color:#660000 !important
}

div {
color:#FF0000  !important;
}



input[type="file"] {
background-color:transparent !important;
}


.postarea .postblock{
-moz-border-radius:15px /*2px 0 0 2px*/ !important;
margin-right:-1px !important;
padding:0 3px !important;
border:solid 0px #555 !important;
opacity:.85 !important;
border-right-style: dashed !important;
border:solid 2px #FFFFFF !important;
text-align:center !important;
background-color:#FF0000 !important
}



div.logo img{display:none !important;}

iframe{display:none !important;}



hr{display:none !important;}

form hr{display:block !important;}

hr{height:0px !important;border:none !important;background:#999 !important;}



td.replyhl{-moz-border-radius:37px !important;opacity:.80 !important;background-color:#FF0000 !important;color:#000000 !important;
border:solid 8px #990000 !important
}


 td.reply{-moz-border-radius:37px !important;opacity:.80 !important;background-color:#FF0000 !important;color:#000000 !important;
border:solid 2.5px #FFFFFF !important
}





a{text-decoration:none !important;color:#FFFFFF !important;}

a.quotejs:hover, a:hover{font-weight:bold !important;color:#990000 !important;}





table,td{border:none !important;}

td.rules{display:none !important;}





img + br {display:none !important;}






img[md5] {

height: auto !important;

width: auto !important;

}



.hidethread,.unhidethread,.watchthread,.expandthread,.quickreply,.hidewatchedthreads,.refreshwatchedthreads,.restorewatchedthreads,* html .hidethread,.unhidethread,.watchthread,.expandthread,.quickreply,.hidewatchedthreads,.refreshwatchedthreads,.restorewatchedthreadsimg,img[src^="chrome://4chan/skin/buttons/"],img[alt="Locked"],img[alt="closed"] {
background-image:url('http://img175.imageshack.us/img175/1497/yunoiconsbf0.png') !important;
}
img[src^="chrome://4chan/skin/buttons/"],img[alt="Locked"],img[alt="closed"] {

height:0 !important;

margin:0 2px !important;

padding-top:16px !important;

width:16px !important;

background-color:transparent !important;
}



[src$="post_expand_plus.png"] {

background-position:-48px 0 !important

}

.reply{background-color:#CC0000 !important;-moz-border-radius:8px}

[src$="arrow_up.png"] {

background-position:-64px 0 !important;
}



[src$="quote.png"] {

background-position:-80px 0 !important

}



[src$="stop.png"] {

background-position:-96px 0 !important

}



[src$="arrow_right.png"] {

background-position:-112px 0 !important

}



[src$="post_expand_minus.png"] {

background-position:-32px -16px !important

}



[src$="arrow_down.png"] {
background-position:-64px -16px !important
}



[src$="arrow_down2.png"] {
background-position:-80px -16px !important;
}


[src$="empty.png"] {

background-position:-96px -16px !important

}




#twi_table_div{border:none !important}
[id="twi_table"]{background-color:#FF0000 !important;-moz-background-clip:padding !important;border:1px solid #FFFFFF !important}
#watchedthreadsdraghandle,#twi_table_div > .postblock{background-color:#FF0000!important;color:#FFFFFF!important;border:1px solid black !important;-moz-border-radius:12px  12px 0 0 !important;padding:2px 0 !important;}
#twi_table{-moz-border-radius:0 0 12px 12px !important;border:1px solid #FFFFFF !important}
#watchedthreads{border:0px solid #FFFFFF !important}
}
