 */


/* login box */
.loginbox {
  width: 220px;
}

/* login inputs */
.lin {
	width: 100%;
}

/* http://url links */
.url {
color: #B8D56D;
}

/* (bkz: ) links */
.b {
color: #E0C563;
}

/* (ara: ) links */
.ara {
color: #D2949C;
}

/* `gorunmez bkz` */
.gb {
color: #CEFEEE;
}

/* akilli bkz* */
.ab {
color: #FEB392;
}

/* ampul abi */
.ampul {
	/* background-image: url(/img/ampul.gif); */
	background-repeat: no-repeat;
	/* width:18px; */
	height:18px;	
}
/* info section headers */
.infosec {
	background-color: #201C19;
	border: 1px outset #7A6B61;
	cursor:pointer;
}
/* right panel */
.panel {
	width:120px;
	padding:0;
	border:0;
	font-size:x-small;
}
/* arastir dropdown select */
.asl
{
	width:100%;
	margin:0;
}
/* ad box */
.spot {
  width: 120px;
  float:right;
}
.spot TD {
  font-size: 7pt;
}

/* badilist */
#badi
{
  width: 120px;
  padding: 2px;
  font-size: 7pt;
  overflow: hidden;
  border: 1px inset #3C342F;
  background-color: #000;
}
/* inline search textbox */
.aratext
{
  width:100%;
}

/* inline search marker */
.marker
{
	background-color: #000;
	color: #fff;
	border-bottom: 1px solid yellow;
}
/* pager buttons */
.pbut {
  font-size: 7pt;
  width:16px;
  height:16px;
}
/* top buttons */
.nav TD {
  padding:2px;
  height:16px;
  margin:0;
  vertical-align:middle;
}
/* panel leds */

.pbin {
	text-transform: uppercase;
	background-color: transparent;
	color: #2F3648;
	width: 30px;
	height: 45px;
	border: none;
	background-image: url("http://antik.eksisozluk.com/css/nth_binoff.gif");
	background-position: top;
	background-repeat: no-repeat;
	vertical-align: bottom;
}

.pbinhi {
	text-transform: uppercase;
	background-color: transparent;
	color: #2F3648;
	width: 30px;
	height: 45px;
	border: none;
	background-image: url("http://antik.eksisozluk.com/css/nth_binon.gif");
	background-position: top;
	background-repeat: no-repeat;
	vertical-align: bottom;
}

/* page browser text */
.pagi {
  font-size: 7pt;
  text-align: center;
}
/* page selector (select) */
.pagis {
  font-size: 6pt;
}
/* ek$isozluk logo! */
.logo {
  background-image: none;
  width:233px;
  height:45px;
  cursor:default;
}
/* highlight color */
.highlight
{
  background-color: #3F3732;
}
/* copyright line */
.copyright
{
  font-size: 7pt;
  color: #847468;
}
/* topic title */
.title {
  font: bold 12pt Verdana, sans-serif;
  background-color: transparent;
  width: 100%;
  color: #FFF;
  border: none;
}
/* buttons */
.but {
  font: 8pt Arial,sans-serif;
  text-align:center;
  white-space:nowrap;
  cursor: default;
  background-color: #6E5649;
  color: white;
  border: 2px outset #6F655F;
}
/* button mouse over */
#butOver {
  background-color: #13110F;
  border: 2px outset #27221F;
}
/* button pressed */
#butDown {
	border: 2px inset;
}
/* button link adjustments */
.but A {
  color:white;
}
.but A:Hover {
  background-color: transparent;
  color:white;
}
#butOver A {
  color:white;
}
#butOver A:Hover {
  background-color: transparent;
  color:white;
}
/* entry buttons (derived from but) */
.icon {
  font-size: 7pt;
}
.timer {
  font-size: 7pt;
  vertical-align: middle;
}
/* entry author line */
.aul {
  font-size: 8pt;
  line-height: 9pt;
  text-align: right;
}
/* panel classes */
.pi {
  border: none;;
  font-size: 9px;
  padding: 0px;
  text-align: center;
  color: #fff;
}
.pi A:Hover, a.pi  {
  background-color: transparent;
  color: #fff;
}
/* top inputtexts */
.input {
  height:12px;
  font: 7pt Verdana, sans-serif;
}
/* message textarea */
.msgtext {
  font-size: 8pt;
}
/* messages table */
.msg {
  padding: 2px;
  font-size: 8pt;
  color: #C9C4C1;
}
.msg TD {
  font-size: 8pt;
}
/* entry text */
.eol {
	line-height: 14pt;
}
/* entry id */
.ei {
  color: #857469;
  margin: 0;
  padding: 0;
  font-size: 7pt;
}
/* regular tab */
.tab {
  padding-top:2px;
  border-bottom: 2px inset #7A6B61;
  font-size: 7pt;
  white-space: nowrap;

}
.tab DIV {
  line-height: 12pt;
  white-space: nowrap;
  margin-left: 1px;
  padding-left: 4px;
  padding-right: 4px;
  padding-top: 2px;
  padding-bottom: 0;
  border: 2px outset #7A6B61;
  border-right: 1px outset #7A6B61;
  border-bottom: 0;
}
.tab A:Hover {
  background-color: transparent;
}
/* selected tab */
.tabsel {
  border: 2px outset #7A6B61;
  line-height: 11pt;
  white-space: nowrap;
  font-size: 7pt;
  padding-top: 2px;
  padding-bottom: 2px;
  padding-left: 4px;
  padding-right: 4px;
  cursor: default;
  border-bottom:0;
  white-space: nowrap;
}
/* tab contents */
.tabin {
  border: 2px outset #7A6B61;
  border-top: 0;
  padding: 4px;
  height: 245px;
  font-size: 8pt;
}

/* vertical tab */
.vtab {
  padding-right:2px;
  border-left: 2px solid #7A6B61;
  font-size: 7pt;
  white-space: nowrap;
  cursor:pointer;  
}
.vtab DIV {
  line-height: 12pt;
  white-space: nowrap;
  margin-top: 1px;
  padding-top: 4px;
  padding-bottom: 4px;
  padding-right: 2px;
  padding-left: 0;
  border: 2px outset #7A6B61;
  border-top: 1px outset #7A6B61;
  border-left: 0;

}
.vtab A:Hover {
  background-color: transparent;
}
/* vertical selected tab */
.vtabsel {
  border: 2px solid #786960;
  line-height: 11pt;
  white-space: nowrap;
  font-size: 7pt;
  padding-right: 2px;
  padding-left: 2px;
  padding-top: 4px;
  padding-bottom: 4px;
  cursor: default;
  border-left:0;
  white-space: nowrap;
}
/* vertical tab contents */
.vtabin {
  border: 1px solid #7A6B61;
  border-right: 0;
  padding: 4px;
  padding-right: 10px;
  height: 245px;
  font-size: 8pt;
    background-color: #302A26;
}


/* hayvan ara */
.adiv {
  display:block;
  position:absolute;
  top:44px;
  left:213px;
  width:15px;
  overflow:hidden;
}
.amain {
  background-color: #151211;
  border:1px outset #3C342F;
  border-left:0;
  padding:4px
}
TD.amain {
  font-size: 8pt;
}
.aup {
  border-right:none;
  height:18px
}
.amid {
  background-color:#151211;
  border:1px solid #3C342F;
  border-right:0;
  padding:4px;
  line-height:8pt;
  font-weight:bold;
  font-size:1px;
  height: 130px;
  cursor:pointer;
  color: #151211;
}
.abot {
  border-right:none;
  height:140px
}
/* default styles */
html {
  overflow-y:scroll; 
scrollbar-face-color:#5C5247;
scrollbar-highlight-color:#7A6D5F;
scrollbar-3dlight-color:#302A26;
scrollbar-darkshadow-color:#302A26;
scrollbar-shadow-color:#4D443C;
scrollbar-arrow-color:#FFFFFF;
scrollbar-track-color:#302A26;
}
body {
