  background-color: #302A26;
  color: #C9C4C1;
  	background-image: url("http://i.imgur.com/viW1SEI.jpg");
	background-repeat: no-repeat;
	background-position: top left;
	background-attachment: fixed;
}
ul {
	list-style-type: lower-roman;
  line-height: 12pt;
}
a {
  color: #D2E0F9;
  text-decoration: none;
}
a:hover {
background-color: #FFF4ED;
color: #000;
}
select, input, optgroup {
  font: 8pt Verdana, sans-serif;
  background-color: #3F3732;
  border: 1px solid #3C342F;
  color: #fff;
}
textarea {
  font: 9pt Verdana, sans-serif;
  width:98%;
  padding:0;
  margin:0;
  border:0;
  background-color: #3F3732;
  border: 1px solid #3C342F;
  color: #fff;
}
hr {
  color: #3C342F;
}
th {
	font-size: 9pt;
}
td {
	font-size: 9pt;
	line-height: 12pt;
	vertical-align: top;
}
p {
  font-size: 9pt;
  line-height: 14pt;
  vertical-align: top;
}
img, form, table {
  padding: 0;
  margin: 0;
  border: 0;
}
dd {
  margin: 0;
  padding: 0;
}
fieldset {
	margin: 0;
	padding: 2px;
	border: 1px solid #7A6B61;
}
.checkbox, .radio {
	border: none;
	background-color: transparent;
} 
li {
  margin-left: 4px;
  margin-right: 4px;
  padding:0;
}


