css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("my.deviantart.com"){ 

/* ---------- Saving... ---------- */
#lub-saving {
background: url(data:image/png;base64,%3D)!important;
background-repeat:no-repeat!important;
z-index: 999!important;
font-size: 21px!important;
color: #ffffff!important;
border-width:0px!important;
padding-left: 7px!important;
padding-top: 8px!important;
top:3px!important;
right:-3px!important;
width:99px!important;
height:42px!important;
}

/* ---------- Full journal on hover ---------- */
div.mcbox-preview-journal div.mcb-body:hover {
height:auto!important;
max-height:9000px!important;
/*width:650px!important;
max-width:9000px!important;
width:auto!important; */
}

body.bubble div.talk-post div.previewbox, body.bubble div.talk div.text {
overflow:auto!important;
}

/* ---------- Makes the text the full width of your monitor ---------- */
.talkmessage td.f {
width:auto!important
}

/* ---------- I forget :B ---------- */
table.mczone-footer div.nav del {
color:#000000!important;
}

div.pager-panes {
overflow:auto!important;
}

/* ---------- Gets rid of the mood junk when you reply ---------- */
div.talk-post table.f td.rr, div.talk-post div.pager-holder {
display:none!important;
}

/* ---------- Makes the 'Original' box width and height auto, but the width no smaller than 180 pixels and no bigger than 600 pixels.  Change the max-width to suit your needs ---------- */
div.popup2-mcbox-comment {
width:auto!important;
/*max-width:600px!important;*/
min-width:180px!important;
height:auto!important;
padding-right:15px!important;
background:#c8d0c5!important;
border:1px solid #3D6A51!important;
-moz-border-radius:8px!important;
margin-right:55px!important;
margin-top:5px!important;
}

/* ---------- Get rid of blue shit ---------- */

	/* ---------- The icons on the left ---------- */
div.pager-messages i.icon {
background-image:url(data:image/gif;base64,%3D)!important;
}

div.pager-messages div.page2 a.f {
background-image:url(data:image/gif;base64,)!important;
}

	/* ---------- The message count in the ()'s next to each message ---------- */
div.pager-messages div.page2 a.f span.c, div.mczone-filter div.f1 {
color:#000000!important;
}

	/* ---------- The border color when you select a message ---------- */
body.bubble div.bubbleview div.messages div.mcbox-sel i.c i, body.bubble div.bubbleview div.messages div.mcbox-sel div.ringview-ctrl {
border-color:#3d6a51!important;
border-width:2px!important;
}

	/* ---------- The icons to select how to view a message ---------- */
div.mczone h2.mczone-title div.mcviews-listpreviewfull a {
background-image:url(data:image/gif;base64,%3D%3D)!important;
}

	/* ---------- View deviations as thumbs or list ---------- */
div.mczone h2.mczone-title div.mcviews-listthumb a {
background-image:url(data:image/gif;base64,%3D%3D)!important;
}

	/* ---------- Oldest first or newest first ---------- */
div.mczone h2.mczone-title div.mcviews span a {
background-image:url(data:image/gif;base64,%3D%3D)!important;
}

	/* ---------- Next page link colors ---------- */
table.mczone-footer div.nav strong, table.mczone-footer div.nav a {
color:#40534A!important;
}

/* ---------- Settings Page ---------- */

ul.menu_holder a.active {
background-color:#52685c!important;
background-position:100% -300px !important;
}

ul.menu_holder a {
background: #CCD7CC url(data:image/gif;base64,%3D%3D) no-repeat scroll 50% 0!important;
}

/* ---------- Makes some link green, I forget :B ---------- */
div.mcbox-full-poll div.talkmessage div.mcb-title a {
color:#4E6257!important;
}

/* ---------- I forget what this does ---------- */
div.mczone-select-heybox-active {
display:none!important;
}

/* ---------- The shadow behind the original box ---------- */
div.popup2-shadow, img.popup2-shadow {
display:none!important;
}

/* ---------- Alignes images to the bottom in replies and comments ---------- */
div.mcbox div.talkmessage div.mcb-body img {
vertical-align:bottom!important;
}
}
