css:
@-moz-document
	url-prefix('https://www.shacknews.com/search'),
	url-prefix('http://www.shacknews.com/search'),
	url-prefix('https://www.shacknews.com/user/') ,
	url-prefix('http://www.shacknews.com/user/')  {
  
h2#search_hdr {

    font-size: 20px;
    padding: 3px 0;
    line-height: normal;
    margin-bottom: 10px; 
  }

  div.article ul.search-nav {
   
    margin: 0;
    padding: 0;
    height: 31px;    
  }

  ul.search-nav li {
   
    border: none;
    margin: 0 5px 0 0;
    padding: 0;
    
    background-image: none;
    height: auto;    
  }

  ul.search-nav li a {
   
    margin: 0;
    padding: 3px 10px;
    font-size: 14px;
    background-color: white;
    background-image: none;
    
    background-color: #f2f2f2;
    color: #444;
    height: auto;
    line-height: auto;    
  }

  ul.search-nav li.on {
   
    height: auto;    
  }

  ul.search-nav li.on a {
   
    margin: 0;
    padding: 3px 10px;
    font-size: 14px;
    background-color: white;
    background-image: none;
    
    background-color: #E2E2E2;
    height: auto;
    color: red;    
  }

  form div#chatty_search {
    
    margin-bottom: 0;
  }

  div#chatty_search_wrap {
   
    background-color: #E2E2E2;
    margin: 0;
    padding: 5px 0 0 0;
    width: auto;
  }

  div#chatty_search {
    
    background: none; 
    margin: 10px; 
    padding: 0;
    
  }

  div#chatty_search_wrap div.input-wrap {
    
    margin: 0 10px 15px 0;
  }

  /* Search Form */

  div.input-wrap label {
    
    font-family: Tahoma, sans-serif;
    font-size: 8px;
  }

  #chatty_search .search-fields input[type='text'] {
    
    padding: 3px 4px;
    margin: 0;
    font-size: 12px;
  }

  #chatty_search div.search-fields input#chatty_term {
   
    width: 462px;
  }

  #chatty_search input#chatty_search_submit {
    
    padding: 4px 10px 6px 10px;
    margin: 0 0 0 10px;
    font-size: 14px;
    border: none;
    height: auto;
    -webkit-border-radius: 0;
  }

  /* Search Filters */

  #chatty_search .search-filters {
   
    clear: left;  
  }

  /* Search Sort */

  div#chatty_sort {
    
    margin: 0;
  }

  div #chatty_sort .sort-results {
    
    font-family: Tahoma, sans-serif;
    font-size: 11px;
    margin: 5px 10px 0 10px;
  }

  div#chatty_search_wrap div.sort-filters div.input-wrap {
      
    margin-bottom: 10px; 
  }

  h2.search-num-found {
   
    margin-top: 10px;
    margin-bottom: 0;
  }

  /* Chatty Search Results */

  div.article ul.results { 
    
    margin: 0 10px 0 0;
    padding: 0;
  }


  div.article ul.results li.chatty { 
    
    margin: 5px 0;
    padding: 0 5px;
    list-style: none;
    line-height: auto;
    background-color: #f2f2f2;
  }

  div.article ul.results li.chatty span.chatty-author{ 
    
    margin: 0;
    padding: 0;
    font-size: 12px;
    line-height: auto;
  }


  div.article ul.results li.chatty span.chatty-author a.more { 
    
    margin: 0;
    padding: 0;
    font-size: 12px;
    line-height: auto;
    font-family: 'Open Sans', sans-serif;
    font-weight: bold;
    color: #324258;
  }

  div.article ul.results li.chatty span.postdate { 
    
    margin: 0;
    padding: 0;
    font-size: 12px;
    line-height: 16px;
    float: right;
    position: relative;
    top: -21px;
    color: #666;
  }

  div.article ul.results li.chatty a { 
    
    margin: 0;
    padding: 0;
    font-size: 14px;
    line-height: 16px;
    clear: right;
    color: black;
    font-weight: normal;
  }

  /* Pagination */

  div.article div.pagination {
    
    background-color: #CCC; 
    font-size: 14px;
    font-family: Open-Sans, Sans-Serif;
    margin: 5px 0 0 0 !important; 
    padding: 0 10px !important; 
    line-height: auto !important;
    text-align: center;
  }

  div.article div.pagination a {
    
    background-color: #CCC; 
    font-size: 14px;
    font-family: Open-Sans, Sans-Serif;
    font-style: normal;
    line-height: auto !important;
    margin: 0;
    padding: 0;
  }
}
