css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("wmdb.org") {
/* Fondo repetible */
html, body, #header{
background:url(http://i.imgur.com/8WbMn1M.png) !important;
}

#content {
max-width:970px !important;
background:#ffffff !important;
border:1px solid #28CEFF !important;
padding:0 10px 10px 10px !important;
min-height:600px !important;
}

#announcement, #header {
margin:0 !important;
}

#announcement {
margin:0 auto 7px auto !important;
width:100% !important;
max-width:946px !important;
}

#header {
padding-bottom:11px !important;
margin-top:-1px !important;
margin-left:-11px !important;
margin-right:-11px !important;
border-bottom:1px solid #28CEFF !important;
background-position:-1px 0px !important;
}

#login {
float:none !important;
margin-top:6px !important;
margin-left:5px !important;
margin-bottom:6px !important;
}

#content {
margin-top:32px !important;
}

#header a {
letter-spacing:0px !important;
}

#header {
color:#00296F !Important;
color:#0B3D8D !important;
background:#0B3D8D !important;
position:fixed !important;
top:0 !important;
width:100% !important;
left:0 !important;
margin:0 0 50px 0 !important;
text-align:center !important;
height:23px !important;
padding:6px 0 0 0 !important;
border-bottom:1px solid #032D8D !important;
letter-spacing:-6px;
}

#header a:not([href="/"])  {
color:#ffffff !important;
background:#0A367D !important;
padding:3px 10px !important;
border-right:1px solid #07285F !important;
}

#header a:not([href="/"]):hover  {
background:#008C00 !Important;
}

#header a[href="/maps"] {
margin-left:10px !important;
}

#header a:last-of-type {
border:none !important;
}

#header a[href="/"] {
border-right:1px solid #1366EA !Important;
display:inline-block !important;
height:28px !important;
position:relative !important;
top:-6px !important;
padding-right:5px !important;
}

#header a img {
display:inline-block !important;
position:relative !important;
bottom:-7px !Important;
}

/* Fondo cuadro contenido */
#content {
background:#0A328D !important;
color:white !important;
}

/* Color de texto de los links del contenido */
#content a {
color:cyan !important;
}

/* Titulos */
h1 {
background:#1740FF !important;
display:inline-block !important;
margin:0 !important;
margin-bottom:8px !important;
padding:5px 9px !important;
margin-left:-10px !important;
border-color:#27BCFE !important;
border-width:1px !important;
border-style:dotted !important;
border-left:hidden !important;
}

h1 + a {
clear:both !important;
}

h1 + a:before
{
content:" ";
display:block !important;
} 

/* Anuncio */
#announcement {
background:#9D001C !important;
border-color:#FF0000 !important;
border-width:1px !important;
padding:3px 6px !important;
margin-bottom:10px !important;
}

#announcement a {
color:#FFDC15 !important;
}

#shoutbox {
margin:0 auto 0 auto !important;
}

#shoutbox .description{
padding:5px !important;
background:#0B3D8D !important;
color:white !important;
}

#shoutbox a {
color:cyan !important;
}

#shoutbox .title {
background:#0298BD !important;
}

#shoutbox .title a {
text-shadow:1px 1px 2px black !important;
}

#shoutbox .pages a {
color:black !important;
}

}
