css:
@-moz-document url-prefix("http://www.google.com/calendar/"), url-prefix("https://www.google.com/calendar/") {
#calmaster {
    background-color: black;
}

#gridcontainer {
    margin-right: 17px;
}

#mainbody {
    margin-left: 30px;
}

#nav, #sidebar {
    display: none;
}

#onegoogbar {
    display: none;
}

#rhstogglecell {
    display: none;
}

#vr-nav {
    display: none ;
}

.mv-event-container {
    background: black;
    border-bottom: 1px solid black;
    border-right: 1px solid black;
    border-top: 1px solid black;
}

.st-bg {
    border-left: 1px solid black;
}

.st-c .rb-n {
    background-color: #012;
    border: gray;
    padding: 10px;
}

.st-dtitle {
    border-left: 1px solid black;
    border-top: 1px solid black;
}

.st-dtitle-next {
    border-left-color: #ddd;
}

div.mv-container {
    background-color: #000000;
}

label.ep-recl-label {
    color: #9c9c9c;
}

span.group label {
    color: #dedede;
}

td.st-bg {
    background-color: #000000;
}

td.st-dtitle {
    background-color: #000000;
    color: #e6e6e6;
}

td.st-dtitle.st-dtitle-today {
    color: #ff0000;
    font-size: 22px;
}

th.mv-dayname {
    background-color: #000000;
    color: #c7c7c7;
}

div.ca-evp11 {
    color: white;
}

div.te {
    color: gray;   
}




}
