css:
@namespace url(http://www.w3.org/1999/xhtml);
 
@-moz-document url("http://www.tumblr.com/dashboard"), url-prefix("http://www.tumblr.com/blog/"), url-prefix("http://www.tumblr.com/settings/"), url-prefix("http://www.tumblr.com/inbox"), url("https://www.tumblr.com/dashboard"), url-prefix("https://www.tumblr.com/blog/"), url-prefix("https://www.tumblr.com/settings/"), url-prefix("https://www.tumblr.com/inbox") {
*{
font-weight:normal!important;
text-decoration:none!important;
}
 
 
body {
background:LIGHTSEAGREEN url('chrome:') fixed!Important;
-webkit-background-size:cover!important;
-moz-background-size:cover!important;
-o-background-size:cover!important;
background-size:cover!important;
}
 
.post,.post_footer,.post_content,.radar_avatar:before,.radar,.radar_footer,.right_column:after{
    background: transparent!IMPORTANT;
}
 
#recommended_tumblelogs,.post_avatar:after,.nipple,.post_tags:after,.post_permalink,.selection_nipple,.radar,.radar_header,.spotlight,.post_avatar:after{
display: none!important;
}
 
.tab_notice{
    opacity: 0.8;
    background:DARKCYAN!important;
}
 
.logo *{
    background-image:none!Important;
}
 
.logo:before {
    content:'TUMBLR';
    display:block!important;
    text-transform: uppercase!important;
    color: DARKCYAN!IMPORTANT;
    FONT-SIZE:45PX!IMPORTANT;
    MARGIN-TOP:10PX!IMPORTANT;
    MARGIN-BOTTOM:10PX;
    FONT-STYLE:OBLIQUE;
    FONT-FAMILY:ARIAL!iMPORTANT;
    opacity:0.9;
    z-index:-10000000000;
    }
 
.post_wrapper{
border: 2px outset MEDIUMAQUAMARINE!important;
margin-top:-15px!important;
-webkit-border-radius: 5px!important;
-moz-border-radius: 5px!important;
-o-border-radius: 5px!important;
border-radius: 5px!important;
margin-bottom:30px!Important;       /* leaving space for tags */
}
 
.post *,#footer_container *{
color: AZURE!Important;
}
 
#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
 background: MEDIUMTURQUOISE!important;
}
 
.search_form_row {
 background: ROSYBROWN!important;
}
 
.post_footer{
position: absolute;
margin-top:-50px!Important;
}
 
.post_header{
position:absolute;
margin-top:15px!Important;
}
 
.post_content,.post_answer{
padding-bottom: 30px!Important;
}
 
.post_tags {
opacity:0!important;
z-index:1000000000000000000!important;
position:absolute!Important;
border: 2px outset MEDIUMAQUAMARINE!important;
padding: 2px 4px!Important;
-webkit-border-radius: 5px!important;
-moz-border-radius: 5px!important;
-o-border-radius: 5px!important;
border-radius: 5px!important;
    }
   
.post:hover .post_tags {
opacity:0.5!Important;
margin-top:10px!Important;
}
 
.pill,.follow{
background: TAN!important;
color: MISTYROSE!important;
border: 0px!important;
}
 
blockquote {
border-left: 3px solid LIGHTGRAY!important;
}
 
blockquote blockquote{
border-left: 3px solid SILVER!important;
}
 
blockquote blockquote blockquote blockquote blockquote blockquote {
border-left: 3px solid DARKGRAY!important;
}
 
/*RIGHT COLUMN*/
 
#search_field {
position:fixed!important;
right: 18.5%!Important;
margin-top: 75px;
}
 
#user_tools {
position: fixed!Important;
right: 17.5%!important;
margin-top: 10px;
}
 
.right_column {
padding: 5px!Important;
background: ROSYBROWN!important;
-webkit-border-radius: 3px!important;
-moz-border-radius: 5px!important;
-o-border-radius: 6px!important;
border-radius: 5px!important;
margin-top:80px!important;
position:fixed!important;
right: 18%!Important;
}
 
.right_column *{
color: LIGHTGRAY!IMPORTANT;
}
 
 
/*ASK POST*/
.note_item{
background: DARKCYAN!important;
border: 0px !important;
}
 
.txt {
color: DARKGRAY!important;
}
 
.name {
text-transform:capitalize!important;
}
   
::selection {
color: ORANGE!important;
background: PEACHPUFF!Important;
}
 
::-moz-selection {
color: ORANGE!important;
background: PEACHPUFF!Important;
}
 
::-o-selection {
color: ORANGE!important;
background: PEACHPUFF!Important;}
 
.link_button{
border: 3px double MISTYROSE!important;
color: ORANGE!important;
background: PEACHPUFF!Important;
opacity:0.8;
}
 
/*NEW POST OVERLAY*/
 
.plexi.show.color {
background:rgba(0,0,255,0.89)!important;
}
 
/*LOOKS BAD IF YOU CHANGE THE NEXT BACKGROUND COLOR BC ITS THE POST EDITOR*/
.post_type,tbody,#tinymce,.tag_editor .tags,.borderless,#source_editor,.mceStatusbar{
background: #FFF!important;
}
 
.popover_inner *,.optional,.drop_target,.audio_tabs *,.post_media li{
color: BLACK!important;
}
 
.cog {
background-color: HONEYDEW!Important;
-webkit-border-radius: 3px;
-moz-border-radius: 3px;
-o-border-radius: 3px;
border-radius: 3px;
}
 
/*BUTTONS*/
.blue
{
    background-color: DARKCYAN!important;
    color: HONEYDEW!important;
}
 
.flat {
    background-color: BROWN!important;
    border: 0px!important;
    color: WHEAT!important;
}
 
/*BLOG CONTROLS*/
.iconic {
 margin-right: -5px!Important;
}
 
/*RIGHT COLUMN CONTROLS*/
.right_column{
max-height: 50%!important;
overflow-y: auto!important;
overflow-x: hidden!important;
}
 
.plexi .tags *{
    color: darkcyan!important;
}
 
 
}
