css:
@-moz-document domain("fanfou.com") {
@font-face {
  font-family: 'Roboto';
  font-style: normal;
  font-weight: 300;
  src: local('Roboto Light'), local('Roboto-Light'), url(http://themes.googleusercontent.com/static/fonts/roboto/v11/Hgo13k-tfSpn0qi1SFdUfbO3LdcAZYWl9Si6vvxL-qU.woff) format('woff');
}

body{
  background: rgba(238,238,238,1) !important;
}

#navigation, .ui-roundedbox-corner, .ui-roundedbox-content{
  background: none !important;
  left: 7% !important;
}

#main{
  position: absolute;
  right: 10%;
  top: 120px;
  width: 55% !important;
  background: rgba(250,250,250,1) !important;
  border-top-left-radius: 5px !important;
  border-top-right-radius: 5px !important;
  border-bottom-left-radius: 0px !important;
}

h2.slogan{
  display: none;
}

#timeline-notification a{
  background: none !important;
  border: none !important;
  color: #D6B800 !important;
}

.textarea-wrapper textarea {
  width: 140% !important;
  height: 80px !important;
 }

.wa li, #stream ol li{
  width:85% !important;
  border-bottom-width: 1px;
  border-bottom-color: rgba(230,230,230,1);
  border-bottom-style: solid;
  padding-top: 25px !important;
  padding-bottom: 20px !important;
  background: none !important;
}

.wa li .op, #stream ol li .op{
  padding-top: 18px !important;
}

#sidebar{
  background: none !important;
  position:absolute;
  left: 10%;
  top: 105px;
  border: none !important;
}

#user_stats li{
  border: none !important;
}

#reminder{
  display: none;
}

#goodapp{
  display: none;
}

#badge-sect{
  display:none;
}

#trendtabs{
  display: none;
}

#fanfou-ad-microwave-oven{
  display: none;
}

#footer{
  display: none;
}

.sect{
  display:none;
}

#searchr{
  display: none;
}

div #user_rec, #friends{
  display: none !important;
}

.avatar{
  border-radius: 50%;
}

#header{
  padding-top:30px !important;
}

.global-header-content{
  position: absolute !important;
  left: -16% !important;
}

.actpost{
  position: absolute;
  left: 110% !important;
  top: -6px !important;
}
  
.counter{
  position: absolute;
  left: 180px !important;
  top: -18px !important;
  font-family: Georgia !important;
}

#pagination-more{
  width: 86% !important;
}

#navtabs ul li, #navtabs ul li a{
  height: 40px;
}

#navtabs ul .current{
  background: rgba(220,220,220,1) !important;
  border-radius: 3px !important;
}

#navtabs ul .current a{
  background: rgba(0,0,0,0) !important;
}

#navtabs ul li a{
  font-size: 16px;
  padding-top: 6px;
}

.count{
  font-size: 17px !important;
  font-family: Roboto !important;
  font-weight: 100 !important;
}
  
#user_top h3{
  font-family: Roboto;
  font-style:normal;
  font-weight: 100 !important;
  font-size: 25px !important;
  color: rgba(75,88,94,1) !important;
}

#user_stats .count{
  color: rgba(75,88,94,1) !important;
}

#user_stats .label{
  color: rgba(75,88,94,1) !important;
}

.region a{
  color: rgba(58,157,207,1) !important;
}

.stabs{
  border: none !important;
}

#bio a{
  color: rgba(58,157,207,1) !important;
}

.tags{
  display: none;
}

#iconset{
  display: none;
}

#avatar{
  border-radius: 50%;
  border: none !important;
}

.stabs ul li a{
  color: rgba(58,157,207,1) !important;
}
  
.stabs ul li.current a{
  color: rgba(75,88,94,1) !important;
}
    
.ui-roundedbox-content ul li a{
  color: rgba(58,157,207,1) !important;
  background: none !important;
}
      
#userview_link .label{
  color: rgba(58,157,207,1) !important;
  background: none !important;
}
      
.stamp a{
  color: gray !important;
}
        
.reply a{
  background: none !important;
}
          
.content a{
  color: rgba(58,157,207,1) !important;
  background: none !important;
}
            
.paginator li a{
  color: rgba(58,157,207,1) !important;
  background: none !important;
  border: none;
}
              
.paginator li{
  background: none !important;
}

.content, span, #bio, ul, li, .label, .region a, .ui-roundedbox-content ul li a, h1, h2, h3, h4 ,h5 ,h6, p, a{
  font-family: Roboto, 'Noto Sans S Chinese';
  font-weight:normal;
}
  
.author{
  color: rgba(58,157,207,1) !important;
  background: none !important;
}
    
a:link {text-decoration:none !important;}
a:visited {text-decoration:none !important;}
a:hover {text-decoration:none !important;}
a:active {text-decoration:none !important;}
}
