css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://www.shacknews.com/chatty), url-prefix(https://www.shacknews.com/chatty), url-prefix(http://shacknews.com/chatty), url-prefix(https://shacknews.com/chatty)  {

  /* fuell's posts -- change fpauthor_xxxxx to your own user id */
  div.fullpost.fpauthor_26466 { 
    background-image: url(data:image/gif;base64,R0lGODlhCAAKALMAACMCJCICIyABIR0AHhoAGysFLSgEKi0GLyoFLCcEKR8BIBwAHSQDJgAAAAAAAAAAACwAAAAACAAKAAAELLCUQYc8gRIjGRkEcxTHtwTjYVDKWCDLsCCXIiNpECcpEy8pQ2wAkMAogkMEADs=) !important;
  }
  
  ::-moz-selection {
    background: #994ad5;
    color: #fff;
  }
  
  #banner a:hover {
    text-shadow: -1px -1px 7px #994ad5, 1px -1px 7px #994ad5, -1px 1px 7px #994ad5, 1px 1px 7px #994ad5;
  }
  div#ad-leaderboard {
    background: linear-gradient(to right, #000000 0%, #1e0d2b 10%, #1e0d2b 90%, #000000 100%, #000000 100%) repeat scroll 0 0 rgba(0, 0, 0, 0);
  }
  body.page-chatty #main {
    background: linear-gradient(to right, #000000 0%, #1e0d2b 10%, #1e0d2b 90%, #000000 100%, #000000 100%) repeat scroll 0 0 rgba(0, 0, 0, 0);
    display: block;
  }
  
  .article {
    padding: 10px;
  }
  .article h1 {
    font-style: unset;
  }
  .article .author {
    color: #ccc;
  }
  .main-col {
    background-color: #000;
    display: block;
    width: 660px;
  }
  div.aside {
    float: none !important;
    position: relative;
    top: 0px;
    right: 0px;
    width: unset;
    margin-bottom: 300px;
  }
  
  div.commentsblock div.pagenavigation a:link, div.commentsblock div.pagenavigation a:visited, div.commentsblock div.pagenavigation a:active, div.commentsblock div.pagenavigation a:hover, div.commentsblock div.pagenavigation span.nextprev {
    border: 1px solid #000;
    background-color: #000;
    font-size: 14px;
    font-family: "Fjalla One",sans-serif;
    font-weight: 100 !important;
  }
  div.commentsblock div.pagenavigation a.selected_page:link, div.commentsblock div.pagenavigation a.selected_page:visited, div.commentsblock div.pagenavigation a.selected_page:active {
    border: 0px solid #000;
    border-left: 2px solid #9e5dcf;
    background-color: #282a2d;
    padding: 4px 10px;
  }
  .footer-line, .footer-line a {
    font-family: Calibri, sans-serif;
    color: #666;
    margin-bottom: 10px;
  }
  div.collapsed div.postmeta a.showpost:link, div.collapsed div.postmeta a.showpost:visited, div.collapsed div.postmeta a.showpost:active, div.collapsed div.postmeta a.showpost:hover {
    background: linear-gradient(to bottom, #202224 0%, #000 100%) repeat scroll 0 0 rgba(0, 0, 0, 0);
    color: #fff;
    font-size: 11px;
    font-weight: 100;   
  }
  div.threads div.collapsed {
    border-bottom: 1px dotted #994ad5;
  }

  div#chatty_comments_wrap {
    margin: 0 auto;
  }
  div#commenttools.commentstools, div.commentsblock {
   /* width: 980px; */
  }
  div.commentsblock a:link, div.commentsblock a:visited, div.commentsblock a:active {
    color: #fff;
    text-decoration: none;
  }
  div.commentsblock a:hover {
    text-shadow: -1px -1px 7px #994ad5, 1px -1px 7px #994ad5, -1px 1px 7px #994ad5, 1px 1px 7px #994ad5;
    color: #000;
  }
  div#commentssettings, div.newcommentform {
    border: 2px solid #662d91;
    box-shadow: 2px 2px 8px #662d91, -2px 2px 8px #662d91, 2px -2px 8px #662d91, -2px -2px 8px #662d91;
  }
  div#commentssettings h5 {
    border-bottom: 1px solid #662d91;
  }
  div.postbody a:link {
    color: #8e52ca !important;
    text-decoration: underline;
  }
  div.postbody a:visited {
    color: #662d91 !important;
  }
  div.postbody a:hover {
    color: #fff !important;
  }
  div.postbox button.useraction, div#commentssettings button.useraction, div#commentssettings input.useraction[type="submit"] {
    border: 1px solid #662d91;
    width: 120px;
    height: 24px;
    font-family: arial, helvetica;
    font-size: 16px;
    position: relative;
    left: -10px;
  }
  div.threads {
    /* width: 980px !important; */
    padding: 0px !important;
  }

  /* posts */
  div.fullpost {
    border-top: 0px !important;
    border-left: 4px solid #1a1a1a;
    background: linear-gradient(to bottom, #282a2d 0%, #282a2d 22px, #202224 23px, #202224 100%) !important;
  }
  ul li ul li div.fullpost {
    border: 0px !important;
    background: linear-gradient(to bottom, #232628 0%, #232628 22px, #1c1d1f 23px, #1c1d1f 100%) !important;
  }
  div.fullpost.fpmod_stupid {
    border-left: 4px solid #36d83a !important;
  }
  div.fullpost.fpmod_political {
    border-left: 4px solid #d8ac36 !important;
  }
  div.fullpost.fpmod_nws {
    border-left: 4px solid #a61414 !important;
  }
  div.fullpost.fpmod_informative {
    border-left: 4px solid #71d3ec !important;
  }
  span.author {
    position: relative;
    color: #666;
    font-family: 'Century Gothic', sans-serif !important;
    font-size: 14px;
  }
  span.user {
    position: relative;
    color: #fff !important;
    font-family: 'Century Gothic', sans-serif;
  }
  span.user a {
    color: #fff !important;
    font-weight: bold !important;
  }  
  div.postbody {
    font-family: Calibri, arial, sans-serif;
    font-size: 16px;
    color: #a8a9ad !important;
    line-height: 20px !important;
  }
  div.reply {
    top: 0px; 
    right: 0px;
  }
  div.reply a, div.reply a:visited, div.reply a:active {
    border: 0px !important;
    height: 20px;
    background-image: inherit !important;
    background-color: #202224;
  }
  div.reply a:hover {
    background: inherit !important;
    background-color: #fff !important;
  }  
  div.replying div.reply a:link, div.replying div.reply a:visited, div.replying div.reply a:active, div.replying div.reply a:hover, div.reply a:hover {
    background-color: #8e52ca;
  }
  
  div.oneline {
    background-color: #151515 !important;
    font-family: Calibri, arial, sans-serif !important;
    font-size: 15px;
    line-height: 20px;
  }
  span.oneline_user {
    color: #725074 !important;
  }
  div.oneline span.this_user {
    color: #67ff38 !important;
  }
  .op span.oneline_user {
    color: #9e5dcf !important;
    background-color: inherit !important;
    border-radius: 0px !important;
  }
  div.oneline.op span.oneline_user {
    padding: 0px;
  }
  div.commentsblock a:hover {
    text-shadow: 1px 1px 2px #662d91, -1px 1px 2px #662d91, 1px -1px 2px #662d91, -1px -1px 2px #662d91 !important;
    color: #fff;
    background-color: inherit !important;
  }
  div.threads ul ul {
    background-color: #151515;
  }
  div.capped p.capnote strong {
    color: #9e5dcf;
  }
  div.capped p.capnote a:link, div.capped p.capnote a:visited, div.capped p.capnote a:active {
    background: linear-gradient(to bottom, #202224 0%, #000 100%);
    color: #fff !important;
    text-indent: 13px;
    font-weight: normal;
  }
  div.capped p.capnote a:hover {
    background: linear-gradient(to bottom, #310b4c 0%, #000 100%);
    color: #fff;
  }
  li div.olmod_informative {
    background: linear-gradient(to right, #151515 0%, #71d3ec 80%);
  }
  li div.olmod_informative a, li div.olmod_informative a:hover {
    background-color: unset !important;
  }
  div.olmod_informative a span.oneline_body {
    background-image: unset !important;
    padding-left: 0px !important;
  }
  li div.olmod_informative span.oneline_user {
    color: #fff !important;
  }
  li div.olmod_stupid {
    background: linear-gradient(to right, #151515 0%, #36d83a 80%);
  }
  li div.olmod_stupid a, li div.olmod_stupid a:hover {
    background-color: unset !important;
  }
  div.olmod_stupid a span.oneline_body {
    background-image: unset !important;
    padding-left: 0px !important;
  }
  li div.olmod_stupid span.oneline_user {
    color: #fff !important;
  }
  li div.olmod_nws {
    background: linear-gradient(to right, #151515 0%, #a61414 80%);
  }
  li div.olmod_nws a, li div.olmod_nws a:hover {
    background-color: unset !important;
  }
  div.olmod_nws a span.oneline_body {
    background-image: unset !important;
    padding-left: 0px !important;
  }
  li div.olmod_nws span.oneline_user {
    color: #fff !important;
  }
  li div.olmod_political {
    background: linear-gradient(to right, #151515 0%, #d8ac36 80%);
  }
  li div.olmod_political a, li div.olmod_political a:hover {
    background-color: unset !important;
  }
  div.olmod_political a span.oneline_body {
    background-image: unset !important;
    padding-left: 0px !important;
  }
  li div.olmod_political span.oneline_user {
    color: #fff !important;
  }
  
  /******************************************
  /* images and other spammy looking things *
  ******************************************/
  
  /* new comment button near top of chatty */
  div#newcommentbutton a:link, div#newcommentbutton a:visited, div#newcommentbutton a:active, div#newcommentbutton a:hover {
  background-image: url(data:image/gif;base64,);
  }
  
  /* replaces thread tree images */
  div.threads ul ul {
    background-image: url(data:image/gif;base64,R0lGODlhAQACAIAAAHh4eAAAACwAAAAAAQACAAACAgQKADs=) !important;
  }
  div.threads ul ul li {
    background-image: url(data:image/gif;base64,R0lGODlhDAAyAJEAAHh4eBUVFQAAAAAAACwAAAAADAAyAAACN0SOGabKjR5cM9YjYW7bXXoB4kiOX5egVni27FupmBvTkwzW8G1rPffz7HxDYFGoS/KOqWDzUgAAOw==) !important;
  }
  div.threads ul ul li.last {
    background-color: #151515 !important;
    background-image: url(data:image/gif;base64,R0lGODlhDAAcAJEAAHh4eBUVFQAAAAAAACwAAAAADAAcAAACHkSOGabKjR5cM9YjYW7bXXoB4kiK34mm6sq27guvBQA7) !important;
    border-bottom: 1px solid #151515 !important;
  }
  
  /* new shackmsg icon */
  span.author a.shackmsg {
    width: 15px !important;
    height: 13px !important;
background-image: url(data:image/png;base64,);
    background-position: 50% 50%;
    background-repeat: no-repeat;
}
span.author a.shackmsg img {
    visibility: hidden !important;
    width: 15px !important;
}
  
  /* new lightning bolt */
  span.author .lightningbolt, .lightningbolt {
    padding-left: 20px;
background-image: url(data:image/png;base64,);
    background-position: 75% 2px;
    background-repeat: no-repeat;
  }

  span.author .lightningbolt { 
    background-position: 50% 50%;
    padding-left: 15px !important;
  }
  #comments .lightningbolt img, .lightningbolt img { 
    display: none; 
  }
  
  /* mods */
  div.olauthor_208786 span.oneline_user, div.olauthor_172215 span.oneline_user, div.olauthor_2650 span.oneline_user, div.olauthor_169887 span.oneline_user, div.olauthor_228129 span.oneline_user, div.olauthor_194196 span.oneline_user, div.olauthor_3259 span.oneline_user, div.olauthor_10028 span.oneline_user, div.olauthor_168479 span.oneline_user, div.olauthor_5334 span.oneline_user, div.olauthor_8105 span.oneline_user, div.olauthor_32016 span.oneline_user, div.olauthor_1194 span.oneline_user, div.olauthor_6585 span.oneline_user, div.olauthor_168256 span.oneline_user, div.olauthor_169197 span.oneline_user, div.olauthor_3243 span.oneline_user, div.olauthor_169049 span.oneline_user, div.olauthor_9085 span.oneline_user, div.olauthor_6380 span.oneline_user, div.olauthor_12398 span.oneline_user, div.olauthor_7570 span.oneline_user, div.olauthor_8316 span.oneline_user, div.olauthor_9031 span.oneline_user, div.olauthor_9211 span.oneline_user, div.olauthor_7660 span.oneline_user, div.olauthor_169927 span.oneline_user, div.olauthor_169942 span.oneline_user, div.olauthor_185650 span.oneline_user, div.olauthor_171127 span.oneline_user, div.olauthor_16880 span.oneline_user, div.olauthor_44566 span.oneline_user, div.olauthor_174581 span.oneline_user, div.olauthor_232830 span.oneline_user, div.olauthor_9509 span.oneline_user {
    padding-right: 20px !important;
  }

}
