css:
@-moz-document url-prefix("http://www.roblox.com"), url-prefix("https://www.roblox.com") {
#navigation-container a.btn-logo,
#navigation-container a.btn-logo:visited,
#navigation-container a.btn-logo:hover
{
	background-image: url(http://www.roblox.com/images/RevisedHeader/btn-obc_logo.png) !important
}

.site-header .HeaderDivider
{
	border-left: 1px solid #878988 !important
}

.site-header
{
	background-image:url(http://www.roblox.com/images/RevisedHeader/bg-obc_header.png) !important
}

body
{  
	background: url('http://www.roblox.com/images/cssthemes/outrageous2/bkg.jpg') center right #000000 repeat fixed !important;
  	font-family: Arial;
}
#BodyWrapper,#Body {
       -webkit-border-radius: 10px;
       -moz-border-radius: 10px;
       border-radius: 10px;
    }
    
    #ctl00_cphRoblox_rbxHeaderPane_statusBox {
       -webkit-border-radius: 4px;
       -moz-border-radius: 4px;
       border-radius: 4px;
    }
    .subMenu:after {
        position: absolute;
        color: white;
    	content: "ROBLOX: OBC 3.0 Theme by mandown1";
    	right: -70px;
    	padding: 5px;
    }
}
