css:
@-moz-document domain("r10.net")
{
	*
	{
		border-radius: 0!important;
		box-shadow: none!important;
	}
	body
	{
		background-color: #E1E1E2;
	}
	table.tablosifir
	{
		margin-bottom: -2px;
		margin-left: -1px;
	}
	td#reklami, div.span6, body > center, body > div#r10forum > center, body > div#r10forum > div[align="justify"], body > div[align="justify"], .h2smallfont, body > div#r10forum  > form > div[align="center"]
	{
		display: none!important;
	}
	body > form > div[align="center"], body > div#modal-1, div > button#uyelik, body > div.modal-overlay, body > div.smallfont, body > br
	{
		display: none!important;
		height: 0;
		visibility: hidden!important;
		width: 0;
	}
	td.navbar h1, div.navbar h1, div#cihan h1
	{
		font-size: 13px!important;
	}
	td.navbar h1
	{
		margin-top: 0;
	}
	div#cihan h1
	{
		margin-left: 22px;
	}
	div.icerik
	{
		border: 0;
	}
	.kare
	{
		background-color: #E1E4F2;
		height: 60px;
		max-height: 60px;
		max-width: 60px;
		width: 60px;
	}
	.daire
	{
		background-color: #FFF;
		background-origin: content-box;
		background-position:center top;
		background-repeat: no-repeat;
		background-size: 60px;
		border-radius: 60px;
		height: 60px;
		max-height: 60px;
		-moz-background-origin: content-box;
		-moz-background-size: 60px;
		-moz-border-radius: 60px;
		-webkit-background-origin: content-box;
		-webkit-background-size: 60px;
		-webkit-border-radius: 60px;
	}
	.mert
	{
		background-image: url(http://www.r10.net/images/serit.png);
		border-bottom-left-radius: 3px;
		border-bottom-right-radius: 3px;
		height: 3px;
	}
	td.thead, th.thead, div.thead
	{
		padding: 4px;
	}
	.pagenav a
	{
		text-decoration: none;
	}
	.pagenav td
	{
		padding: 2px 4px 2px 4px;
	}
	.shade, a.shade:link, a.shade:visited
	{
		color: #777777;
		text-decoration: none;
	}
	a.shade:active, a.shade:hover
	{
		color: #FF4400;
		text-decoration: underline;
	}
	.tcat .shade, .thead .shade, .tfoot .shade
	{
		color: #DDDDDD;
	}
	.fieldset
	{
		margin-bottom: 6px;
	}
	.fieldset, .fieldset td, .fieldset p, .fieldset li
	{
		font-size: 11px;
	}
	.bigusername
	{
		font-size: 14pt;
	}
}
