css:
@-moz-document domain("gameaboutsquares.com") {
	#wrap { transform: none !important; } /* kill the giants! */

	.layer[style*="url("] {
		background-image: none !important;
	}
	.subsquare + .layer:before,
	.circle:after ,
	.layer:after {
		white-space: pre; /* use \00000A in content to force a linebreak */
		text-transform: uppercase;
		color: #fff;
		text-shadow: 0 0 3px #000;
		display: block;
		position: absolute;
	}
	.subsquare + .layer:before {
		visibility: hidden;
		top: 0;
		right: 0;
		z-index: 10000;
		font-size: 0.8em;
	}
	.subsquare + .layer[style*="radius: 35px;"]:before {
		visibility: visible;
	}
	.circle[style*="background-color:"]:after ,
	.layer[style*="background-color:"]:after {
		text-align: center;
		width: 100%;
		bottom: 10%;
	}
	.subsquare[style*="rgb(156, 32, 32)"] + .layer:before,
	.subsquare[style*="rgb(194, 70, 70)"] + .layer:before,
	.subsquare[style*="rgb(154, 19, 5)"] + .layer:before,
	.subsquare[style*="rgb(192, 57, 43)"] + .layer:before,
	.circle[style*="rgb(192, 57, 43)"]:after ,
	.circle[style*="rgb(194, 70, 70)"]:after ,
	.layer[style*="rgb(194, 70, 70)"]:after ,
	.layer[style*="rgb(192, 57, 43)"]:after {
		content: 'red' !important;
	}
	.subsquare[style*="rgb(44, 62, 80)"] + .layer:before,
	.subsquare[style*="rgb(6, 24, 42)"] + .layer:before,
	.circle[style*="rgb(44, 62, 80)"]:after ,
	.layer[style*="rgb(44, 62, 80)"]:after {
		content: 'teal' !important;
	}
	.subsquare[style*="rgb(41, 128, 185)"] + .layer:before,
	.subsquare[style*="rgb(3, 90, 147)"] + .layer:before,
	.circle[style*="rgb(41, 128, 185)"]:after ,
	.layer[style*="rgb(41, 128, 185)"]:after {
		content: 'blue' !important;
	}
	.subsquare[style*="rgb(203, 118, 8)"] + .layer:before,
	.subsquare[style*="rgb(241, 156, 46)"] + .layer:before,
	.subsquare[style*="rgb(230, 126, 34)"] + .layer:before,
	.subsquare[style*="rgb(192, 88, 0)"] + .layer:before,
	.circle[style*="rgb(230, 126, 34)"]:after ,
	.circle[style*="rgb(241, 156, 46)"]:after ,
	.layer[style*="rgb(241, 156, 46)"]:after ,
	.layer[style*="rgb(230, 126, 34)"]:after {
		content: 'orange' !important;
	}
	.subsquare[style*="rgb(11, 113, 64)"] + .layer:before,
	.subsquare[style*="rgb(0, 75, 26)"] + .layer:before,
	.subsquare[style*="rgb(0, 122, 95)"] + .layer:before,
	.subsquare[style*="rgb(22, 160, 133)"] + .layer:before,
	.circle[style*="rgb(22, 160, 133)"]:after ,
	.circle[style*="rgb(11, 113, 64)"]:after ,
	.layer[style*="rgb(11, 113, 64)"]:after ,
	.layer[style*="rgb(22, 160, 133)"]:after {
		content: 'green' !important;
	}
	.subsquare[style*="rgb(117, 51, 144)"] + .layer:before,
	.subsquare[style*="rgb(155, 89, 182)"] + .layer:before,
	.circle[style*="rgb(155, 89, 182)"]:after ,
	.layer[style*="rgb(155, 89, 182)"]:after {
		content: 'purple' !important;
	}
	.subsquare[style*="rgb(89, 102, 103)"] + .layer:before,
	.subsquare[style*="rgb(127, 140, 141)"] + .layer:before,
	.circle[style*="rgb(127, 140, 141)"]:after ,
	.layer[style*="rgb(127, 140, 141)"]:after {
		content: 'grey' !important;
	}
	.subsquare[style*="rgb(47, 49, 30)"] + .layer:before,
	.subsquare[style*="rgb(85, 87, 68)"] + .layer:before,
	.circle[style*="rgb(85, 87, 68)"]:after ,
	.layer[style*="rgb(85, 87, 68)"]:after {
		content: 'brown' !important;
	}

}
