css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("https://www.tumblr.com/dashboard"), url-prefix("https://www.tumblr.com/tagged/") {

    :root {
        --content-width: 1380px;  /* customize here only; should be value in px units */
    }

    .l-container {
        width: 1380px !important;  /* for browsers without CSS variables */
        width: var(--content-width) !important;
    }

    #left_column {
        width: 1030px !important;  /* for browsers without CSS variables */
        width: calc(var(--content-width) - 350px) !important;
    }

    .post_full {
        width: 100% !important;
    }

    .post_content,
    .flipcard,
    .flipcard_front,
    .high_res_link,
    .image,
    .photoset_row,
    .post_content_inner,
    .post_media,
    .photoset_photo,
    .photoset_photo img {
        width: 100% !important;
        height: auto !important;
    }

}
