css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document 
url-prefix("http://www.bungie.net"),
url-prefix("https://www.bungie.net")
{
	body
	{
		background: url(http://i.imgur.com/Ze19Nv9.png) no-repeat fixed top center,url(http://i.imgur.com/1rTFtk1.png) no-repeat fixed bottom center,url(http://i.imgur.com/yWWIaDl.gif) fixed !important;
		background-color: #fff !important;
	}
	div#TopicsPage.column.main, .column.main
	{
		background: rgba(14,14,14,0.5) !important;
		color: #fff !important;
		padding-left: 123px !important;
		text-shadow: none !important;
	}
	.contextPane.on
	{
		background-color: rgba(0,0,145,0.1) !important;
	}
	#accountPaneWrapper
	{
		background: url(http://i.imgur.com/Ze19Nv9.png) no-repeat fixed top center,url(http://i.imgur.com/1rTFtk1.png) no-repeat fixed bottom center,url(http://i.imgur.com/yWWIaDl.gif) fixed rgba(221, 225, 228, 1)!important;
		background-color: rgba(221, 225, 228, 1) !important;
	}
	.container_underHeader .btn_openCreateNewTopic,.searchedTag
	{
		color: #fff !important;
	}
	#accountPane
	{
		background: url(http://i.imgur.com/oP7YxVb.png) no-repeat fixed 50% 0px / cover !important;
	}
	.Legend #content
	{
		background: url(http://i.imgur.com/oUgC8hR.png) no-repeat center #0b0b0b;
	}
	#statBlocks
	{
		background-color: rgba(149,204,255,0.4) !important;
		box-shadow: inset 0px 0px 0px 0px #fff, inset 0px -1px 23px 2px #52e4ff, 0 2px 12px rgba(240, 247, 255, .8), 0 0px 0px 3px rgba(222, 239, 255, .2);
	}
	.PublicForum .column.main:before
	{
		background: none repeat scroll 0px 0px rgba(221, 225, 228, 0.4);
		content: "";
		height: 100%;
		left: 0px;
		position: absolute;
		width: 0px !important;
		z-index: 0;
	}
	nav#Nav_Community.column.side.left
	{
		height: 0px;
		visibility: hidden;
		width: 0px;
	}
	.container_sort
	{
		background-color: rgba(19,19,19,0.9) !important;
		width: 93.2% !important;
	}
	div.container_underHeader.cf
	{
		width: 85% !important;
	}
	.topics
	{
		background-color: rgba(24,24,24,0.7) !important;
		width: 95% !important;
	}
	div#forum_post.forum_panel.forumPost
	{
		background-color: rgba(24,24,24,0.7) !important;
		width: 95% !important;
	}
	#post > .post
	{
		background-color: rgba(0,117,224,0.2) !important;
	}
	.forumPost .header_post
	{
		background-color: rgba(14,14,14,0.7) !important;
		color: #0abfff !important;
	}
	.forumPost .post_title
	{
		color: #0084ff !important;
		text-shadow: 0px 1px 0px #004483 !important;
	}
	.forumPost article h1 a
	{
		color: #0abfff !important;
	}
	.forumPost article .post_body, .forumPost article .post_urlLinkOrImage
	{
		color: #fff !important;
	}
	.topics .title
	{
		color: #0084ff !important;
	}
	.forumPost.employee > article > .post h1:after, .replies .employee.post h1:after
	{
		background: none !important;
		border: none !important;
		color: #FFD224 !important;
		content: "| BUNGIE" !important;
		font-size: 14px !important;
		padding-bottom: 3% !important;
	}
	.forumPost.employee > article > .post h1, .replies .employee.post h1
	{
		background: none repeat scroll 0% 0% #9e8216 !important;
		border: 1px solid #ffd224 !important;
		padding-right: 75% !important;
	}
	div.post.employee .content .post_body.cf
	{
		color: #FFD224 !important;
	}
	.forumPost .post.employee
	{
		background: url('http://i.imgur.com/4Lx2k86.png') no-repeat 100% -27% !important;
	}
	.forumPost.ninja > article > .post h1:after, .replies .ninja.post h1:after
	{
		background: none !important;
		border: none !important;
		color: #f96 !important;
		content: "| Master Forum Ninja" !important;
		font-size: 14px !important;
		padding-bottom: 3% !important;
	}
	.forumPost.ninja > article > .post h1, .replies .ninja.post h1
	{
		background: none repeat scroll 0% 0% #3B2317 !important;
		border: 1px solid #F96 !important;
		padding-right: 65% !important;
	}
	div.post.ninja .content .post_body.cf
	{
		color: #F96 !important;
	}
	.forumPost.mentor > article > .post h1:after, .replies .mentor.post h1:after
	{
		background: none !important;
		border: none !important;
		color: #2A2A2A !important;
		content: "| Mentor" !important;
		font-size: 14px !important;
		padding-bottom: 3% !important;
	}
	.forumPost.mentor > article > .post h1, .replies .mentor.post h1
	{
		background: none repeat scroll 0% 0% #F5F9FC !important;
		border: 1px solid #AAB8C3 !important;
		padding-right: 75% !important;
	}
	div.container_body.cf
	{
		background: rgba(255,255,255,0.0) !important;
	}
	ui.container_search_news.news_search_section.topics cf
	{
		background: #fff !important;
	}
	.Bungie .column.main, .GroupSearch .column.main, .MyCommunity .column.main, .Edit .column.main, .Search .column.main, .Groups .column.main, .Activity .column.main
	{
		background: none !important;
		margin-left: auto !important;
		margin-right: auto !important;
		width: 70% !important;
	}
	.topics li:hover
	{
		background: none repeat scroll 0 0 rgba(0, 117, 234, 0.1) ! important;
		padding-left: 1px !important;
	}
	.Search .header_body
	{
		border-bottom: 4px solid #fff;
	}
	.Search .header_body,p.header_search_section
	{
		background: rgba(14,14,14,0.9) !important;
	}
	.GroupSearch .header_body,.header_search_section
	{
		background: none rgba(0,0,0,0.4) !important;
		border-bottom: 1px solid #fff;
	}
	.container_search_news.news_search_section.topics.cf,.footer_search_section,.container_users.dualColList,header_search_section,.container_groups.groups_search_section
	{
		background: rgba(24,24,24,0.9) !important;
	}
	.groups_search_section, .activity_search_section, .news_search_section, .topics_search_section, .help_search_section
	{
		margin: 0px 11px !important;
		padding: 4px !important;
	}
	.Search .topics
	{
		background: rgba(0,0,0,1);
	}
	.groups_search_section .content > a, .dualColList .content > a
	{
		color: #0abfff !important;
	}
	.groups_search_section p, .news_search_section p
	{
		color: #2b5299 !important;
	}
	li.cf.news
	{
		border-top: 1px solid #fff !important;
	}
}
