css:
@-moz-document domain("tumblr.com") {

/* blue fix */
div.search_form_row {background-color: rgba(179, 125, 151, .5)}
div.l-header .selection_nipple {border-bottom-color: rgba(179, 125, 151, .5)}

#left_column, #right_column {
	background-color: transparent;
}
.controls_section.user_list li.item .follow_list_item_blog:before {
  background-image: none;
  border-right: none;
  }
.radar div.radar_footer .radar_avatar:before {
  background-image: none;
  }
#right_column:after {display:none}

	/* background image */
body:not(.mceContentBody) {
  background: url('http://images4.fanpop.com/image/photos/17200000/Canada-America-hetalia-17237985-900-700.jpg') fixed no-repeat top center rgb(179, 125, 151);
  background-size: cover;
}

	/* posts' background */
.l-content {
  background-color: transparent !important;
}

	/* right-side menus */
#right_column {
  background-color: rgba(179, 125, 151, .5);
  border-radius: 10px;
}
#right_column:hover {
  background-color: rgba(179, 125, 151, .8);
}

	/* search box */
div.search_form_row {
  background-color: rgba(179, 125, 151, .5);
}

	/* 'new post' buttons */
#new_post {
  background-color: rgba(179, 125, 151, 0)/*=transparent*/;
  border: thin solid purple;
  width: 542px !important; /* +2px to account for border width */
}
#new_post .post_avatar:after {
  border-right-color: rgba(179, 125, 151, 0)/*=transparent*/;
}
#new_post:hover {
  background-color: rgba(179, 125, 151, .8);
  border-color: transparent;
}
#new_post:hover .post_avatar:after {
  border-right-color: rgba(179, 125, 151, .8);
}
 		/* icons */
#new_post .new_post_label I::before {
  color: purple;
}
 		/* labels */
#new_post .new_post_label_text {
  color: purple;
}
#new_post:hover .new_post_label_text {
  color: rgba(255,255,255,.5);
}

	/* tab icons */
.l-header .selection_nipple {
  border-bottom-color: rgba(179, 125, 151, 1);
}

	/* remove the blue fade - https://userstyles.org/styles/102769 */
.controls_section.user_list li.item .follow_list_item_blog:before {
  background-image: none;
  border-right: none;
  }
.radar div.radar_footer .radar_avatar:before {
  background-image: none;
  }
#right_column:after {display:none}
}
