css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("logitech.com") {

html, body, tbody, thead, th, tr, td, blockquote, li, ul,
 h1, h2, h3, h4, h5, h6, font, strong, p, form, footer,
#promosResWrap, .primary-bar, .secondary-bar, .secondary,
.marquee, .highlights li div, .row, .features, .wedge,
.inner, .secondary-nav-bar 
{
color: #fff !important;
background: #000 !important;
}
select, button, .filterContainer, .lia-decoration-border-content,
span, a, #header2 .c-wrap .tab li .title, .lia-quilt-column,
.lia-menu-bar, .lia-quilt-column-alley, .lia-quilt, #header2,
.slider, .featured-product, .filter-row, .product, .products,
.outer-wrapper, .specs
{
background: #000 !important;
}
legend, select, input, span, code, textarea, pre, label,
 div, dd, .blue, .grey, .dark-grey
{
color: #FFF !important;
}
a {/*links*/
color: #FFF !important;
}
a:hover {/*link hover*/
text-decoration: underline !important;
}
textarea, pre, input, code { /*highlight*/
background: #333333 !important;
}
.filterContainer { /*filter*/
border:1px #fff solid !important;
}
}
