css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.cs-manager.com") {
/* general font and background */
body {
font-family: tahoma !important;
background-color:#000000 !important; 
background-image:url("http://i55.photobucket.com/albums/g121/Skewt/yab/BG.png") !important;
background-repeat:repeat !important;
} 
.dev tr td , .inline tr td, .inline input{
font-family: tahoma !important;
}
div#header a[href="/"]{
visibility: hidden !important;
}
/*center all*/
#container, #header, #footer_container{
border-left:1px solid black !important;
border-color: #665f55 !important;
}
#container, #footer_container,.post,#content, #header {
margin: 0 auto !important;
}

/* banner */
#header{
background-image:url("http://i55.photobucket.com/albums/g121/Skewt/yab/logo7-3.png") !important;
border-left-color: #665f55 !important;
border-right-color: #665f55  !important;
border-bottom-width: 0px !important;
}


/* clan, games/league, .... */
div#menu h3, div#info h3{
background-image:url("http://i55.photobucket.com/albums/g121/Skewt/yab/buttonbg3.png") !important;
background-position:top left !important;
border-top:0 !important;
border-bottom-color: #90887c !important;
border-bottom-width: 1px !important;
color: white !important;
}

/* presentation, news, players, ...*/
ul.menu li a, ul.gameinfo li a, ul#friend li a{
background-color:#90887c!important;
color: #f9f7ea!important;
border-color:#898075 !important;
background-image:url("http://i55.photobucket.com/albums/g121/Skewt/yab/linkbg-3.png") !important;
}
/* presentation, news, players, ... HOVER EFFECT */
ul.menu li a:hover, ul.gameinfo li a:hover, ul#friend li a:hover{
background-color:#aba49b!important;
color: #4c453d!important;
border-color: #665f55 !important;
}
ul#friend li a[title="Active"]:hover span{
background-image:url("http://i55.photobucket.com/albums/g121/Skewt/yab/friendsBGonline-1.png") !important;
background-color:#ffffff!important;
color: #d4ffcb!important;
}

/* buddy icon */
ul#friend a[title="Active"] span{
background-color: transparent !important;
background-image:url("http://users.telenet.be/tim.daminet/csm/online.png") !important;
background-repeat: no-repeat !important;
background-position: top right !important;
color: #97e188!important;
padding-right: 14px !important;
padding-top: 0px !important;
}
ul#friend a[title^="Idle:"] span{
background-color: transparent !important;
background-image:url("http://users.telenet.be/tim.daminet/csm/idle.png") !important;
background-repeat: no-repeat !important;
background-position: top right !important;
color: #90887c!important;
padding-right: 14px !important;
padding-top: 0px !important;
}

/*title*/
td.head a{
color: white!important;
}
td.head, div#guestbook h2, div#page h2, div[style="width: 602px;"] h2{
background-image:url("http://i55.photobucket.com/albums/g121/Skewt/yab/titlebar2.png") !important;
background-repeat:repeat-x !important;
background-position: top right !important;
border-color: #dfdfdf !important;
border-bottom: 0 !important;
color: white !important;
font-family: tahoma !important;
border-color: white !important;
}
/* forum/logout button */
div#header ul#tabtop li a:hover{
background-image:url("http://i55.photobucket.com/albums/g121/Skewt/yab/ForumBGhover.png") !important;
background-repeat: no-repeat !important;
border:0 !important;
background-color: transparent !important;
text-transform: uppercase !important;
padding:5px !important;
padding-top:8px !important;
margin-right:10px !important;
font-family: tahoma !important;
}
/* forum/logout button */
div#header ul#tabtop li a{
background-image:url("http://i55.photobucket.com/albums/g121/Skewt/yab/ForumBG.png") !important;
background-repeat: no-repeat !important;
border:0 !important;
background-color: transparent !important;
text-transform: uppercase !important;
padding:5px !important;
padding-top:8px !important;
margin-right:10px !important;
font-family: tahoma !important;
}
div#header ul#tabtop li a[title="FORUM"], div#header ul#tabtop li a[title="CSM!"]{
color:black !important;
}
div#header ul#tabtop li a[title="Log out"]{
color: #5c554b!important;
}

/* middle content value & info + lineup*/
td.info, td.foot, table.lineup tr td.position, div#page h2, div[style="width: 602px;"] h3{
font-family: tahoma !important;
background-color: #ffffff !important;
border-color: #efefef !important;
}
td.value, table.lineup tr td.player a, div.players ul#players li a, table.info tr td, tabel.dev tr td.tactic a.tactic{
font-family: tahoma !important;
background-color: #ffffff !important;
border-color: #efefef !important;
}
/*upcoming game*/
div#upcoming{
text-transform: uppercase !important;
}

/* search*/
form#search{
background-color:#ada59c!important;
}
input[name="q"], select[name="s"]{
background-color:#d9d4cf !important;
}
input.button, input.buttonsmall{
background-image:url("http://i55.photobucket.com/albums/g121/Skewt/yab/search-1.png") !important;
width:123px;
background-repeat:repeat-x !important;
border-color: #ada59c !important;
border-width:1px !important;
color: white !important;
font-family: tahoma !important;
}

/*lancamp*/
.lan .info{
background-color: #e5e5e5 !important;
background-image:url("http://users.telenet.be/tim.daminet/csm/lancamp.png") !important;
background-repeat: no-repeat !important;
background-position: center right !important;
}
.lan .value{
background-color: #f2f2f2 !important;
}

/*mail & scribble & guestbook*/
table.mail tr.head td, div#guestbook h3, td.select, td.delete{
background-color: #f2f0f0 !important;
font-family: tahoma !important;
text-transform: uppercase !important;
border-color: white !important;
}
div#guestbook div.post{
font-family: tahoma !important;
background-color: #faf7f7 !important;
border-color: #f2f0f0 !important;
}
table.mail tr td{ 
font-family: tahoma !important;
border-color: #efefef !important;
}
div#guestbook div.post div.modify{
background-color: #faf7f7 !important;
border-color: #f2f0f0 !important;
}
div#guestbook div.post div.modify > a:first-child{
color:green !important;
}
div#guestbook div.post div.modify a{
color: red !important;
}

/*tab help & tab vip*/
li.right a#help_tab{
background-color: #ebe7e1!important;
border-color: white !important;
}
li.vip a{
background-color: #ebe7e1!important;
border-color: white !important;
}
/* borders */
div#menu,#footer_left {
	border-right-width: 1px !important;
	border-right-color: #665f55  !important;
}
div#info,#footer_right {
	border-left-width: 1px !important;
	border-left-color: #665f55 !important;
}
}
