css:
@-moz-document url("https://tweetdeck.twitter.com/") {

.account-inline .fullname {
	display: none;
}

.account-inline .username {
	font-weight: bold;
	font-size: 0.9em;
}

.tweet-body footer {
	-webkit-transform: scale(0.9);
}

.item-box {
    padding: 6px;
}

.stream-item:nth-child(odd) {
	background: rgba(255,255,255,0.05) !important;
}

}
