css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mercadolibre.com.ar"), domain("mercadolibre.com.mx"), domain("mercadolibre.com.co"), domain("mercadolivre.com.br"),domain("mercadolibre.cl"),domain("mercadolibre.co.cr"),
    domain("mercadolibre.com.do"), domain("mercadolibre.com.ec"), domain("mercadolibre.com.pa"),
    domain("mercadolibre.com.pe"),domain("mercadolivre.pt"), domain("mercadolibre.com.uy"), domain("mercadolibre.com.ve")

{
body {
    background: url('http://www.gamestorrents.com/images/body_bg.jpg') repeat scroll 0% 0% #3E3E3E;
    margin: 0px;}

.vip-wrapper {
    background: url('http://www.gamestorrents.com/images/body_bg.jpg') repeat scroll 0% 0% #3E3E3E;
    margin: 0px;}   
    
.nav-path nav     {
    background: url('http://www.gamestorrents.com/images/body_bg.jpg') repeat scroll 0% 0% #3E3E3E;
    margin: 0px;}   
    
.price-info-installments     {color: #81BEF7 !important;}
    
.ch-price  {color: #81BEF7 !important;}
    
.box.new {background-color: #121726 !important;} 
    
.box.dashedTop {background-color: #121726 !important;}
    
a {color: #81BEF7 !important;}
    
.mainHeader.ml form input.search {background-color: #121726 !important}
input {
    -moz-appearance: textfield;
    -moz-binding: url("chrome://global/content/platformHTMLBindings.xml#inputFields");
    color: #E0DA65 !important;}
    
.ch-dropdown.secondary .ch-dropdown-trigger {background-color: #071D13 !important;}
    
.ch-dropdown-content {background-color: #071D13 !important;}
    
.actions, .actions-full {background-color: #1A263E !important;}
    
.list-view-item-subtitle {color: #658792 !important;}
    
.ch-price { color: #DDBC34 !important;}
    
.extra-info-condition{color: #DDBC34 !important;}
    
.extra-info-location{ color: #DDBC34 !important;}  
    
.extra-info-sold{ color: #DDBC34 !important;} 
    
.short-description.bg-great-info {background-color: #1A263E !important;}    
    
.ch-user-no-select { color: #dbdbdb !important;}    
    
a:visited strong, a:visited {color: #dbdbdb !important;}     
    
.vertical-qcat    {background-color: #1A263E !important;}    
    
dl.item-data { color: #000000 !important;} 
    
.vertical-qcat dl dd em {color: rgba(227, 227, 227, 1);}    
    
.vertical-qcat dl dt {color: #FFC741  !important;}
    
.simple-actions-v2     {color: #000000  !important;} 
    
.section .mclics {display: none !important;  }
    
.simple-actions-v2.ch-dropdown-skin {color: #FFC741  !important;} 

  .ml-header-fixed {position: static;}  
    
.mclics-bottom {display: none !important;  }
.short-desc-fixed.showed.collapsed {top: -90px;}      
.ch-pagination li a {    background-color: rgba(137, 96, 0, 1);}    
.related-searches {display: none !important;  }    
#itemDescription     {color: #1C1C1C  !important;} 
.ch-box-lite, .ch-box { background-color: #EFEDED;}
.no-warranty  { background-color: #bdbdbd;}
.ml-vip .selection-methods .selection-methods-info {
    background-color: #000;
    
}
}
