css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("https://robotframework.googlecode.com/hg/doc/userguide/") {
    .document {
        padding: 0 2em;
        font-family: Verdana;
    }
    h1, h2, h3 {
        border-top: 1px solid lightgray;
        padding-top: 0.5em;
    }
    a {
        text-decoration: none;
    }
    a:hover {
        text-decoration: underline;
    }
    img, .figure a img {
        border: 1px solid lightgray;
        padding: 1em;
    }
    .figure a:hover img {
        border: 1px solid blue;
    }
    p.caption {
        font-size: 80%;
        font-style: italic;
        margin-bottom: 1em;
    }
    pre, pre.literal-block, pre.doctest-block {
        padding: 1em;
    }
    .tip {
        background-color: lightgreen;
    }
    .prog, .code, .cli, misc, .literal, .path, .var {
        background: none repeat scroll 0 0 #e5e5e5;
        font-family: monospace;
        font-size: 1.2em;
        font-style: normal;
        padding: 0 0.5em;
        white-space: nowrap;
    }
    .opt, .name {
        font-family: monospace;
        font-size: 1.3em;
        font-style: normal;
        font-weight: bold;
    }
    table.example, table.tsv-example, table.messages, table.tabular {
        margin: 5px 20px 20px;
    }   
    table.tabular th, table.tabular td,
    table.example th, table.example td, table.tsv-example td {
        padding: 0.4em 0.5em;
    }
    table.example th, table.example td, table.tsv-example td,
    table.tabular th, table.tabular td {
        font-family: monospace;
        font-size: 1.1em;
    }
    table.tabular.docutils td:nth-child(2) {
        font-family: Verdana !important;
        font-size: 0.9em !important;
    }
    .note {
        background-color: lightyellow;
    }
    .opt {
        font-weight: bold;
        white-space: nowrap;
    }
}
