div[id^="userinfo"] ~  .bar,
div[id^="userinfo"] ~ .regdate,
div[id^="userinfo"] ~ .linkbtn,
.avatars + p.ol,
.postinfo > b
{display:none !important;}


.postinfo,.avatars,td.postcontent .defaultpost
{height:auto !important;min-height: 0px !important}


td.postcontent{width:100% !important;}

