,td[id^="userinfo_"] > .tsdm_statbar

/* 对贴吧自动签名脚本的签名屏蔽 */
,.p_content div[id^="post_content_"] > img[src="http://hiphotos.baidu.com/xuezhiling/pic/item/3f320df41bd5ad6ed82eb34981cb39dbb7fd3c63.jpg"] ~ *
,.p_content div[id^="post_content_"] > img[src="http://hiphotos.baidu.com/xuezhiling/pic/item/3f320df41bd5ad6ed82eb34981cb39dbb7fd3c63.jpg"] 

{display:none !important;}

/* beta： csdn post zip */
