css:
@-moz-document domain("facebook.com") {
:root {
--bg1: #eee !important;         
--bg2: #fff !important;           
--bg3: #f5f5f5 !important;             
--text1: #333 !important;              
--text2: #444 !important; 
    
--accent: #e6b6b6 !important;    
}

			/* Autohide Sidebar */

	/* sidebar hidden */
._1k0o {
    margin-left: -330px;
}

	/* sidebar showing */
._1k0o:hover {
    margin-left: -180px;
}

			/* Basics */

* {
    transition: ease-in-out .3s;
}

a, a:link, span a, ._517h, ._59pe:focus, ._59pe:hover {
    color: var(--accent) !important;
    text-decoration: none !important;
}
    
                 /* bg */
  
body, ._5vb_, ._5vb_ #contentCol {    
background: var(--bg1) !important;
color: var(--first-color) !important;
}    

				/* Header */

._2t-a, ._2s1x ._2s1y, ._19eb:hover {
background: var(--bg2) !important;
border: none;
box-shadow: none;
}   

#u_0_b > div:nth-child(1) > div:nth-child(1), #u_0_b > div:nth-child(1) > div:nth-child(3), #u_0_b > div:nth-child(1) > div:nth-child(4), #u_0_m {
    display: none;
}

				/* Logo */

._2md {
    margin-left: 100px;
    filter: invert(100%);
    opacity: 0.6;
}

				/* Search */

._585- {
    background-color: var(--bg3) !important;
    max-width: 400px;
    margin: 0 auto;
    text-align: center;
    border: none;
    height: 25px;
    top: 9px;
    padding-left: 10px;
}

._585-, ._585- ._4w98 {
    border-radius: 20px;
}

._4kny ._585- {
    margin-left: 160px;
}

._585- ._4w98._4w97, ._585- ._4w98._4w97:hover, ._585- ._4w98 {
    background-color: var(--bg3) !important;
}
  
			/* left sidebar */

._1k0o {
    background: var(--bg2);
    height: 1000px;
    position: fixed;
    left: 0;
    top: -15px;
    padding: 20px;
    border-right: 15px solid var(--bg3);
    box-shadow: 2px 5px 8px rgba(0,0,0,0.1);
/*     z-index: 1; */
}

#universalNav::after {
    content: ">";
    font-size: 20px;
    color: #aaa;
    text-align: right;
    position: fixed;
    margin-left: 155px;
/*     margin-top: -50px; */
/*     z-index: 9999999; */
}

			/* content */

		/* Making A Post Section */

._m_1 ._2aha, ._5qtp {
    font-size: 0px;
}

._m_1 ._2aha:hover, ._5qtp:hover {
    font-size: 13px;
}

._m_1 {
    width: 38px;
}

._2yq ._4-u2 {
    border-radius: 25px;
    box-shadow: 3px 6px 8px rgba(0,0,0,0.1);
    background: var(--bg2);
    border: none;
    padding: 5px;
    margin-bottom: 20px;
}

._2yq._2ltu ._4-u2:before, ._2yq ._4-u2::before {
    border: none;
}

html ._5vb_._5vb_.hasLeftCol div#contentArea, #stream_pagelet {
    margin: 0 auto;
    margin-left: 30px;
 	background: transparent;
    width: 550px;
/*     z-index: -1; */
}

._3ubp, ._sg1, ._4jy2, ._2-sm._xah, ._4jy2:hover, ._2-sm._xah:hover {
    background: var(--bg2);
}

._3ubp {
    border-radius: 10px;
}

._2-sm._xah {
    color: var(--text1);
}

		/* posts */

._4_dr._20h5, ._16ve, ._3ubp, ._4jy2, ._2-sm._xah, ._517h, ._59pe:focus, ._59pe:hover {
    border: none;
}

._ikh ._4bl7, #js_486 > span._3-8_._-xe._3-8_ > i, #js_4as > a > span._3-8_._-xe._3-8_ > i, #u_0_11 > div > button > i {
    display: none;
}

.fbStreamPrivacy .lock {
    opacity: 0;
}
.fbStreamPrivacy .lock:hover {
    opacity: 1;
}

		/* chat nub */

._4mq3 .fbNubButton {
 	background: var(--accent); 
    border-radius: 100px;
    width: 90px;
    border: none;
    padding: 10px;
    margin-top: -60px;
}

.fbNubFlyoutTitlebar, ._4mq3 .fbNubFlyoutTitlebar {
 	background: var(--accent);   
}

._4mq3 .fbNubButton .label .count, #fbDockChatBuddylistNub > a > span.label {
    display: none;
}


		/* notification jewel */


._330i._1z4y.hasNew a.jewelButton ._2n_9, ._330i._1z4y a.jewelButton:active ._2n_9, ._330i._1z4y.openToggler a.jewelButton ._2n_9 {
    filter:invert(100%);
}

._4962 .jewelCount{
    filter:grayscale(100%);
}

			/* remove right sidebar */

._5rzs._5v6d, ._5r-_.homeWiderContent div#rightCol ._5v6d div.home_right_column {
	display: none;
}

		/* clean up left sidebar */

#createNav, #appsNav {
    display: none;
}
}
