@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("guerretribale.fr") {

    
    a{color:#6D473D;}
    a:hover{color:#cc5555;}
    .forum:hover.selected a,.modemenu td.selected:hover a{color:#602B11;text-decoration:underline;}
   .forum{border:none;}
   .forum:hover{border:none;background:#6D473D}
    .forum:hover a{color:#F1EBDD;}
    .btn,.btn-default,.modemenu td,.modemenu td a,span.forum,.forum a{transition:all 0.3s ease;}
    .modemenu td:hover{background:#6D473D;}
    .modemenu td:hover a{color:#F4E4BC}
    .btn,.btn-default{
        background:#7D5730;
        border-radius:0;
        border:none;
        padding:1em;
    }
    .btn:hover,.btn-default:hover{
        background:#A57F57;
    }
    #show_outgoing_units .vis tr:hover td{background:#fff}
    #menu_row > .menu-item,.top_bar,#menu_row > td.menu-item > a,.top_bar .bg_left,.top_bar .bg_right{background:#75452D;transition:all 0.3s ease;}
    #menu_row > td.menu-item > a{background:#603C2A;height:35px;left:0px;margin-left:-2px;border-left:1px solid #000}
    #topContainer{top:-2px;}
    
    
    #menu_row > .menu-item:hover,#menu_row > td.menu-item:hover > a{background:#4C2E1F;}

    
    
    .menu_column tr .menu-column-item a,.menu_column tr .menu-column-item,.menu_column tr:first-child .menu-column-item a,.menu_column tr:first-child .menu-column-item, .menu_column tr:first-child .menu-column-item a,#topdisplay .menu_column .menu-column-item a,#topdisplay .menu_column tr:first-child .menu-column-item a{background:#603C2A;}
    
        .menu_column tr .menu-column-item a:hover,.menu_column tr .menu-column-item:hover,.menu_column tr:first-child .menu-column-item a:hover,.menu_column tr:first-child .menu-column-item:hover,#topdisplay .menu_column .menu-column-item a:hover,#topdisplay .menu_column tr:first-child .menu-column-item a:hover{background:#4C2E1F;}
    
#topdisplay .menu_column .menu-column-item a, .menu_column .menu-column-item a {left:-22px;}    
    
    .menu_column tr .bottom {display:none;}
    
   
