css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://vesnamihajlovicblog.wordpress.com/"),
               url-prefix("https://vesnamihajlovicblog.wordpress.com/")

{
#access { background: rgba(0, 0, 0, 0.7); }


#page { background: rgba(0, 0, 0, 0.7) url('http://live-for-life.ru/wp-content/uploads/2013/10/BestHDWallpapersPack966_7.jpg') repeat fixed center !important; }

#content { background: #C9C9C9
 }


h1,  h4, h5, h6, dt, #content th {
color: #000000;
}

.entry-content, .h4, .entry-title, .entry-title a {
color: #000000; }
  
  
a, a:visited {
color: #FFF;
}
    
h2, .comment-author, .comment-content {
    
color: #C0C0C0;
}

h3 {
color: #000000;
}

 
#recent-posts-4 { font-weight:bold !important;
    font-size: 12px !important;
    text-shadow: 1px 1px 5px rgba(0,0,0,1) !important;}

#nav-below, #comments, #nav-above {
border-radius: 10px;
border: 1px solid rgba(0,0,0,1) !important;
box-shadow: 0 0 1em 1px #000000 !important;
  background: #2F2F2F
}

#content {
border-radius: 10px;
border: 1px solid rgba(250,250,250,1) !important;
box-shadow: 0 0 1em 3px #A5B5B0 !important;
}


#custom-header-image, #access, #search-3, #text-2,  #recent-posts-4, #blog_subscription-2 { 

box-shadow: 0 0 1em 3px #000 !important;
}

.widget {
background: rgba(0, 0, 0, 0.7);
  }
}
