css:
html, #main {background-color: #111 !important;}
  
p, h1, h2, h3, h5, td, h4, label, legend {
   color: #7c7b7b !important; 
  }

.pull-left>a {color: #383636 !important;}

.media-body .media-heading a{color: #adabab !important;}
.media-body .media-heading a:hover{background-color: transparent !important;}
    
.link-user {color: silver !important;}
.link-user:hover {background-color: transparent !important; color: white !important;}

.user-moderator {color: #c16501 !important;}
.user-moderator:hover {color: #e27602 !important;}

.user-admin{color: #00910e !important;}
.user-admin:hover{color: #01b710 !important;}

.user-developer{ color: #bf0101 !important;}
.user-developer:hover{ color: #f20000 !important;}

.page-header{color: #605e5e !important;}

.icon-user-online{color: green !important;}
  
.panel-body{color: #9b9999 !important;}

table tr{color: #848484 !important;}

.badge{color: black !important;}
  
.btn-default:hover, .btn-subscription.subscribed:hover, .btn-favourite:hover, .btn-collect:hover {
	background-color: #111 !important;
	border-color: #222 !important;
	color: white !important;
}
.btn-group.open .dropdown-toggle {
	background-color: #111 !important;
	border-color: #222 !important;
}
/*btn-group{
background-color: #111 !important;}*/
.btn-default, .btn-subscription.subscribed, .btn-fav, .btn-collect, .btn--purity, .wallpaperSearch .btn--tagInfo{background-color:initial;}  
.dropdown-menu>li>a {
	color: grey !important;
}
  
.dropdown-menu {
	background-color: #111 !important;
	border-color: #222 !important;
}

.btn-default, .btn-subscription.subscribed, .btn-favourite, .btn-collect {
	color: grey !important;
	background-color: #111 !important;
	border-color: #222 !important;
}

.panel-default {
	border-color: #222 !important;
}
.masthead__search-input.panel{background-color: #111 !important;}

tabbrowser tabpanels { background: none !important; }

.panel-body {
	/*background: #181818 !important;*/
	line-height: 1.5 !important;
	font-weight: 500 !important;
	-webkit-font-smoothing: antialiased !important;
	border-color: #222 !important;
}
.panel{background-color:initial !important;}
ul.collectionList>li .media-body{background-color: #111 !important;}
.wallpaperSearch{background: #181818 !important;}

.panel-default>.panel-heading {
	color: grey;
	background-color: #111;
	border-color: #222;
}

.panel-info>.panel-heading {
	color: #31708f;
	background-color: #111;
	border-color: #222;
}

.panel-info {border-color: #222;}
.btn-link {color: grey !important;}


.navbar-inverse .navbar-nav>li>a {color: grey !important;}

.navbar-inverse {
	background: rgba(0, 0, 0, 0.9) !important;
	-webkit-font-smoothing: antialiased !important;
	text-align: center !important;
}

.form-control, .tt-hint, .typeahead, .tt-query {
	color: night;
	background-color: #111 !important;
	border-color: #222 !important;
}

a {color: #e2e0e0 !important;}

legend {border-bottom: 1px solid #222 !important;}

.list-group-item{
	background-color: #111 !important;
	border-color: #222 !important;
}
  
.list-group>a {color: grey !important;}
    
.table {border-color: #222 !important;}
    
.table>thead>tr>th, .table>thead>tr>td, .table>tbody>tr>th, .table>tbody>tr>td, .table>tfoot>tr>th, .table>tfoot>tr>td {
border-color: #222 !important;      
}

a.purity-sfw, a.purity-sfw:hover, a.purity-sketchy, a.purity-sketchy:hover, a.purity-nsfw, a.purity-nsfw:hover {
color: #999 !important;
}
      
.table-hover>tbody>tr:hover>td, .table-hover>tbody>tr:hover>th {
background-color: #222 !important;
}

.list-collection>li {
border-top: 1px solid #222 !important;
border-bottom: 1px solid #222 !important;
background-color: #111 !important;
}
  
.page-header {border-bottom: 1px solid #222 !important;}
    
hr {border-color: #222 !important;}
    
.breadcrumb {background-color: #111 !important;}
      
.navbar-default {
	/*background-color: #111 !important;*/
	border-color: #222 !important;
}
.container-fluid{background-color: #111 !important;}

    
.navbar-default .navbar-nav>.active>a, .navbar-default .navbar-nav>.active>a:hover, .navbar-default .navbar-nav>.active>a:focus {
	color: grey !important;
	background-color: #222 !important;
}

.btn-primary, .btn-subscription{
	background-color: #111 !important;
	border-color: #222 !important;
}
  
.well {
	background-color: #111 !important;
	border: 1px solid #222 !important;
}

.alert-warning {
	background-color: #111 !important;
	border-color: #222 !important;
	color: grey !important;
}
.nav-pills>li.active>a, .nav-pills>li.active>a:hover, .nav-pills>li.active>a:focus {
background-color: #222 !important;
}

.nav>li>a:hover, .nav>li>a:focus, li>a:hover, li>a:focus {
background-color: #222 !important;
}

.dropdown-menu .divider {background-color: #222 !important;}
        
.modal-content {
	background-color: #111 !important;
	border: 1px solid #222 !important;
}
          
.modal-header {border-bottom: 1px solid #222 !important;}
          
.modal-footer {border-top: 1px solid #222 !important;}
              
.pagination>li>a, .pagination>li>span {
	color: grey !important;
	background-color: #111 !important;
	border: 1px solid #222 !important;
}

.pagination>.disabled>span, .pagination>.disabled>span:hover, .pagination>.disabled>span:focus, .pagination>.disabled>a, .pagination>.disabled>a:hover, .pagination>.disabled>a:focus {
	background-color: #222 !important;
	border-color: #333 !important;
}

.pagination>li>a:hover, .pagination>li>span:hover {
	color: grey !important;
	background-color: #222 !important;
	border: 1px solid #333 !important;
}
    
.wallpaper-stage {background-color: #111 !important;}

.md-editor>.md-header, .md-editor .md-footer {
background: #111 !important;
}
  
.md-editor {border: 1px solid #222 !important;}
    
.md-editor>.md-preview {
	background: #111 !important;
	border-top: 1px dashed #222 !important;
	border-bottom: 1px dashed #222 !important;
}
.table>thead>tr>td.danger, .table>thead>tr>th.danger, .table>thead>tr.danger>td, .table>thead>tr.danger>th, .table>tbody>tr>td.danger, .table>tbody>tr>th.danger, .table>tbody>tr.danger>td, .table>tbody>tr.purity-nsfw>td, .table>tbody>tr.danger>th, .table>tbody>tr.purity-nsfw>th, .table>tfoot>tr>td.danger, .table>tfoot>tr>th.danger, .table>tfoot>tr.danger>td, .table>tfoot>tr.danger>th {
background-color: #4f0000 !important;
}
.table>thead>tr>td.warning, .table>thead>tr>th.warning, .table>thead>tr.warning>td, .table>thead>tr.warning>th, .table>tbody>tr>td.warning, .table>tbody>tr>th.warning, .table>tbody>tr.warning>td, .table>tbody>tr.purity-sketchy>td, .table>tbody>tr.warning>th, .table>tbody>tr.purity-sketchy>th, .table>tfoot>tr>td.warning, .table>tfoot>tr>th.warning, .table>tfoot>tr.warning>td, .table>tfoot>tr.warning>th {
background-color: #5f602a !important;
}
.panel panel-danger{background-color: #4f0000 !important;}

.btn-nsfw.btn-active {background-color: #4D0000 !important;}

.btn-warning, .btn-sketchy.btn-active {background-color: #5f602a !important;}

.btn-success, .btn-sfw.btn-active {background-color: #326832 !important;}

.btn-danger {background-color: #4D0000 !important; border-color: #d43f3a !important;}
.btn-danger:hover {background-color: #4D0000 !important; border-color: #d43f3a !important;}

.panel_contents a{color: black !important;}

.panel_contents a:hover{background-color: transparent !important;}

tbody a{color: black !important;}

.breadcrumb{background-color: transparent !important;}

a.table_tools_button,.table_tools .dropdown_menu_button{color: black !important;}

thead a{color: #5e6469 !important;}

.dropdown_menu_list li a{color: black !important;}
.title_bar a {color: black !important;}

.pagination a {color: black !important;}

.text-right a {color: grey !important;}

.list-group-item.active, .list-group-item.active:hover, .list-group-item.active:focus {
	background-color: #666 !important;
	color: white !important;
}
.btn-favourite.favourited {
	color: white !important;
	background-color: #333 !important;
  opacity: 0.6 !important;
}
.close {color: white !important;}

span.purity-sfw{color: #c9c9c9 !important;}

span.purity-nsfw br{color: #666 !important;}
span.purity-sfw{color: green !important;}
.purity-sfw{color: green !important;}

.purity-sketchy{color: #EC971F !important;}

.purity-nsfw{color: #d9534f !important;}

span.tt-dropdown-menu {background-color: #222 !important;}

.tt-suggestion:hover{background-color: #333 !important;}

.btn-info {
	color: silver !important;
	background-color: #222 !important;
	border-color: #333 !important;}

.btn-info:hover {background-color: #333 !important;}
.pull-left.btn:hover{background-color: #444 !important;}
.list-group a:hover{ background-color: #222 !important; }
body.homepage.index .navbar{padding-bottom:0px;}
.navbar-default{background-color:none !important;}
