css:
@-moz-document domain("trello.com") {
body[style*='background-color: rgb(0, 121, 191)'] { background-color: #3488bc !important; }
body[style*='background-color: rgb(0, 121, 191)'] #header { background: rgba(53, 153, 219, 1) !important; }
body[style*='background-color: rgb(0, 121, 191)'] .board-header { background: rgba(53, 153, 219, 1) !important; }

body, html, input[type="text"], select, textarea { font-size: 13px; }

.board-header { margin-bottom: 10px; }

.sidebar-section-header .icon-sm { height: auto; margin-right: 7px; }
.compact-board-tile-link-thumbnail { width: 15px; }
.compact-board-tile-link-text { padding-left: 5px; }
.compact-board-tile-link-text.has-sub-name { padding-top: 1px; }

.list-wrapper { width: 235px; }
.list-header { padding-top: 6px; }
.list-header-name { font-size: 13px; }
.list-header-num-cards { font-size: 11px; font-weight: 600; line-height: 12px; display: block; }
.list-card.is-covered .list-card-cover { max-height: 200px; }
.list-card-labels {
  padding-bottom: 2px !important;
  margin: -6px 0 4px 0 !important;
}
.list-card-labels .card-label {
  height: 10px !important;
  line-height: 10px !important;
  padding: 2px 3px !important;
  width: auto !important;
  min-width: 20px !important;
  font-weight: 600 !important;
  font-size: 9px !important;
  text-transform: lowercase !important;
  margin: 0 1px 0 0 !important;
  border-top-left-radius: 0 !important;
  border-top-right-radius: 0 !important;
}

.badges { padding-top: 0px; padding-bottom: 0; }
.badge { margin-right: 0px; padding-right: 2px; }
.badge-text { font-size: 9px; font-weight: 600; }
.badge.is-complete { margin-left: 2px; margin-right: 2px; }
.badge.is-complete,
.badge.is-due-soon,
.badge.is-due-now,
.badge.is-due-past { text-shadow: 0 0 2px rgba(0, 0, 0, .6); }

.list-card-title,
textarea.list-card-composer-textarea { font-size: 12px; line-height: 15px; }

.list-card-members { margin-top: -1px; padding-right: 2px; }
.list-card-members .member,
.list-card-members .member img { width: 20px; height: 20px; }

.window { min-width: 1050px; padding: 10px; }
.window-main-col { width: 856px; }
.card-detail-data { margin-top: 10px; padding-top: 15px; border-top: 1px dotted #ccc; }

.checklist-new-item-text { overflow: hidden !important; }

pre { font-size: 11px; line-height: 15px; }
code { font-size: 12px; line-height: 15px; padding: 3px 2px 1px 2px; margin: 0; }

#boards-drawer,
.pop-over-content { zoom: 90%; }

.card-label { text-shadow: 0 0 5px rgba(0, 0, 0, .6); }
.js-card-desc { margin: 10px 0; }
.card-detail-item-header-edit { float: right; }

[title="This card has a description."] { display: none; }

.list-wrapper.mod-add { width: auto; }
}
