css:
@-moz-document domain("treesmovienight.com") {

  .video-and-chat>.video {
    width: 82% !important;
  }

  .video-and-chat>.chat {
    width: 18% !important;
  }

  .video-and-chat>.chat .content .tab-content.messages .messages-wrapper .messages .message.flair-bot.flair-bot,.video-and-chat>.chat .content .tab-content.messages .messages-wrapper .messages .message.is-activity.flair-bot {
    overflow-x: hidden;
    /*background: url('http://i.imgur.com/YTqb3zo.png');*/
    background: url('https://s3-us-west-1.amazonaws.com/gabbz/greenwp.png');
  }

  .video-and-chat>.chat .content .tab-content.messages .messages-wrapper {
    overflow-x: hidden;
    background-color: #1f1f1f;
  }

  .chat li {
    background-color: #1f1f1f;
    color: #cccccc;
  }

  .chat li.is-activity, .video-and-chat>.chat .content .tab-content.messages .messages-wrapper .messages .message.is-myself, .video-and-chat>.chat .content .tab-content.messages .messages-wrapper .messages .message.is-activity {
    background-color: #1d1d1d;
    color: #aaaaaa;
  }

  .chat la.is-important {
    background-color: #007238;
  }

  .video-and-chat>.chat .content .tab-content.messages .form {
    background-color: #1f1f1f !important;
    color: #cccccc !important;
    border-color: #000 !important;
  }

  .video-and-chat>.chat .content .tab-content.messages .messages-wrapper .messages .message.is-spoiler {
    border: 1px dashed !important;
    border-color: #720000 !important;
    background-color: #0b0b0b;
    color: #FFF !important;
  }

  .video-and-chat>.chat .content .tab-content.messages .messages-wrapper .messages .message.is-spoiler:hover {
    background-color: #0b0b0b;
    color: #FFF;
  }
  .video-and-chat>.chat .content .tab-content.messages .messages-wrapper .messages .message:hover {
    background-color: #727272 !important;
    color: #000; 
  }

  .video-and-chat>.chat .content .tab-content.messages .messages-wrapper .messages .message.is-mentioned {
   background-color: #720000; 
  }

  .video-and-chat>.chat .content .tab-content.messages .messages-wrapper .messages .message.is-whisper {
    background-color: #c07c00;
  }

  .video-and-chat>.chat .content .tab-content.messages .messages-wrapper .messages .message.is-whisper:hover {
    background-color: #727272;
    color: #1d1d1d;
  }

  .video-and-chat>.chat .content .darkpurp{
    color:#5d3872 !important
  }
}
